UPDATE `trans_words` SET `content_loc6`='Not bad at all, but you know, I think I\'d get a bath in as soon as possible, if I was you.$B$BNow, about your sea monster...$B$BHelp yourself to a brew or two in the meantime.' WHERE `word_id` = 154046; -- locales_quest - offerrewardtext : entry=11209
UPDATE `trans_words` SET `content_loc6`='Not bad at all. Perhaps there is still hope for the old ways.' WHERE `word_id` = 173816; -- locales_quest - offerrewardtext : entry=14283
UPDATE `trans_words` SET `content_loc6`='Not bad for a city-dwellin\' $R. Good show, aye!' WHERE `word_id` = 181177; -- locales_quest - offerrewardtext : entry=26462
UPDATE `trans_words` SET `content_loc6`='Not bad for a new overseer. I\'ve another task for you if you\'re feeling brave today.' WHERE `word_id` = 161747; -- locales_quest - offerrewardtext : entry=12906
UPDATE `trans_words` SET `content_loc6`='Not bad! Let\'s hope they don\'t ask for lava or something next, right?' WHERE `word_id` = 188860; -- locales_quest - offerrewardtext : entry=28632
UPDATE `trans_words` SET `content_loc6`='Not bad, $C. Now that the priest\'s image is trapped within the mirror, I have something else that needs doing.' WHERE `word_id` = 159237; -- locales_quest - offerrewardtext : entry=12260
UPDATE `trans_words` SET `content_loc6`='Not bad, $N! Did you have a hard time getting past their guard?$B$BNo matter - many thanks, and if you\'re ever in need of stout with a kick, don\'t forget the Barleybrews!' WHERE `word_id` = 131459; -- locales_quest - offerrewardtext : entry=311
UPDATE `trans_words` SET `content_loc6`='Not bad, $N. It\'s a good thing you\'re on our side.' WHERE `word_id` = 174121; -- locales_quest - offerrewardtext : entry=14369
UPDATE `trans_words` SET `content_loc6`='Not bad, $N. Keep it up, we could use a few more people like you around here.' WHERE `word_id` = 146342; -- locales_quest - offerrewardtext : entry=9155
UPDATE `trans_words` SET `content_loc6`='Not bad, $N. Those zhevra hold a lot of strength in their legs. A sharp kick from them can knock even a tauren on its haunches!$B$BIt looks like you have some strength in you too. Are you ready for a real challenge?' WHERE `word_id` = 133705; -- locales_quest - offerrewardtext : entry=845
UPDATE `trans_words` SET `content_loc6`='Not bad, $N. You might be slightly more useful than I initially thought.' WHERE `word_id` = 159583; -- locales_quest - offerrewardtext : entry=12413
UPDATE `trans_words` SET `content_loc6`='Not bad, friend. You might make some gold out here - if you manage to stay alive.' WHERE `word_id` = 161862; -- locales_quest - offerrewardtext : entry=12932
UPDATE `trans_words` SET `content_loc6`='Not bad, huh?  Maybe you can put a word in with the high commander. Maybe he\'ll put us in charge of our own squad!' WHERE `word_id` = 187627; -- locales_quest - offerrewardtext : entry=28277
UPDATE `trans_words` SET `content_loc6`='Not bad, mon. Ya have a deep power in ya spells - you may have a talent for dis. Some rough edges ta be sure, but we\'ll get dose ironed out here and I\'ll teach ya a few new tings as we go.' WHERE `word_id` = 180504; -- locales_quest - offerrewardtext : entry=26273
UPDATE `trans_words` SET `content_loc6`='Not bad, mon. Ya have a natural flow to ya attacks - you may have a talent for dis. Some rough edges ta be sure, but we\'ll get dose ironed out here and I\'ll teach ya a few new tings as we go.' WHERE `word_id` = 175460; -- locales_quest - offerrewardtext : entry=24777
