UPDATE `trans_words` SET `content_loc6`='Quite a day this is, $C. The blood of Trollbane will no longer haunt my people, and the sigils needed to take Trol\'kalar are in our possession. Soon, very soon, we will strike against the hated trolls of Zul\'Gurub.' WHERE `word_id` = 132896; -- locales_quest - offerrewardtext : entry=644
UPDATE `trans_words` SET `content_loc6`='Quite an accomplishment, $N. I\'ll be certain that this gets to Harrison when his expedition here is concluded.' WHERE `word_id` = 188027; -- locales_quest - offerrewardtext : entry=28376
