UPDATE `trans_words` SET `content_loc6`='Temple of Winter Investigated' WHERE `word_id` = 162101; -- locales_quest - ObjectiveText2 : entry=12986
UPDATE `trans_words` SET `content_loc6`='Ten thousand souls cry out in unison, $N! You have struck a mortal blow to the Scourge and their masters.' WHERE `word_id` = 140364; -- locales_quest - offerrewardtext : entry=27142
UPDATE `trans_words` SET `content_loc6`='Teracula was an even mightier adversary than Zephyrus.$B$B<You open the guide that Jekyll gave to you.>' WHERE `word_id` = 188905; -- locales_quest - offerrewardtext : entry=28644
UPDATE `trans_words` SET `content_loc6`='Teronis is dead? We must see to it that his sacrifice was not in vain. Are you willing to see this through, $C? Very well, allow me some time to think about how we should proceed. I will know shortly.' WHERE `word_id` = 172428; -- locales_quest - offerrewardtext : entry=13645
UPDATE `trans_words` SET `content_loc6`='Teronis\' body lies broken atop the island. For some unknown reason, the murlocs have left it alone.$B$BThe deep slashes across his corpse obviously came from the weapons and claws of the murlocs.' WHERE `word_id` = 134310; -- locales_quest - offerrewardtext : entry=991
UPDATE `trans_words` SET `content_loc6`='Teronis\' body lies broken atop the small rise next to the Keeper. Deep slashes crisscross his corpse.' WHERE `word_id` = 181180; -- locales_quest - offerrewardtext : entry=26463
UPDATE `trans_words` SET `content_loc6`='Terrific! These coils will decompress with the force of a thousand suns! That\'s just a rough approximation of course, don\'t hold me accountable for those numbers.' WHERE `word_id` = 187370; -- locales_quest - offerrewardtext : entry=28215
UPDATE `trans_words` SET `content_loc6`='Terrific! Twilight\'s Hammer will no doubt try to re-take the mountain, but they\'ll have to do so without the leadership of those fat-for-brains thugs.' WHERE `word_id` = 179117; -- locales_quest - offerrewardtext : entry=25886
UPDATE `trans_words` SET `content_loc6`='Territorial Fetish placed' WHERE `word_id` = 175486; -- locales_quest - ObjectiveText1 : entry=24813
