UPDATE `trans_words` SET `content_loc6`='Test Rune of Command' WHERE `word_id` = 156420; -- locales_quest - ObjectiveText1 : entry=11348
UPDATE `trans_words` SET `content_loc6`='Test the Dried Seeds' WHERE `word_id` = 133840; -- locales_quest - ObjectiveText1 : entry=877
