UPDATE `trans_words` SET `content_loc6`='Use the Highborne Prison to bind 5 Highborne Spirits.' WHERE `word_id` = 275332; -- locales_quest - objectives : entry=25766
UPDATE `trans_words` SET `content_loc6`='Use the item well, $N. Let them feel the pain that they so dearly wish to inflict upon others.' WHERE `word_id` = 144348; -- locales_quest - offerrewardtext : entry=8790
UPDATE `trans_words` SET `content_loc6`='Use the Kodohide Drum at the Marmot Den. While controlling the Marmot, poison the three kegs in the Bloodmaul Camp. When that is completed, return to Rexxar at Thunderlord Stronghold in the Blade\'s Edge Mountains.' WHERE `word_id` = 152109; -- locales_quest - objectives : entry=10720
UPDATE `trans_words` SET `content_loc6`='Use the Mechashark X-Steam Controller' WHERE `word_id` = 175501; -- locales_quest - ObjectiveText1 : entry=24817
UPDATE `trans_words` SET `content_loc6`='Use the Mistmantle Family Ring at Manor Mistmantle, east of town.' WHERE `word_id` = 181944; -- locales_quest - objectives : entry=26674
UPDATE `trans_words` SET `content_loc6`='Use the Naturalized Ammunition to take control of the Death\'s Door Fel Cannons. Use them to destroy both the South Warp-Gate and the North Warp-Gate. Then, use the Druid Signal to call out to an Evergrove Druid to report your success.' WHERE `word_id` = 152877; -- locales_quest - objectives : entry=10911
UPDATE `trans_words` SET `content_loc6`='Use the portal in Dragonmaw Port to go to Orgrimmar and report to Garrosh Hellscream.' WHERE `word_id` = 182360; -- locales_quest - objectives : entry=26798
UPDATE `trans_words` SET `content_loc6`='Use the Purified Moon Well Water to douse the four Braziers of Protection within Shadow Hold.' WHERE `word_id` = 186981; -- locales_quest - objectives : entry=28126
UPDATE `trans_words` SET `content_loc6`='Use the Rod of Purification on the Darkstone of Terokk in Veil Skith and return to Kirrik at the Refugee Caravan in Bone Wastes.' WHERE `word_id` = 152555; -- locales_quest - objectives : entry=10839
UPDATE `trans_words` SET `content_loc6`='Use the shredder to destroy the Talrendis Ancient. Emergency Backup Shredders are available at the Orgrimmar Rear Gate if you lose yours.' WHERE `word_id` = 173417; -- locales_quest - objectives : entry=14155
UPDATE `trans_words` SET `content_loc6`='Use the Spiked Worg Collars near the cages behind Huntsman Russell. Follow the Gilnean Bloodhound to find and kill the leader of the Orc Scouts.' WHERE `word_id` = 173069; -- locales_quest - objectives : entry=14002
UPDATE `trans_words` SET `content_loc6`='Use the Sunfury Disguise, go into Manaforge Coruu and listen to the conversation between Commander Dawnforge and Arcanist Ardonis. Report back to Caledis Brightdawn after completing this task.$B$BCompleting tasks for the Scryers will cause your Aldor reputation level to decrease.' WHERE `word_id` = 150041; -- locales_quest - objectives : entry=10198
UPDATE `trans_words` SET `content_loc6`='Use the Teleporter in the back of Fuselight-by-the-Sea to port up the mountain, then report to Dolph Blasto at Fuselight in the Badlands.' WHERE `word_id` = 185675; -- locales_quest - objectives : entry=27763
UPDATE `trans_words` SET `content_loc6`='Use the Wicked Strong Fetish to curse 5 buildings or towers in Bladespire Hold and 2 in Bloodmaul Outpost. Return to T\'chali the Witch Doctor at Bloodmaul Ravine in the Blade\'s Edge Mountains after you have done so.' WHERE `word_id` = 151409; -- locales_quest - objectives : entry=10544
UPDATE `trans_words` SET `content_loc6`='Use the Wickerman Ashes' WHERE `word_id` = 190786; -- locales_quest - ObjectiveText2 : entry=29376
