UPDATE `trans_words` SET `content_loc6`='Use Ultrasafe Personnel Launcher to deploy to Frostmane Retreat' WHERE `word_id` = 178906; -- locales_quest - EndText : entry=25839
UPDATE `trans_words` SET `content_loc6`='Use your Mana Tap racial ability on creatures with mana six times, then return to Arcanist Helion.' WHERE `word_id` = 145966; -- locales_quest - objectives : entry=8346
UPDATE `trans_words` SET `content_loc6`='Use your newfound strength to destroy the Gurubashi!' WHERE `word_id` = 142736; -- locales_quest - offerrewardtext : entry=8043
UPDATE `trans_words` SET `content_loc6`='Using the key found on the skeleton of the former Mrs. Dalson, you unlock the door to the outhouse. A bloodthirsty ghoul leaps from the outhouse, having been trapped inside for who knows how long.$B$BIt is clear by the bloodcurdling scream of the ghoul at your presence that the former Farmer Dalson is now beyond saving.' WHERE `word_id` = 139751; -- locales_quest - offerrewardtext : entry=5059
UPDATE `trans_words` SET `content_loc6`='Using the keystone, you confirm the grave is from one of the original builders, and you learn important details of the mummification process.' WHERE `word_id` = 189278; -- locales_quest - offerrewardtext : entry=28801
UPDATE `trans_words` SET `content_loc6`='Using these elements, I can fuse the fragments together... to hopefully what they once were.' WHERE `word_id` = 140180; -- locales_quest - offerrewardtext : entry=5247
UPDATE `trans_words` SET `content_loc6`='Uther\'s Tomb Defiled' WHERE `word_id` = 147033; -- locales_quest - ObjectiveText1 : entry=9444
UPDATE `trans_words` SET `content_loc6`='V? The only V I know is VanCleef, but he died many years ago.' WHERE `word_id` = 180581; -- locales_quest - offerrewardtext : entry=26295
UPDATE `trans_words` SET `content_loc6`='Vaelastrasz\'s soul is at ease, champion. The Aspects all watch your progress with great interest. Know that you have powerful allies.$B$BI was commanded to grant you something to aid you in this struggle. Use it well...' WHERE `word_id` = 144187; -- locales_quest - offerrewardtext : entry=8730
UPDATE `trans_words` SET `content_loc6`='Val\'kyr?  Koltira brought val\'kyr to this fight?  Has he gone mad? No, this is not Koltira\'s work. This was Sylvanas.' WHERE `word_id` = 183831; -- locales_quest - offerrewardtext : entry=27205
UPDATE `trans_words` SET `content_loc6`='Valgarde Scout Rescued' WHERE `word_id` = 155286; -- locales_quest - ObjectiveText1 : entry=11244
UPDATE `trans_words` SET `content_loc6`='Valis Windchaser\'s advice' WHERE `word_id` = 164380; -- locales_quest - ObjectiveText1 : entry=13835
UPDATE `trans_words` SET `content_loc6`='Valishj Slain' WHERE `word_id` = 173823; -- locales_quest - ObjectiveText1 : entry=14284
UPDATE `trans_words` SET `content_loc6`='Valorcall was loyal to me in life. His death will not be something I bear lightly. But bear it I must. I am Forsaken. My old existence means nothing. The sigils are ours; Trol\'kalar awaits.' WHERE `word_id` = 179663; -- locales_quest - offerrewardtext : entry=26032
UPDATE `trans_words` SET `content_loc6`='Valve #1 released' WHERE `word_id` = 176725; -- locales_quest - ObjectiveText1 : entry=25204
