UPDATE `trans_words` SET `content_loc6`='Vindicator Vuuleen has asked you to kill Gorr\'Dim, leader of the Bladespire Hold ogres. Once you have done so, return to her at Bladespire Hold in the Blade\'s Edge Mountains.' WHERE `word_id` = 151328; -- locales_quest - objectives : entry=10517
