UPDATE `trans_words` SET `content_loc6`='Volkhan shall haunt the dreams of my people no more.$B$BContinue your efforts, little $R. In time, I am certain my people will grow to forgive Thorim.' WHERE `word_id` = 162591; -- locales_quest - offerrewardtext : entry=13109
UPDATE `trans_words` SET `content_loc6`='Vorrel led such a troubled existence. And what a good mate he was...$B$BDamn those filthy humans!$B$BVorrel is gone, never to enjoy undeath again. At least I can take solace in the revenge you took in his name. For that I thank you.$B$BNow let me be to mourn....' WHERE `word_id` = 134532; -- locales_quest - offerrewardtext : entry=1051
UPDATE `trans_words` SET `content_loc6`='Vrykul banners burned' WHERE `word_id` = 162485; -- locales_quest - ObjectiveText1 : entry=13084
