UPDATE `trans_words` SET `content_loc6`='Vylestem Vines healed' WHERE `word_id` = 141787; -- locales_quest - ObjectiveText1 : entry=7029
UPDATE `trans_words` SET `content_loc6`='Wahoo!! Did you SEE that?!$B$BWe need to get this into production right away!$B$BHere. Take the rest of the stuff to Jinx. You\'ll find him setting up our new facility just west of here.' WHERE `word_id` = 275166; -- locales_quest - details : entry=13977
UPDATE `trans_words` SET `content_loc6`='Wahoo!$B$BThat\'ll teach \'em!' WHERE `word_id` = 163534; -- locales_quest - offerrewardtext : entry=13321
UPDATE `trans_words` SET `content_loc6`='Wahooo! I saw them light up all the way from here! I\'ll just chalk that up as another success and begin bottling this stuff up for the invasion - of course it might need a little more testing as we go if you\'re at all interested....' WHERE `word_id` = 163182; -- locales_quest - offerrewardtext : entry=13239
UPDATE `trans_words` SET `content_loc6`='Wait for Rhea to retrieve the egg' WHERE `word_id` = 186038; -- locales_quest - ObjectiveText1 : entry=27858
UPDATE `trans_words` SET `content_loc6`='Wait to be launched from Flintlocke\'s Cannon' WHERE `word_id` = 188728; -- locales_quest - ObjectiveText1 : entry=28598
UPDATE `trans_words` SET `content_loc6`='Wait, you were sent here by THE Shandris Feathermoon? I can barely believe my ears! To know that my cause is supported by her massive army... Yes, yes, you will be perfect. You\'re the key to my future!' WHERE `word_id` = 183039; -- locales_quest - offerrewardtext : entry=26985
UPDATE `trans_words` SET `content_loc6`='Wake up, $N! We don\'t have much time. I rescued your gear for you. At least now you\'ve seen the foolishness of our leader first-hand. His blood is contaminated, his rage is unquenchable -- and he\'ll destroy us all trying to defeat the Horde!' WHERE `word_id` = 181460; -- locales_quest - offerrewardtext : entry=26549
UPDATE `trans_words` SET `content_loc6`='Waltor was wise in sending you to me, $R. The gods smile down upon those that mete justice. You will be he that doles out punishment to the wicked!' WHERE `word_id` = 157280; -- locales_quest - offerrewardtext : entry=11662
UPDATE `trans_words` SET `content_loc6`='WANTED!$B$B    "Cap\'n" J. Garvey of$B     the Maiden\'s Dagger$B$BCharged with piracy on the open seas, kidnapping, extortion, arson, slavery, et al. Garvey recently arrived in these waters leading a crew of Southsea Freebooters and was last seen assaulting Fray Island. Bring proof of death in an identifiable condition!$B$B-- Gazlowe' WHERE `word_id` = 166635; -- locales_quest - details : entry=895
UPDATE `trans_words` SET `content_loc6`='WANTED!$B$BStronglimb Deeproot, ancient of war and chief defender of the Living Grove south of Sylvanaar.$B$BFind him by heading west across the bridge that traverses the Bloodmaul Ravine.$B$BReturn his hewn trunk to Tor\'chunk Twoclaws inside the Thunderlord Stronghold inn.' WHERE `word_id` = 151237; -- locales_quest - details : entry=10489
UPDATE `trans_words` SET `content_loc6`='War comes, $N, and with it comes untold horrors. You have done all that could be asked of you and for that you should be rewarded.$B$BShould this... this prophecy ever come to pass...$B$B<Geologist Larksbane turns pale.>$B$BThe future of us all could be at an end.$B$BThese armaments will prove invaluable to your campaign in this desert.' WHERE `word_id` = 143289; -- locales_quest - offerrewardtext : entry=8316
UPDATE `trans_words` SET `content_loc6`='War, you say. Divinations for war are always difficult. Some wars must never be fought, even if victory was assured. Some wars have to be waged even if defeat is certain.' WHERE `word_id` = 185181; -- locales_quest - offerrewardtext : entry=27630
UPDATE `trans_words` SET `content_loc6`='Warden Stillwater Defeated' WHERE `word_id` = 189184; -- locales_quest - ObjectiveText2 : entry=28748
UPDATE `trans_words` SET `content_loc6`='Warden\'s Game Completed' WHERE `word_id` = 185419; -- locales_quest - ObjectiveText1 : entry=27693
UPDATE `trans_words` SET `content_loc6`='Warlord Jin\'arrak Destroyed' WHERE `word_id` = 158757; -- locales_quest - ObjectiveText1 : entry=12152
UPDATE `trans_words` SET `content_loc6`='Warlord Thresh\'jin\'s Body burned' WHERE `word_id` = 184635; -- locales_quest - ObjectiveText1 : entry=27482
UPDATE `trans_words` SET `content_loc6`='Warlord Zaela sent you? Good. If you Horde are as strong as your Warchief promised, I expect to see some bloodshed on these shores.' WHERE `word_id` = 185004; -- locales_quest - offerrewardtext : entry=27583
UPDATE `trans_words` SET `content_loc6`='Warmaul Ogre Banner Planted' WHERE `word_id` = 148977; -- locales_quest - ObjectiveText1 : entry=9927
UPDATE `trans_words` SET `content_loc6`='Warring Elemental slain' WHERE `word_id` = 176738; -- locales_quest - ObjectiveText1 : entry=25206
UPDATE `trans_words` SET `content_loc6`='Warsong Banner Planted in Magmothregar' WHERE `word_id` = 157295; -- locales_quest - ObjectiveText1 : entry=11670
UPDATE `trans_words` SET `content_loc6`='Warsong enemies destroyed' WHERE `word_id` = 172843; -- locales_quest - ObjectiveText1 : entry=13935
UPDATE `trans_words` SET `content_loc6`='Warsong Peon Freed' WHERE `word_id` = 157032; -- locales_quest - ObjectiveText1 : entry=11611
UPDATE `trans_words` SET `content_loc6`='Warsong Shredder returned' WHERE `word_id` = 172493; -- locales_quest - ObjectiveText2 : entry=13698
UPDATE `trans_words` SET `content_loc6`='Warsong Stockpile Incinerated' WHERE `word_id` = 178071; -- locales_quest - ObjectiveText1 : entry=25622
UPDATE `trans_words` SET `content_loc6`='Was your crossing difficult, $N? You have come a long way to speak with me. But fear not, I know why Gakin has sent you, and he was wise to, for I can help you in your search.' WHERE `word_id` = 136211; -- locales_quest - offerrewardtext : entry=1716
UPDATE `trans_words` SET `content_loc6`='Wash the blood from your garments, $N, and do not mourn what you had to do. Instead, give thanks. You have lessened the threat to our people here in Auberdine, even if Darkshore is still in jeopardy from the effects of fel moss.' WHERE `word_id` = 134296; -- locales_quest - offerrewardtext : entry=985
UPDATE `trans_words` SET `content_loc6`='Watch as foul materials disintegrate. Watch as metal turns to dust.$B$BThe Light cleanses all and only the purest residue remains.' WHERE `word_id` = 150971; -- locales_quest - offerrewardtext : entry=10420
UPDATE `trans_words` SET `content_loc6`='Watch as the plant works its magic.' WHERE `word_id` = 147150; -- locales_quest - offerrewardtext : entry=9473
UPDATE `trans_words` SET `content_loc6`='Watch Witch Doctor Mau\'ari pull the E\'ko from Rimepelt\'s body.' WHERE `word_id` = 188517; -- locales_quest - objectives : entry=28541
UPDATE `trans_words` SET `content_loc6`='Water of Vision consumed' WHERE `word_id` = 174539; -- locales_quest - ObjectiveText1 : entry=20441
UPDATE `trans_words` SET `content_loc6`='Water parts easily, yet put enough of it together and it will create an unstoppable force. It washes away earth and fire, and easily pushes air out of the way.$B$BWe are that unstoppable force, $N, not those that have poisoned Bloodmyst Isle!' WHERE `word_id` = 147252; -- locales_quest - offerrewardtext : entry=9504
UPDATE `trans_words` SET `content_loc6`='Water Ward acquired' WHERE `word_id` = 183261; -- locales_quest - ObjectiveText2 : entry=27042
UPDATE `trans_words` SET `content_loc6`='Watering Hole Investigated' WHERE `word_id` = 172685; -- locales_quest - EndText : entry=13881
UPDATE `trans_words` SET `content_loc6`='Waters... cleansed. Yes. All is well.$B$BYou understand our way, $N.' WHERE `word_id` = 161038; -- locales_quest - offerrewardtext : entry=12735
UPDATE `trans_words` SET `content_loc6`='Watery Soul Captured' WHERE `word_id` = 151195; -- locales_quest - ObjectiveText1 : entry=10480
UPDATE `trans_words` SET `content_loc6`='Waygate destroyed' WHERE `word_id` = 183330; -- locales_quest - ObjectiveText1 : entry=27058
UPDATE `trans_words` SET `content_loc6`='Wayward Plainstrider Returned' WHERE `word_id` = 176689; -- locales_quest - ObjectiveText1 : entry=25194
UPDATE `trans_words` SET `content_loc6`='We all showed up to the party, but the guest of honor stayed at home.' WHERE `word_id` = 156274; -- locales_quest - offerrewardtext : entry=11303
UPDATE `trans_words` SET `content_loc6`='We appreciate your assistance here in this swamp, $N. I would like to inform you that I have received correspondance from Nethergarde Keep.$B$BEnohar Thunderbrew has requested help in investigating the shipwrecks of the Red Reaches, but we have had none willing to make the journey, and I could not leave the Harborage.$B$BNethergarde Keep sits in the Blasted Lands, south of the Swamp of Sorrows. Do not journey too close to Stonard on your way.' WHERE `word_id` = 186225; -- locales_quest - details : entry=27919
UPDATE `trans_words` SET `content_loc6`='We are all in your debt, $N.$B$BIf I am certain of anything, it is that you have not failed us, nor will you in the future!' WHERE `word_id` = 163208; -- locales_quest - offerrewardtext : entry=13244
UPDATE `trans_words` SET `content_loc6`='We are but moments away from the Dragon Soul.' WHERE `word_id` = 191517; -- locales_quest - offerrewardtext : entry=30100
UPDATE `trans_words` SET `content_loc6`='We are close to the truth, $N.' WHERE `word_id` = 153135; -- locales_quest - offerrewardtext : entry=10972
UPDATE `trans_words` SET `content_loc6`='We are fortunate that you have recovered these intact, $N. With them, I can use the Lorehammer to send you back in time, to where the North Wind started to corrupt the true timeline. It\'s the only way we can restore the true history of this place.' WHERE `word_id` = 162341; -- locales_quest - offerrewardtext : entry=13049
UPDATE `trans_words` SET `content_loc6`='We are here to monitor the scars of the land, and watch for further signs of instability. While we haven\'t seen any yet, a cataclysmic aftershock could devastate the Barrens even further.$B$BTo the northeast, near the chasm, earth elementals rampage out of control. To sooth the earth, these need to occasionally be destroyed, and their essence returned in a ritual. If you help us do this, $C, I would be happy to reward you.' WHERE `word_id` = 175503; -- locales_quest - details : entry=24824
UPDATE `trans_words` SET `content_loc6`='We are honored by Flint Oremantle\'s presence. I am given to understand that the Stonefather is in possession of a fragment of the World Pillar. It is imperative that we recover all of the fragments. The instability between worlds is increasing. Soon it may become too great even for mighty Thrall to hold at bay.' WHERE `word_id` = 181046; -- locales_quest - offerrewardtext : entry=26413
UPDATE `trans_words` SET `content_loc6`='We are in your debt, $C. If I know anything about the furbolg, they do not like their noses bloodied. It won\'t be long now before they tuck tail and return to their village.' WHERE `word_id` = 172394; -- locales_quest - offerrewardtext : entry=13632
UPDATE `trans_words` SET `content_loc6`='We are in your debt, $C. Unable to complete their nefarious rituals, the satyr are now fighting a losing battle where they are unable to replenish their numbers.' WHERE `word_id` = 172486; -- locales_quest - offerrewardtext : entry=13683
UPDATE `trans_words` SET `content_loc6`='We are in your debt, $N. Perhaps now, with Malfurion\'s help, we can save the rest of Darkshore.' WHERE `word_id` = 172231; -- locales_quest - offerrewardtext : entry=13580
UPDATE `trans_words` SET `content_loc6`='We are pleased that you have answered our summons, $C.' WHERE `word_id` = 189075; -- locales_quest - offerrewardtext : entry=28716
UPDATE `trans_words` SET `content_loc6`='We are proud that you have agreed to train with us and compete on our behalf.' WHERE `word_id` = 163924; -- locales_quest - offerrewardtext : entry=13593
UPDATE `trans_words` SET `content_loc6`='We are proud that you have decided to join us and I know you will shine on the field.' WHERE `word_id` = 164151; -- locales_quest - offerrewardtext : entry=13703
UPDATE `trans_words` SET `content_loc6`='We are saved for now. Thank you.$B$BUnfortunately due to the nature of their interference, the Infinite\'s will have almost unlimited opportunity to do so again.$B$BWhat I can discern is that in the future, you are at the heart of the struggle to stop them, $N!' WHERE `word_id` = 163203; -- locales_quest - offerrewardtext : entry=13243
UPDATE `trans_words` SET `content_loc6`='We are the Black Dragonflight. We have rained destruction on the lands of Azeroth for thousands of years. The decrepit Cult of the Damned worships death and we shall be glad to oblige.' WHERE `word_id` = 159254; -- locales_quest - offerrewardtext : entry=12263
UPDATE `trans_words` SET `content_loc6`='We are the heirs to a legacy of arcane magic and your path will not be an easy one. Many still hold us responsible for the actions of our forebears, but our talents are needed.$B$BI want to show you a new spell, Arcane Missiles. It allows you to fire volleys of arcane energy, but you won\'t be able to cast it all the time. Casting other spells has a chance to create an opportunity for you to use Arcane Missiles.$B$BI want you try it. Look for a training dummy near the southeastern edge of Aldrassil.' WHERE `word_id` = 182857; -- locales_quest - details : entry=26940
UPDATE `trans_words` SET `content_loc6`='We are turning the tides, $N. We should be able to contain further damage.$B$BMuch work still remains to be done.' WHERE `word_id` = 172264; -- locales_quest - offerrewardtext : entry=13588
UPDATE `trans_words` SET `content_loc6`='We are victorious! The Legion\'s advances have been halted!$B$BI offer you a choice of rewards for your heroic deeds!' WHERE `word_id` = 149297; -- locales_quest - offerrewardtext : entry=10011
UPDATE `trans_words` SET `content_loc6`='We be here... Da hunter, he be in da cave. Da beast... Sometin\' ain\'t right about her...' WHERE `word_id` = 189849; -- locales_quest - offerrewardtext : entry=29116
UPDATE `trans_words` SET `content_loc6`='We can finally rest here now, $N. It appears Darkshore has been saved -- and we have you to thank for it.' WHERE `word_id` = 172744; -- locales_quest - offerrewardtext : entry=13897
UPDATE `trans_words` SET `content_loc6`='We can handle things from here. I thank you for your help, $N. I assume that when we next meet, it will be in battle. Take care of yourself until then. We\'ll need all the help we can get.' WHERE `word_id` = 183729; -- locales_quest - offerrewardtext : entry=27174
UPDATE `trans_words` SET `content_loc6`='We can only hope that the confusion and distrust you have created enables us to keep our enemies divided.' WHERE `word_id` = 188273; -- locales_quest - offerrewardtext : entry=28476
UPDATE `trans_words` SET `content_loc6`='We can only hope that with the quieting of the ghosts of the officers, the spirits of the foot soldiers will follow. Thank you once again for your help, $N.' WHERE `word_id` = 149490; -- locales_quest - offerrewardtext : entry=10057
UPDATE `trans_words` SET `content_loc6`='We can only hope your efforts will keep the two forces apart long enough for us to exploit their weakness.' WHERE `word_id` = 188271; -- locales_quest - offerrewardtext : entry=28475
UPDATE `trans_words` SET `content_loc6`='We can quickly turn this flesh into stew.' WHERE `word_id` = 162507; -- locales_quest - offerrewardtext : entry=13089
UPDATE `trans_words` SET `content_loc6`='We can still use more of the sanguine hibiscus from the shamblers, bog giants and underbats of the Underbog at Coilfang Reservoir. Bring them to me in bunches of five and I will be most grateful.' WHERE `word_id` = 148066; -- locales_quest - offerrewardtext : entry=9714
UPDATE `trans_words` SET `content_loc6`='We can\'t let that book remain in Sunreaver hands. If we hope to identify the sword and use it to our advantage, we have to find a way to capture the book Krasus lent them.' WHERE `word_id` = 164935; -- locales_quest - offerrewardtext : entry=14444
UPDATE `trans_words` SET `content_loc6`='We can\'t thank you enough, $N!$B$BNow with these two flasks, unstable though they may be, we\'ll be able to send one of our boys north to the Skyguard Outpost.$B$BHere, just wait a moment and we\'ll call for one of them.$B$B<Torkus\'s right head shouts out for his boy in a low growl.>' WHERE `word_id` = 153499; -- locales_quest - offerrewardtext : entry=11061
UPDATE `trans_words` SET `content_loc6`='We cannot wait for me to regain my strength.$B$BI must rely on you to make our preparations....' WHERE `word_id` = 157934; -- locales_quest - offerrewardtext : entry=11946
UPDATE `trans_words` SET `content_loc6`='We could easily have held Silverwind Refuge from the Horde had they not taken control of our water elemental defenders on Mystral Lake.$B$BOne of their shaman summoned in Tideress, and old and angry water spirit of the forest who befouled our elementals and turned them against us. With the lake and the Horde pressing in on us, we had no choice but to retreat to the spire here.$B$BGo east. Take the lake back so that we will have a chance to retake the refuge some day.' WHERE `word_id` = 166788; -- locales_quest - details : entry=1016
UPDATE `trans_words` SET `content_loc6`='We could sure use some help out here.' WHERE `word_id` = 188351; -- locales_quest - offerrewardtext : entry=28496
UPDATE `trans_words` SET `content_loc6`='We could use more of your kind around here, $N. Thank you for your help.' WHERE `word_id` = 141341; -- locales_quest - offerrewardtext : entry=6442
UPDATE `trans_words` SET `content_loc6`='We couldn\'t have gotten where we are without you, $N. The combined might of the Aldor and Scryers is not enough to crush the Legion and Kael. We are thankful to have allies of your caliber among us.' WHERE `word_id` = 154592; -- locales_quest - offerrewardtext : entry=11556
UPDATE `trans_words` SET `content_loc6`='We dealt the Gordunni ogres a large blow at Dire Maul, but we haven\'t quite finished the job. My scouts have reported a group of them amassed in the hills to the north, gathering for some reason.$B$BIt\'s possible they\'re interested in the Horde settlement nearby, but I don\'t want to take any chances. Scout the area, kill any ogres you come across, and report back.' WHERE `word_id` = 177446; -- locales_quest - details : entry=25432
UPDATE `trans_words` SET `content_loc6`='We definitely don\'t want another sundering, friend. I\'ll do all that I can to help you on your mission.' WHERE `word_id` = 159691; -- locales_quest - offerrewardtext : entry=12439
UPDATE `trans_words` SET `content_loc6`='We definitely have a match here. And the presence of the ogres and what they were doing there explains everything. Now I get why it\'s called the Hewn Bog, duh!$B$BWe have to do something about this situation, but what?  We can\'t just wipe out all of those ogres; they have every bit as much a right to be here as any of the other creatures in the marsh!' WHERE `word_id` = 148046; -- locales_quest - offerrewardtext : entry=9708
UPDATE `trans_words` SET `content_loc6`='We did it, $N!  We held Nahom.' WHERE `word_id` = 188374; -- locales_quest - offerrewardtext : entry=28501
UPDATE `trans_words` SET `content_loc6`='We did it, $N! At terrible cost...but victory is ours. This keep could serve well as a home for me and my pack, though we tend to prefer more natural habitats. It may be best to leave it as a reminder of Godfrey\'s betrayal and of the Bloodfang warriors that died here in battle.' WHERE `word_id` = 186424; -- locales_quest - offerrewardtext : entry=27968
UPDATE `trans_words` SET `content_loc6`='We did it, $N! Aviana\'s Egg should soon be tucked safely in the boughs of her shrine. Do you think this means she\'ll really return to us?' WHERE `word_id` = 178673; -- locales_quest - offerrewardtext : entry=25764
UPDATE `trans_words` SET `content_loc6`='We did it, $N! Fighting that ... thing side-by-side really brought all the Wildhammers together. The heads of the families are more united than I\'ve ever seen them. Drink up, $N, and celebrate!! Of course, our work is only beginning. I\'ll rally our new Wildhammer reinforcements with the rest of the Alliance and prepare to storm the Twilight Bastion. I\'m sure we\'ll meet again soon.' WHERE `word_id` = 188938; -- locales_quest - offerrewardtext : entry=28655
UPDATE `trans_words` SET `content_loc6`='We did it, $N! Narkrall Rakeclaw lies dead at my feet, and Thundermar has weathered a storm of drakes! This wouldn\'t have been possible if it weren\'t for the help of the Alliance. I owe you a debt that I don\'t think can ever be repaid.' WHERE `word_id` = 187649; -- locales_quest - offerrewardtext : entry=28282
UPDATE `trans_words` SET `content_loc6`='We did it, $N. Thanks to you a good man has survived.' WHERE `word_id` = 173412; -- locales_quest - offerrewardtext : entry=14154
UPDATE `trans_words` SET `content_loc6`='We did it, $N. We\'ve started the evacuation. If we leave soon we\'ll leave the Forsaken fleet in the dust.' WHERE `word_id` = 182046; -- locales_quest - offerrewardtext : entry=26706
UPDATE `trans_words` SET `content_loc6`='We don\'t anticipate the Scourge letting up on their attacks any time soon, but we still appreciate any help we get guarding the Scar.' WHERE `word_id` = 146018; -- locales_quest - offerrewardtext : entry=8475
UPDATE `trans_words` SET `content_loc6`='We don\'t have much time. To get me out of here you are going to have to take out Warlord Roktrog. He holds the key to this prison.' WHERE `word_id` = 178220; -- locales_quest - offerrewardtext : entry=25662
UPDATE `trans_words` SET `content_loc6`='We done a great thing, mon! And dis just be da beginning....$B$BSeek me out in Zul\'Drak when da time be right.$B$BDere be a war to be won, mon!' WHERE `word_id` = 159137; -- locales_quest - offerrewardtext : entry=12238
UPDATE `trans_words` SET `content_loc6`='We done enough, $N. We can\'t keep fighting dese zombies. We need to figure out da source of dis voodoo. An\' I tink I know where to start...' WHERE `word_id` = 189980; -- locales_quest - offerrewardtext : entry=29150
UPDATE `trans_words` SET `content_loc6`='We done it, mon!$B$BI got da chieftains all caged up and you led my abominations to victory over their armies.$B$BIt be time ta celebrate, mon!' WHERE `word_id` = 160825; -- locales_quest - offerrewardtext : entry=12690
UPDATE `trans_words` SET `content_loc6`='We face heavy Horde counterattacks daily, $C. We need every pair of hands we can get to keep them at bay.' WHERE `word_id` = 188547; -- locales_quest - offerrewardtext : entry=28550
UPDATE `trans_words` SET `content_loc6`='We Forsaken are here because of a common enemy with the blood elves: Dar\'Khan! He has returned and now commands the Scourge that are invading this land from his steadfast, Deatholme, to the south.$B$BLady Sylvanas Windrunner, who originally hails from these lands, and used to be an elf, has a history with Dar\'Khan and wants him dead!$B$BWe will defeat him, $C, and you will help us do it!' WHERE `word_id` = 146667; -- locales_quest - offerrewardtext : entry=9327
UPDATE `trans_words` SET `content_loc6`='We furbolg are not foolish, $N. We will not yield Timbermaw Hold to any askers. However, we are also in dire need of assistance. If you help us, we will grant you our favor and allow you passage.' WHERE `word_id` = 188014; -- locales_quest - offerrewardtext : entry=28373
UPDATE `trans_words` SET `content_loc6`='We got him good, $N. But not good enough.$B$BOne thing\'s for sure... we won\'t be seeing him around these parts any longer.' WHERE `word_id` = 158798; -- locales_quest - offerrewardtext : entry=12164
UPDATE `trans_words` SET `content_loc6`='We got to meet a Great One! Too bad it was during his nap time, but we understand. We like naps, too! Maybe we learn naps from Great Ones like we learn about shrines and shinies!$B$BCan we come back and visit the Great One again when not sleeping?' WHERE `word_id` = 164553; -- locales_quest - offerrewardtext : entry=13956
UPDATE `trans_words` SET `content_loc6`='We have a map of the Highland approaches. The plan is clear: we will show ourselves in force. The Dragonmaw orcs, on seeing the might of our combined forces on their shore, will have no choice but to ally with us in our crusade to destroy the Twilight\'s Hammer.$B$BThe fleet is assembling at Bilgewater Port in Azshara. I grow impatient with their progress.$B$B$C! You have a knack for greasing the wheels of my war machine. Seek out Warlord Krogg in Azshara and find out what\'s taking so long.' WHERE `word_id` = 180701; -- locales_quest - details : entry=26324
UPDATE `trans_words` SET `content_loc6`='We have been blessed today!  You are alive and well!' WHERE `word_id` = 173803; -- locales_quest - offerrewardtext : entry=14280
UPDATE `trans_words` SET `content_loc6`='We have crushed them by land! Soon we will crush them by air!' WHERE `word_id` = 158322; -- locales_quest - offerrewardtext : entry=12053
UPDATE `trans_words` SET `content_loc6`='We have dealt a blow to Malygos, but it has come at a high price.' WHERE `word_id` = 158005; -- locales_quest - offerrewardtext : entry=11969
UPDATE `trans_words` SET `content_loc6`='We have done what we can for the creatures of the land. Now we must take direct action against those that have caused this!' WHERE `word_id` = 158808; -- locales_quest - offerrewardtext : entry=12166
UPDATE `trans_words` SET `content_loc6`='We have enough of a supply here on the isle to offer you a handful in exchange for a Zandalar Honor Token. Speak with Vinchaxa if you need to learn how to get tokens; otherwise, let\'s get to the business at hand!' WHERE `word_id` = 271912; -- locales_quest - objectives : entry=8196
UPDATE `trans_words` SET `content_loc6`='We have few records from the time around the War of the Ancients, especially near the destruction of the Well of Eternity. Given the upheaval and cataclysmic events that were taking place, it is no big surprise.$B$BThank you, $N. With your help, my work here is finished, and I will be able to deliver a full report to the Circle.' WHERE `word_id` = 134162; -- locales_quest - offerrewardtext : entry=953
UPDATE `trans_words` SET `content_loc6`='We have just begun this war to reclaim our forest from the forces of corruption, but a battle this day has been won! $N, the people of Auberdine owe you a debt of gratitude that will never be easily repaid. Please accept this along with our thanks. What we have learned here today may one day free our furbolg friends from the shackles of torment permanently.' WHERE `word_id` = 139374; -- locales_quest - offerrewardtext : entry=4763
UPDATE `trans_words` SET `content_loc6`='We have much to cover. In time, the ways of the sunwalkers will be as plain and natural to you as the shaman\'s conversations with the elements.' WHERE `word_id` = 183163; -- locales_quest - offerrewardtext : entry=27015
UPDATE `trans_words` SET `content_loc6`='We have no time to send orcs to the temple, $N. The humans come, and they are many. Stonard must be defended!' WHERE `word_id` = 186088; -- locales_quest - offerrewardtext : entry=27871
UPDATE `trans_words` SET `content_loc6`='We have our answers, $N. Let\'s report back.' WHERE `word_id` = 180428; -- locales_quest - offerrewardtext : entry=26254
UPDATE `trans_words` SET `content_loc6`='We have struck a major blow against the enemy with your help.$B$BHonor Hold is far safer than it\'s been in years.' WHERE `word_id` = 191281; -- locales_quest - offerrewardtext : entry=29654
UPDATE `trans_words` SET `content_loc6`='We have the information we need. Let\'s not tip our hand by engaging the enemy too soon.' WHERE `word_id` = 183142; -- locales_quest - offerrewardtext : entry=27010
UPDATE `trans_words` SET `content_loc6`='We have the people of Emberstone on our side now thanks to you.' WHERE `word_id` = 175209; -- locales_quest - offerrewardtext : entry=24676
UPDATE `trans_words` SET `content_loc6`='We have to persuade the goblins of Gadgetzan to side with us, $C.' WHERE `word_id` = 188396; -- locales_quest - offerrewardtext : entry=28507
UPDATE `trans_words` SET `content_loc6`='We have what we need. Are you ready to activate the Altar of Naias, $N?' WHERE `word_id` = 182141; -- locales_quest - offerrewardtext : entry=26730
UPDATE `trans_words` SET `content_loc6`='We haven\'t got much time. Listen carefully, $R. What I have to tell you is classified and for your ears only.' WHERE `word_id` = 138658; -- locales_quest - offerrewardtext : entry=3981
UPDATE `trans_words` SET `content_loc6`='We haven\'t gotten much in the way of reinforcements lately. We can use every pair of hands we can get.' WHERE `word_id` = 188600; -- locales_quest - offerrewardtext : entry=28565
UPDATE `trans_words` SET `content_loc6`='We heard all the commotion and were just about to leave. Glad to see you didn\'t take too bad of a beating.' WHERE `word_id` = 150650; -- locales_quest - offerrewardtext : entry=10330
UPDATE `trans_words` SET `content_loc6`='We here! Must work fast.$B$BWatch out for big-tongues.' WHERE `word_id` = 160102; -- locales_quest - offerrewardtext : entry=12536
UPDATE `trans_words` SET `content_loc6`='We kill many many wasps.$B$BRakjak don\'t think they run out.' WHERE `word_id` = 160087; -- locales_quest - offerrewardtext : entry=12533
UPDATE `trans_words` SET `content_loc6`='We knew the Alliance would send help and not a moment too soon!' WHERE `word_id` = 185552; -- locales_quest - offerrewardtext : entry=27726
UPDATE `trans_words` SET `content_loc6`='We knew the Horde would send help and not a moment too soon!' WHERE `word_id` = 185534; -- locales_quest - offerrewardtext : entry=27721
UPDATE `trans_words` SET `content_loc6`='We know all about the serpents, $N. But there is something else out there lurking... The jungle has come to life, $C!' WHERE `word_id` = 189809; -- locales_quest - offerrewardtext : entry=29105
UPDATE `trans_words` SET `content_loc6`='We learn from our life experiences, $N. From what this marker says, I am sure that you have only become stronger and wiser as a result of yours. You have grown much since we last spoke.$B$BTake this in remembrance of the difficult tasks you have accomplished as a $R $C, and know that you will always have my respect and admiration.' WHERE `word_id` = 139748; -- locales_quest - offerrewardtext : entry=5057
UPDATE `trans_words` SET `content_loc6`='We lost our battle against the fel orcs so very long ago. You have restored some of our lost honor by killing Urtrak. $B$BTake one of our weapons with you. When you are fighting our most hated foes, use this weapon, and those who are free of our curse will come to your aid. $B$BBefore you leave, those who died at the siege tower wish to honor you for destroying our enemies.' WHERE `word_id` = 150318; -- locales_quest - offerrewardtext : entry=10258
UPDATE `trans_words` SET `content_loc6`='We make it look easy.' WHERE `word_id` = 186863; -- locales_quest - offerrewardtext : entry=28094
UPDATE `trans_words` SET `content_loc6`='We may not have to live down the shame of this defeat, $N. The Warchief will likely have us all executed instead.$B$BI\'ll be damned if we don\'t go down without a fight. Although the whole beachhead is overrun by Bloodfang worgen, there may still be hope. I want you to accompany one of our sea pups to the Beachhead, directly west of here, and recover our supplies. Return to me when the pup can carry no more.' WHERE `word_id` = 183367; -- locales_quest - details : entry=27069
UPDATE `trans_words` SET `content_loc6`='We meet again. I have been watching your progress from afar, $C, and I am pleased.$B$BThe time has come for you to commune with the element of water, to learn its mysteries and give yourself over to the water so that you might in turn learn to master it.' WHERE `word_id` = 147242; -- locales_quest - offerrewardtext : entry=10490
UPDATE `trans_words` SET `content_loc6`='We might be too late for the shards to make a difference, but our options are somewhat limited.' WHERE `word_id` = 160316; -- locales_quest - offerrewardtext : entry=12579
UPDATE `trans_words` SET `content_loc6`='We must be quick or face being overtaken by the Scourge! There\'s really no time for instructions, $R. You\'re just going to have to jump in and hope for the best.' WHERE `word_id` = 162421; -- locales_quest - offerrewardtext : entry=13070
UPDATE `trans_words` SET `content_loc6`='We must first expand your mind so that you may commune with the ancestors.' WHERE `word_id` = 149438; -- locales_quest - offerrewardtext : entry=10044
UPDATE `trans_words` SET `content_loc6`='We must gather whatever data that they\'ve stored in this area.' WHERE `word_id` = 150695; -- locales_quest - offerrewardtext : entry=10339
UPDATE `trans_words` SET `content_loc6`='We must investigate your findings further, but we must not lose track of the fragment.' WHERE `word_id` = 180433; -- locales_quest - offerrewardtext : entry=26255
UPDATE `trans_words` SET `content_loc6`='We must make haste! As you can surely see, his time is not long.' WHERE `word_id` = 178753; -- locales_quest - offerrewardtext : entry=25794
UPDATE `trans_words` SET `content_loc6`='We must prepare you for the battles that lie ahead...' WHERE `word_id` = 150417; -- locales_quest - offerrewardtext : entry=10279
UPDATE `trans_words` SET `content_loc6`='We must recover that book from the Silver Covenant. Without that book from Krasus, we stand little chance of solving the mystery of the sword in Icecrown or unlocking its power.' WHERE `word_id` = 165043; -- locales_quest - offerrewardtext : entry=24555
UPDATE `trans_words` SET `content_loc6`='We must return all of the artifacts, $N. When all four are gathered, you will put them back in their graves.' WHERE `word_id` = 156795; -- locales_quest - offerrewardtext : entry=11511
UPDATE `trans_words` SET `content_loc6`='We must stop the Blackrock orcs before they can expand their influence.' WHERE `word_id` = 188957; -- locales_quest - offerrewardtext : entry=28667
UPDATE `trans_words` SET `content_loc6`='We must study these creatures further.$B$BFar to the east - near the huge lava flow beneath the ruins of Baa\'ri - you will find arcano control units. These controllers were crafted by a genius named Gnomus for the sole purpose of exploring lava via mechanical scorpion.$B$BHe has long since lost his mind and is now only interested in eating the hottest food known to gnome-kind.$B$B<Tiorus gives you a funny look.>$B$BI want you to use the control and tag the diemetradons of the lava flow for further study.' WHERE `word_id` = 273337; -- locales_quest - details : entry=10672
UPDATE `trans_words` SET `content_loc6`='We must take care in how we speak around the abbey and its environs, but don\'t let that deter you from the path of power.$B$BI want to show you one of my favorite spells. It will allow you to set an opponent on fire, damaging the enemy immediately and continuing to do more harm over time. I\'ll show you how it\'s done, and then I want you to go try it on one of the training dummies south of the entrance to the abbey.' WHERE `word_id` = 182750; -- locales_quest - details : entry=26914
UPDATE `trans_words` SET `content_loc6`='We need to keep moving, $N. Booty Bay won\'t hold out for much longer.' WHERE `word_id` = 182024; -- locales_quest - offerrewardtext : entry=26699
UPDATE `trans_words` SET `content_loc6`='We never knew trees got that big! Nope. Maybe Great Ones had special plan for it?' WHERE `word_id` = 164495; -- locales_quest - offerrewardtext : entry=13929
UPDATE `trans_words` SET `content_loc6`='We night elves know of the terror outlined in this report. Shandris was wise to send this, and you, to me.$B$BThese creatures will consume Feralas rapidly; they are nothing short of one of the greatest menaces we all will ever face. The insects you and the courier encountered are known as the silithid. They are mere tools of one of the greatest threats that both the Horde and Alliance will ever face.$B$BI pray we will have the resolve to defeat them before they destroy us.' WHERE `word_id` = 138957; -- locales_quest - offerrewardtext : entry=4267
UPDATE `trans_words` SET `content_loc6`='We owe a great debt to you for all that you\'ve done to see this battle through. Not often can we claim victories so decisively, but it seems there\'s far more to this plot than just the Shatterspear. If you haven\'t already checked his body for information, make sure to do so before you leave.' WHERE `word_id` = 172047; -- locales_quest - offerrewardtext : entry=13515
UPDATE `trans_words` SET `content_loc6`='We ran into a little - MINOR - problem. Everyone that tried handling the blight vaporized. We\'re working on adjusting the formula a tad so it doesn\'t instantly kill you for looking at it sideways. The good news is that I think I\'ve almost got it!' WHERE `word_id` = 158918; -- locales_quest - offerrewardtext : entry=12189
UPDATE `trans_words` SET `content_loc6`='We rarely enjoy the opportunity to employ a brave $C like you. Our archive grows more thorough, thanks to your efforts.' WHERE `word_id` = 191535; -- locales_quest - offerrewardtext : entry=30104
UPDATE `trans_words` SET `content_loc6`='We received reports that a goblin shipwreck occured on this beach some time ago, with the survivors left to fend for themselves. We expected there to be a modicum of survivors, perhaps some even suitable for interrogation.$B$BWhat we found was a crew of Bilgewater goblins who have all become insane, delusional, and maniacally violent...to further degrees than is normally observed, rather.$B$BIn short, they are useless and dangerous. Gnomeregan will be best served by cutting down as many as we can.' WHERE `word_id` = 176189; -- locales_quest - details : entry=25060
UPDATE `trans_words` SET `content_loc6`='We shall rejoice at your success, my young friend! There\'s blood in the water and it is our enemy\'s!$B$BYou have the eternal gratitude of this old fisherman, and most certainly of all the Kalu\'ak once I spread the word!$B$BHere now, I insist, take something as a reward, please.' WHERE `word_id` = 158180; -- locales_quest - offerrewardtext : entry=12017
UPDATE `trans_words` SET `content_loc6`='We should be able to focus our efforts on the rest of the trolls to the east, but there\'s still the matter of the wendigos in the Grizzled Den.' WHERE `word_id` = 178500; -- locales_quest - offerrewardtext : entry=25724
UPDATE `trans_words` SET `content_loc6`='We should have killed Bloodfang in Silverpine. It\'s a shame he still lives. It\'s an even bigger shame that we have as of yet been unable to turn worgen into undead. Surely there is a way...' WHERE `word_id` = 187844; -- locales_quest - offerrewardtext : entry=28332
UPDATE `trans_words` SET `content_loc6`='We should have known about the damned mausoleum. This is preposterous! Duke August Foehammer and Ambo Cash will be dispatched to the front lines in preparation for our next assault.$B$BOnce you have cleared the mines I will send you to Foehammer so that we may begin our attack against the Scourge of Naxxramas!$B$BThe information you have garnered thus far will be sent to Lord Fordragon at the Wrathgate upon the wings of our most able gryphon!' WHERE `word_id` = 159522; -- locales_quest - offerrewardtext : entry=12321
UPDATE `trans_words` SET `content_loc6`='We should not forget the orc Kargath once was, nor the lessons to be learned from his downfall.' WHERE `word_id` = 191278; -- locales_quest - offerrewardtext : entry=29653
UPDATE `trans_words` SET `content_loc6`='We suffer much from the events of the past. Thank you for what you have done to help protect the Timbermaw. As a sign of our growing friendship, please accept this humble offering.' WHERE `word_id` = 143569; -- locales_quest - offerrewardtext : entry=8460
UPDATE `trans_words` SET `content_loc6`='We suffer much from the events of the past. Thank you for what you have done to help protect the Timbermaw.' WHERE `word_id` = 145793; -- locales_quest - offerrewardtext : entry=6131
UPDATE `trans_words` SET `content_loc6`='We survived and all of the portals are now shutdown! You don\'t think Magmatooth is going to hold a grudge, do you?' WHERE `word_id` = 185601; -- locales_quest - offerrewardtext : entry=27745
UPDATE `trans_words` SET `content_loc6`='We survived the attack!$B$BWe could not have done it without you, $N.$B$BWe\'ll be heading out as soon as the tide rises. If you weren\'t such a promising hero I\'d offer you a place on my crew. But I can tell you have bigger fish to fry.$B$BEven if you are a puny $R....' WHERE `word_id` = 132994; -- locales_quest - offerrewardtext : entry=26628
UPDATE `trans_words` SET `content_loc6`='We thank you, hero.' WHERE `word_id` = 142058; -- locales_quest - offerrewardtext : entry=27110
UPDATE `trans_words` SET `content_loc6`='We think we seen you before. You visit Mistwhisper village sometime already?$B$BSoo-say said you good. Soo-say smart, so we trust you too.$B$B<Mistcaller Soo-gan offers an awkwardly large smile.>' WHERE `word_id` = 160293; -- locales_quest - offerrewardtext : entry=12574
UPDATE `trans_words` SET `content_loc6`='We trolls been subject to da splittin\' of empires for longer than some races been existin\'.$B$BWe always regrow from da ruins.' WHERE `word_id` = 191441; -- locales_quest - offerrewardtext : entry=29844
UPDATE `trans_words` SET `content_loc6`='We used to always comment that Luminrath would wear that cloak with just about anything. To him, red went equally well with white, black, purple, blue, yellow, orange, and green. He would never wear the same robe two days in a row, but the cloak was always there...' WHERE `word_id` = 150539; -- locales_quest - offerrewardtext : entry=10306
UPDATE `trans_words` SET `content_loc6`='We very glad you brought us here, $N. We always worried what would happen to us when the orphan matron found us in Sholazar.' WHERE `word_id` = 164509; -- locales_quest - offerrewardtext : entry=13933
UPDATE `trans_words` SET `content_loc6`='We welcome you to the world of Azeroth, and offer you one of these unique gifts.' WHERE `word_id` = 161206; -- locales_quest - offerrewardtext : entry=12781
UPDATE `trans_words` SET `content_loc6`='We were bound to run into the Twilight\'s Hammer sooner or later, $N. If they\'re behind this, it\'s quite likely that the World Pillar fragment is in their possession.' WHERE `word_id` = 180405; -- locales_quest - offerrewardtext : entry=26248
UPDATE `trans_words` SET `content_loc6`='We were driven to the edge of extinction once before, $N. But we druids, keepers of the old ways, saved our people from famine.$B$BWhen we cut ourselves off from the outside world and our crops failed, it was our order who called upon the earth\'s blessings and restored the harvest.$B$BJust as we did then, it is best to put our fate in the hands of a higher power. Join me now and learn what the wild has to teach us.' WHERE `word_id` = 173814; -- locales_quest - details : entry=14283
UPDATE `trans_words` SET `content_loc6`='We were forced to learn some magics to protect us from what is commonly called our greatest enemy: the arcane.$B$BI would teach you a spell to help you defend yourself and your companions from those who would wield such power.' WHERE `word_id` = 140619; -- locales_quest - offerrewardtext : entry=5676
UPDATE `trans_words` SET `content_loc6`='We were not naive, as our forefathers were. We were suspicious and cunning and ready for betrayal at every turn.$B$BYet our pride and greed drove us to ruin once more. How many more will be lost to Kel\'theril?' WHERE `word_id` = 188492; -- locales_quest - offerrewardtext : entry=28535
UPDATE `trans_words` SET `content_loc6`='We were once known as the People\'s Militia. These days, though, we fight under the banner of Stormwind, with the full support of King Varian Wrynn. Our goals here in Westfall are many: to clean up the mess left behind by the Defias, to expand the reach of the Alliance and to maintain order. So far we are failing on all fronts.$B$BTimes are worse now than when the Defias ran amok. Now with the indigenous gnolls of the region up in arms, we find ourselves backed into a corner. Help us, $N!' WHERE `word_id` = 180544; -- locales_quest - details : entry=26287
UPDATE `trans_words` SET `content_loc6`='We were proud and noble. Our faith in the light protected us, and with the Sunwell, we wanted for nothing.$B$BBecause of that blindness, we came here to repeat the tragedy of history, as our entire race would in time.' WHERE `word_id` = 188488; -- locales_quest - offerrewardtext : entry=28534
UPDATE `trans_words` SET `content_loc6`='We were shocked before we died. Shocked that one among our noble and austere kind could descend into vicious obsession so quickly. We died as naive fools, the victims of our own blithe assumptions.$B$BThe living would know that these events would play out yet again... both here, and among the entire elven race with the fall of Quel\'thalas.' WHERE `word_id` = 188433; -- locales_quest - offerrewardtext : entry=28518
UPDATE `trans_words` SET `content_loc6`='We were shot down by the Burning Legion, even though we waved a flag of truce. Who knew the Burning Legion was so mean? $B$BWe cannot come with you to Spinebreaker Post. We need to repair our zeppelin and be on our way.' WHERE `word_id` = 150114; -- locales_quest - offerrewardtext : entry=10213
UPDATE `trans_words` SET `content_loc6`='We will discuss the purpose for your visit soon. First. let me say that I am indeed impressed by your service and your skill in wielding the Light.' WHERE `word_id` = 148106; -- locales_quest - offerrewardtext : entry=9721
UPDATE `trans_words` SET `content_loc6`='We will fight to the last orc, $N. I hope you\'ll keep yourself alive long enough to properly destroy these trespassers.' WHERE `word_id` = 186017; -- locales_quest - offerrewardtext : entry=27852
UPDATE `trans_words` SET `content_loc6`='We will find a shred of good within these dragons, $N. Whether it is in the wild eggs you found, the bodies of the whelplings you slew, or the eggs that we have encouraged Nyxondra to lay... we will find an untainted black dragon.' WHERE `word_id` = 185701; -- locales_quest - offerrewardtext : entry=27770
UPDATE `trans_words` SET `content_loc6`='We will leave this place on our own, Lady $N - once we are certain that the evil within has been wholly destroyed. Your journey of legend is almost at an end.' WHERE `word_id` = 144372; -- locales_quest - offerrewardtext : entry=8801
UPDATE `trans_words` SET `content_loc6`='We will meet many times in the days to come. Each time you will hopefully leave my company a little more powerful... a little more prepared. Like the Eternal Flame, your spirit will burn bright and hot.$B$BGo now, $N. Go and test yourself. I will be here for you when you need.' WHERE `word_id` = 137778; -- locales_quest - offerrewardtext : entry=3093
UPDATE `trans_words` SET `content_loc6`='We will put those three to good use once we finally take the vault.$B$BSpeaking of which....' WHERE `word_id` = 161689; -- locales_quest - offerrewardtext : entry=12893
UPDATE `trans_words` SET `content_loc6`='We will take Grim Batol back from the Twilight\'s Hammer and thier allies. It may take time, but this is one charge that we cannot forsake.' WHERE `word_id` = 184730; -- locales_quest - offerrewardtext : entry=27505
UPDATE `trans_words` SET `content_loc6`='WE WILL USE... THESE... RUNESTONES TO ALLOW... YOU TO POSSESS... ONE OF... THE WATER ELEMENTALS.' WHERE `word_id` = 180042; -- locales_quest - offerrewardtext : entry=26141
UPDATE `trans_words` SET `content_loc6`='We will win this battle. Whatever it takes, we will not let these elementals back into Hyjal.' WHERE `word_id` = 189934; -- locales_quest - offerrewardtext : entry=29139
UPDATE `trans_words` SET `content_loc6`='We wish the process of creating a darkrune was foolproof, $N. Alas, it is not. Please, take this bag and look in it to see what we\'ve wrought today. We couldn\'t bear to look ourselves.$B$BRemember, if you get a darkrune, take it to Gahk to exchange for a crystalforged darkrune. Then you\'ll be able to once more bring down Shartuul\'s transporter.$B$BOh, and $N, don\'t forget to come by again tomorrow and pickup the banishing crystal. There are always more demons to be banished.' WHERE `word_id` = 153455; -- locales_quest - offerrewardtext : entry=11051
UPDATE `trans_words` SET `content_loc6`='We witnessed the cursed smoke rise from here. You are most brave. $C.' WHERE `word_id` = 152720; -- locales_quest - offerrewardtext : entry=10874
UPDATE `trans_words` SET `content_loc6`='We witnessed the detonation of the bomb. Terrible, terrible destruction... Farwatcher Glen, for better or for worse, is still intact. Unfortunately we\'ve nowhere to run.' WHERE `word_id` = 179288; -- locales_quest - offerrewardtext : entry=25925
UPDATE `trans_words` SET `content_loc6`='We won\'t have to worry about Gava\'xi anymore, thanks to you. Let us arrange for your reward now.' WHERE `word_id` = 148857; -- locales_quest - offerrewardtext : entry=9900
UPDATE `trans_words` SET `content_loc6`='We, too, are suspicious of the Dark Irons.' WHERE `word_id` = 188653; -- locales_quest - offerrewardtext : entry=28582
UPDATE `trans_words` SET `content_loc6`='We, too, are suspicious of the Dark Irons\' recent activities.' WHERE `word_id` = 188650; -- locales_quest - offerrewardtext : entry=28581
UPDATE `trans_words` SET `content_loc6`='We\'d all suspected da naga, but I knew nothin\' of the Twilight Cult bein\' beneath the waves. Somehow dey are corruptin\' and pervertin\' the elements, makin\' demselves into dese horrors. We goin\' ta have to be fightin\' both of dese threats to stand a chance of bringin\' peace ta Vashj\'ir. We don\'t have da men ta do that yet. Come, $C, we got much ta do.' WHERE `word_id` = 177149; -- locales_quest - offerrewardtext : entry=25334
UPDATE `trans_words` SET `content_loc6`='We\'ll beat them if they come by land and we\'ll beat them if they come by air. If these motherless undead come by sea we\'re going to give them the beating of their lives.' WHERE `word_id` = 157716; -- locales_quest - offerrewardtext : entry=11889
UPDATE `trans_words` SET `content_loc6`='We\'ll make a good profit on these, $N. I\'ll make sure Khoraazi hears about your work here.' WHERE `word_id` = 148819; -- locales_quest - offerrewardtext : entry=9892
UPDATE `trans_words` SET `content_loc6`='We\'ll need worg hides, and lots of them. It doesn\'t matter if they\'re torn, burned, or bloody, but we need plenty of them.$B$BActually, it\'s probably better if they\'re a little beat-up.$B$BYou\'ll find worgs both to the west and to the east of here.' WHERE `word_id` = 187210; -- locales_quest - details : entry=28178
UPDATE `trans_words` SET `content_loc6`='We\'re all fine here. A little shaken... but alive.' WHERE `word_id` = 173288; -- locales_quest - offerrewardtext : entry=14099
UPDATE `trans_words` SET `content_loc6`='We\'re all going to die!!!$B$B<You slap Megs to calm her down.>$B$BSorry about that, $G Mr. : Ms.; $N. Okay, we need to find a way to get a lot of moolah quickly.$B$BThere are reports of the Trade Prince sending hired looters into the streets.$B$BYou could take the hot rod out there and run them down, snatching the loot that they\'ve illegally stolen. There wouldn\'t be anything wrong with that, right?' WHERE `word_id` = 173324; -- locales_quest - details : entry=14121
UPDATE `trans_words` SET `content_loc6`='We\'re all in agreement then - the best course of action is to wait until we can narrow down the areas where these plates might lie hidden. You\'ve brought us this far $N, and I hope you will be the one to head back in to the field to confront the secrets of Uldum.$B$BAllow me to give you this honorarium in the meantime; this should cover any expenses you\'ve encountered in assisting us, and a little something extra. Be sure to check back with us soon... Uldum beckons us all, young one.' WHERE `word_id` = 137579; -- locales_quest - offerrewardtext : entry=2968
UPDATE `trans_words` SET `content_loc6`='We\'re all in agreement then - the best plan of attack is putting this on hold until we can narrow down the areas where these plates might lie hidden. You\'ve brought us this far $N, and I hope you will be the one to head back in to the field to unlock Uldum for us all!$B$BAllow me to give you this honorarium in the meantime; this should cover any expenses you\'ve encountered in assisting us, and a little something extra. Be sure to check back with us soon... Uldum beckons!' WHERE `word_id` = 137566; -- locales_quest - offerrewardtext : entry=2964
UPDATE `trans_words` SET `content_loc6`='We\'re bringing out the bug spray, eh? I guess it can\'t be helped.' WHERE `word_id` = 189036; -- locales_quest - offerrewardtext : entry=28706
UPDATE `trans_words` SET `content_loc6`='We\'re closing in on the Blackrock high command. There\'s nowhere left for those dogs to run.' WHERE `word_id` = 181993; -- locales_quest - offerrewardtext : entry=26692
UPDATE `trans_words` SET `content_loc6`='We\'re dealing with an organization here, rookie. You don\'t just off the richest bum in Westfall in broad daylight and leave no witnesses. Someone with a lot of power is behind these murders. What have you learned so far?' WHERE `word_id` = 180345; -- locales_quest - offerrewardtext : entry=26232
UPDATE `trans_words` SET `content_loc6`='We\'re facing heavy resistance from the Horde, $C. Any reinforcements are welcome.' WHERE `word_id` = 188550; -- locales_quest - offerrewardtext : entry=28551
UPDATE `trans_words` SET `content_loc6`='We\'re finally free of Razlo Crushcog and his interference! With the defeat of Crushcog and his forces, Thermaplugg can\'t afford to send any more of his followers to the surface. I can\'t wait for the day when we defeat him for good!' WHERE `word_id` = 180847; -- locales_quest - offerrewardtext : entry=26364
UPDATE `trans_words` SET `content_loc6`='We\'re going to be filthy rich, I tell you! Well, if we can get off of this island, that is.' WHERE `word_id` = 175532; -- locales_quest - offerrewardtext : entry=24859
UPDATE `trans_words` SET `content_loc6`='We\'re going to go places together, $N. With my good looks and your... can-do attitude, we\'re sure to make a killing.' WHERE `word_id` = 156599; -- locales_quest - offerrewardtext : entry=11434
UPDATE `trans_words` SET `content_loc6`='We\'re grateful for your support. Your presence on the battlefield gives us the time we need to refortify. Your efforts make all the difference.' WHERE `word_id` = 189925; -- locales_quest - offerrewardtext : entry=29137
UPDATE `trans_words` SET `content_loc6`='We\'re halfway there, $N. One more switch needs to be activated before we can retrieve the Etymidian.' WHERE `word_id` = 160225; -- locales_quest - offerrewardtext : entry=12559
UPDATE `trans_words` SET `content_loc6`='We\'re halfway to being able to remove Trol\'kalar from Trollbane\'s tomb. We are not exactly sure where the third sigil is, as its holders seem to move around Arathi quite a bit, but I have faith that you\'ll be able to find it.' WHERE `word_id` = 132883; -- locales_quest - offerrewardtext : entry=641
UPDATE `trans_words` SET `content_loc6`='We\'re here, $N. My brother\'s forces will learn the meaning of defeat today.' WHERE `word_id` = 162373; -- locales_quest - offerrewardtext : entry=13057
UPDATE `trans_words` SET `content_loc6`='We\'re in a bit of a bind, $C. Sarus\' spiders are starting to get a little uppity as we\'ve been unable to feed them for days. If we leave the murlocs will overtake our post! Furthermore, Warden Stillwater over at the Sludge Fields is demanding that we send him more humans! He is insatiable! We need help!' WHERE `word_id` = 186917; -- locales_quest - offerrewardtext : entry=28111
UPDATE `trans_words` SET `content_loc6`='We\'re in a real mess here, young $C. Deathwing and his Twilight armies have launched a full attack upon Azeroth. The king has called for all able-bodied citizens of the Alliance to lend a hand.$B$BThe Stormwind guards are hard pressed to keep the peace here, with so many of us in distant lands and so many threats pressing close. We\'re enlisting the aid of anyone willing to defend their home.$B$BIf you\'re here to answer the call, then speak with my superior, Marshal McBride. He\'s outside the abbey.' WHERE `word_id` = 166493; -- locales_quest - details : entry=783
UPDATE `trans_words` SET `content_loc6`='We\'re left with very few choices, $N. What we do next will be a critical decision.' WHERE `word_id` = 173841; -- locales_quest - offerrewardtext : entry=14294
UPDATE `trans_words` SET `content_loc6`='We\'re nearly there, $N. The time is now to charge further ahead and take out Walden!' WHERE `word_id` = 186231; -- locales_quest - offerrewardtext : entry=27920
UPDATE `trans_words` SET `content_loc6`='We\'re not out of the woods by any means, but your efforts have helped to slow the enemy down.$B$BCaptain Grondel up in Drak\'Agal will likely have need of you.$B$BFarewell, $N.' WHERE `word_id` = 160364; -- locales_quest - offerrewardtext : entry=12591
UPDATE `trans_words` SET `content_loc6`='We\'re proud to count you among us, $N, and we look forward to training with you.' WHERE `word_id` = 164155; -- locales_quest - offerrewardtext : entry=13704
UPDATE `trans_words` SET `content_loc6`='We\'re saved! You\'ve shot down enough of them that we should be able to handle the rest. I cannot thank you enough, $N.' WHERE `word_id` = 172602; -- locales_quest - offerrewardtext : entry=13853
UPDATE `trans_words` SET `content_loc6`='We\'ve already lost many brave warriors to this cursed wasteland. I have little interest in sending more untested recruits to the slaughter.$B$B<Nazgrel glares at you sternly through his ragged wolf-mask>$B$BStill, if General Krakork sent you to me, he must have seen something in you. What say you then, $N? Will you serve your warchief with honor?' WHERE `word_id` = 150471; -- locales_quest - offerrewardtext : entry=10291
UPDATE `trans_words` SET `content_loc6`='We\'ve been expecting you, $N. Akama is in great need of your help.' WHERE `word_id` = 153071; -- locales_quest - offerrewardtext : entry=10958
UPDATE `trans_words` SET `content_loc6`='We\'ve been expecting you, $N. Akama said you would arrive sooner or later.' WHERE `word_id` = 151548; -- locales_quest - offerrewardtext : entry=10575
UPDATE `trans_words` SET `content_loc6`='We\'ve been expecting your arrival, $N. Welcome to Northrend.' WHERE `word_id` = 157304; -- locales_quest - offerrewardtext : entry=11672
UPDATE `trans_words` SET `content_loc6`='We\'ve cleared straight through \'em! Jintha\'Alor is as good as wiped out!' WHERE `word_id` = 181343; -- locales_quest - offerrewardtext : entry=26516
UPDATE `trans_words` SET `content_loc6`='We\'ve come one step closer to achieving total victory over the Horde. We cannot allow the enemy to obtain a foothold in this world or any other.' WHERE `word_id` = 155987; -- locales_quest - offerrewardtext : entry=11337
UPDATE `trans_words` SET `content_loc6`='We\'ve done it! Cho\'gall himself has been utterly humiliated by you. His minions are dead, the Gordok are scattered, and now all will know the might of the Horde! Lok\'tar, $N. You\'ve more than earned your reward.' WHERE `word_id` = 177130; -- locales_quest - offerrewardtext : entry=25329
UPDATE `trans_words` SET `content_loc6`='We\'ve done it! The nightmare\'s connection to this place has been severed. The rest of the land is safe from this intrusion now, and in time, the abberations here will be subsumed by the balance of nature.$B$BI cannot thank you enough, $N. By helping to restore order here, you\'ve let me make amends for my foolish actions.' WHERE `word_id` = 175032; -- locales_quest - offerrewardtext : entry=24601
UPDATE `trans_words` SET `content_loc6`='We\'ve done it! Without your help, Ironforge Airfield would surely be occupied by the enemy and the Dark Irons would have a clear road to Ironforge itself. The Council of Three Hammers must be informed of what happened here.' WHERE `word_id` = 179909; -- locales_quest - offerrewardtext : entry=26102
UPDATE `trans_words` SET `content_loc6`='We\'ve done it, $N! Thank you for your help. You were truly amazing out there. I never thought a $C could handle so many enemies at once.$B$BYou\'ve truly shown bravery in the face of overwhelming odds.' WHERE `word_id` = 136034; -- locales_quest - offerrewardtext : entry=1651
UPDATE `trans_words` SET `content_loc6`='We\'ve done it, $N!$B$BWe have all of the components we need to perform the cleansing ritual.' WHERE `word_id` = 158383; -- locales_quest - offerrewardtext : entry=12068
UPDATE `trans_words` SET `content_loc6`='We\'ve done it, we\'ve really done it! The refuge is spared and the stormforged are defeated! We must warn Brann before Loken gets it in his mind to try to rebuild his forces. The only way to secure the victory you have won today is to go into Ulduar and defeat Loken for all time!' WHERE `word_id` = 162210; -- locales_quest - offerrewardtext : entry=13007
UPDATE `trans_words` SET `content_loc6`='We\'ve driven the Forsaken back. We hold three out of the four districts. But at what cost...' WHERE `word_id` = 175593; -- locales_quest - offerrewardtext : entry=24904
UPDATE `trans_words` SET `content_loc6`='We\'ve enjoyed having you ride with us, $N. Hopefully, once Gidwin and Tarenar join the Argent Crusade, we can continue to travel together.' WHERE `word_id` = 184485; -- locales_quest - offerrewardtext : entry=27448
UPDATE `trans_words` SET `content_loc6`='We\'ve given them everything we have... yet still they come. Do not worry, $N. We\'ll slay many more before today is over.' WHERE `word_id` = 173563; -- locales_quest - offerrewardtext : entry=14221
UPDATE `trans_words` SET `content_loc6`='We\'ve got quite the costly skeleton key there, don\'t we $N? Still, it remains unfinished; the time has now come to put some focus behind our creation. To that end, you\'re going to have to face the ultimate foe we\'ve been working towards facing in Andorhal... the being that runs the show within the ruined city walls.$B$BI\'m speaking of Araj the Summoner - a lich, and one of the most powerful beings the Scourge calls as its own.' WHERE `word_id` = 140704; -- locales_quest - offerrewardtext : entry=5801
UPDATE `trans_words` SET `content_loc6`='We\'ve got the horses, I\'ll make sure Duskhaven gets them.' WHERE `word_id` = 174310; -- locales_quest - offerrewardtext : entry=14416
UPDATE `trans_words` SET `content_loc6`='We\'ve got to keep moving!  We\'re getting so very close!' WHERE `word_id` = 184826; -- locales_quest - offerrewardtext : entry=27525
UPDATE `trans_words` SET `content_loc6`='We\'ve had strikes on our own lumber line while you\'ve been gone. This should at least put us on equal footing for gathering rates. Good work, $N.' WHERE `word_id` = 185899; -- locales_quest - offerrewardtext : entry=27822
UPDATE `trans_words` SET `content_loc6`='We\'ve hit the mother lode!!! Now, what\'s all this about a pygmy witchdoctor in charge of the monkeys? Is he dead? Because if he\'s dead, then legally I can stake my claim... not that I wasn\'t going to do that anyway. By $R law, all Kaja\'mite belongs to us!' WHERE `word_id` = 173137; -- locales_quest - offerrewardtext : entry=14021
UPDATE `trans_words` SET `content_loc6`='We\'ve lost a lot of good orcs, but the fight is going our way, $N. Mor\'ghor\'s regime is collapsing. All that remains is to destroy the beast himself.' WHERE `word_id` = 181732; -- locales_quest - offerrewardtext : entry=26621
UPDATE `trans_words` SET `content_loc6`='We\'ve lost so many good soldiers to that monster, Thel\'zan. I wish I could be there to see you crush his phylactery in front of him and end his existence.' WHERE `word_id` = 159802; -- locales_quest - offerrewardtext : entry=12467
UPDATE `trans_words` SET `content_loc6`='We\'ve managed to take this small area, but to do anything more we\'ll need your help.' WHERE `word_id` = 186786; -- locales_quest - offerrewardtext : entry=28069
UPDATE `trans_words` SET `content_loc6`='We\'ve sent everything we had at her and her workers, but they are strong in battle. None have bothered to return. I\'m assuming some have died, but I\'m sure others simply ran... cowards.$B$BBut now that reinforcements have arrived, perhaps there\'s still something we can do. Perhaps we can still punish her for lying and betraying Belgrom.$B$BWhat do you say, are you in?' WHERE `word_id` = 138174; -- locales_quest - offerrewardtext : entry=3504
UPDATE `trans_words` SET `content_loc6`='Weapon racks burned' WHERE `word_id` = 184054; -- locales_quest - ObjectiveText1 : entry=27310
UPDATE `trans_words` SET `content_loc6`='Weapon System Tested' WHERE `word_id` = 165413; -- locales_quest - ObjectiveText1 : entry=25295
UPDATE `trans_words` SET `content_loc6`='Weapons, eh? I\'m about to make an executive decision here, $N. Let\'s hope this pays off, else King Wrynn will have my head!' WHERE `word_id` = 178933; -- locales_quest - offerrewardtext : entry=25845
UPDATE `trans_words` SET `content_loc6`='Wear the Felsworn Gas Mask and use the Legion Communicator at Forge Camp: Anger.' WHERE `word_id` = 152476; -- locales_quest - objectives : entry=10819
UPDATE `trans_words` SET `content_loc6`='Webbed Crusader Freed' WHERE `word_id` = 162217; -- locales_quest - ObjectiveText1 : entry=13008
UPDATE `trans_words` SET `content_loc6`='Webwinder Hollow Spider slain' WHERE `word_id` = 179750; -- locales_quest - ObjectiveText2 : entry=26063
UPDATE `trans_words` SET `content_loc6`='Weeder Greenthumb Discovered' WHERE `word_id` = 148179; -- locales_quest - ObjectiveText2 : entry=29563
UPDATE `trans_words` SET `content_loc6`='Weeping Quarry Undead slaughtered' WHERE `word_id` = 162917; -- locales_quest - ObjectiveText1 : entry=13172
UPDATE `trans_words` SET `content_loc6`='Welcome $C! You and I are going to be good friends! I can tell already.' WHERE `word_id` = 187609; -- locales_quest - offerrewardtext : entry=28273
UPDATE `trans_words` SET `content_loc6`='Welcome aboard then, Valiant $N. The Exodar\'s delegation is proud to count you among its members.' WHERE `word_id` = 164108; -- locales_quest - offerrewardtext : entry=13690
UPDATE `trans_words` SET `content_loc6`='Welcome aboard then, Valiant $N. The Gnomeregan delegation is proud to count you among its members.' WHERE `word_id` = 164100; -- locales_quest - offerrewardtext : entry=13688
UPDATE `trans_words` SET `content_loc6`='Welcome aboard then, Valiant $N. The Stormwind delegation is proud to count you among its members.' WHERE `word_id` = 164090; -- locales_quest - offerrewardtext : entry=13684
UPDATE `trans_words` SET `content_loc6`='Welcome aboard, $N! You soon will have access to the most advanced engineering schemata anywhere on Azeroth.$B$BNow that you have committed yourself to the pursuit of this knowledge, it is time to examine your gnome-oriented engineering work. From there, we\'ll help you build a plan to maximize your gnome engineering talents!' WHERE `word_id` = 138395; -- locales_quest - offerrewardtext : entry=3640
UPDATE `trans_words` SET `content_loc6`='Welcome aboard, Goblin Engineer $N! You soon will have access to the most powerful engineering schemata anywhere on Azeroth.$B$BNow that you have committed yourself to the pursuit of this knowledge, it is time to examine your goblin-oriented engineering work. From there, we\'ll help you build a plan to maximize your goblin engineering talents!' WHERE `word_id` = 138386; -- locales_quest - offerrewardtext : entry=3638
UPDATE `trans_words` SET `content_loc6`='Welcome back - I\'ve been following your progress on this lesson since you left, and I must admit I was surprised to learn about the situation at the Crossroads. This does bear some further investigation, as the introduction of such poisons into the environment is most upsetting to the balance.$B$BYou handled yourself with grace under pressure. For that, I can find no better way to reward you with permanent power over poison. Use it well, $N... use it well.' WHERE `word_id` = 141043; -- locales_quest - offerrewardtext : entry=6130
UPDATE `trans_words` SET `content_loc6`='Welcome back - I\'ve been following your progress on this lesson since you left, and I must admit I was surprised to learn about the situation in Auberdine. This does bear some further investigation, as the introduction of such poisons into the environment is most upsetting to the balance.$B$BYou handled yourself with grace under pressure. For that, I can find no better way to reward you than with permanent power over poison. Use it well, $N... use it well.' WHERE `word_id` = 141026; -- locales_quest - offerrewardtext : entry=6125
UPDATE `trans_words` SET `content_loc6`='Welcome back from the battle! The Twilight\'s Hammer have been disgraced in the eyes of the Gordunni, and driven from Feralas.$B$BWe\'ve shown the glory of the Alliance in full force today! Be proud!' WHERE `word_id` = 177145; -- locales_quest - offerrewardtext : entry=25333
UPDATE `trans_words` SET `content_loc6`='Welcome back from your exercises, $N. Even while we practice for the competition, it\'s good to remind ourselves that the Scourge is still out there.' WHERE `word_id` = 163919; -- locales_quest - offerrewardtext : entry=13592
UPDATE `trans_words` SET `content_loc6`='Welcome back from your exercises, $N. I\'m confident that you will make us proud when the competition starts.' WHERE `word_id` = 164239; -- locales_quest - offerrewardtext : entry=13749
UPDATE `trans_words` SET `content_loc6`='Welcome back to duty soldier. Good to have you back.' WHERE `word_id` = 174492; -- locales_quest - offerrewardtext : entry=14478
UPDATE `trans_words` SET `content_loc6`='Welcome back to Nijel\'s Point, $N. So, tell me what you found.$B$BWhat!? Nothing? An empty chest ain\'t gonna get us paid, I\'m afraid.$B$BTell me more about these marks you saw... these slashes.' WHERE `word_id` = 135675; -- locales_quest - offerrewardtext : entry=1455
UPDATE `trans_words` SET `content_loc6`='Welcome back, $N. A short mission, but invigorating, yes? Take a rest for now. But come back when you have fresh orders.' WHERE `word_id` = 163784; -- locales_quest - offerrewardtext : entry=13404
UPDATE `trans_words` SET `content_loc6`='Welcome back, $N. How was the battle? Exciting? Take a rest for now. But come back when you have fresh orders.$B$BFor the Alliance!' WHERE `word_id` = 163742; -- locales_quest - offerrewardtext : entry=13382
UPDATE `trans_words` SET `content_loc6`='Welcome back, $N. I sense that your first meeting with the Great Bear Spirit is the same as it is with all druids when they first start down the path of the Claw... a little bewildering, but very intense. I know it was that way with me.$B$BThe Great Bear Spirit has been a part of Azeroth as long as Azeroth has existed in the heavens. We have come to rely on its wisdom and its power to fuel our purpose. Now, the time has now come for your first test of this purpose. Listen closely...' WHERE `word_id` = 140804; -- locales_quest - offerrewardtext : entry=5931
UPDATE `trans_words` SET `content_loc6`='Welcome back, $N. I sense that your first meeting with the Great Bear Spirit is the same as it is with all druids when they first start down the path of the Claw... a little bewildering, but very intense. I know it was that way with me.$B$BThe Great Bear Spirit has been a part of Azeroth as long as Azeroth has existed in the heavens. We tauren listen closely to such spirits, and we all prosper from its purpose. Now, the time has now come for your first test of this purpose. Listen closely...' WHERE `word_id` = 140808; -- locales_quest - offerrewardtext : entry=5932
UPDATE `trans_words` SET `content_loc6`='Welcome back, $N. I\'ve heard stories from the east. Who would have thought that a young $R $C like you would have been the one to wipe out the last whimpering remnants of the Scarlet Crusade?' WHERE `word_id` = 176005; -- locales_quest - offerrewardtext : entry=25012
UPDATE `trans_words` SET `content_loc6`='Welcome back, $N. What was done was not for revenge, or pride. It was simply to protect Thunder Bluff, and all our people, from torment and fear. Today, Mulgore is safe once more. The Earth Mother thanks you for all your brave deeds, $N, as do I. You have earned your reward, hero. May you be welcome in Thunder Bluff always.' WHERE `word_id` = 174920; -- locales_quest - offerrewardtext : entry=24540
UPDATE `trans_words` SET `content_loc6`='Welcome back, $N. You\'ve spent time reading the book, I hope?$B$BThen you should be prepared to have yourself tested. There are always tasks to be done throughout the city and our lands; acts of compassion and understanding; people who need help; creatures to slay.$B$BBy proving you have the patience to help others, especially those who are less fortunate, you prove that you are a servant of the Light and of sound mind.' WHERE `word_id` = 136018; -- locales_quest - offerrewardtext : entry=1646
UPDATE `trans_words` SET `content_loc6`='Welcome back, $N. Your report on this gazebo is most... disturbing. It would seem that even if the naga there were not planning on an attack, their continued presence bodes ill for our push into Feralas. At any rate, here is compensation for your investigation.$B$BIt would seem that Feathermoon Stronghold is in dire need of additional assistance concerning the Hatecrest naga. I\'d like to offer you the task, if you are so inclined. There is much to do.' WHERE `word_id` = 137331; -- locales_quest - offerrewardtext : entry=2867
UPDATE `trans_words` SET `content_loc6`='Welcome back, initiate. Now that you know the truth about the source of our power and you have demonstrated your skill in wielding it, I will grant you the ability to resurrect the fallen in the field.$B$BStay true to the order and excel at your training and you will only continue to grow in power.' WHERE `word_id` = 147942; -- locales_quest - offerrewardtext : entry=9685
UPDATE `trans_words` SET `content_loc6`='Welcome home, $N. Raschal checked in just a little while ago and told me what happened.$B$BI... no, the entire Stronghold can\'t even begin to thank you for your great deed; Raschal would have been dead without your timely aid, and your assistance came to us without even so much as the whisper of a mercenary\'s fee. You bear the mark of a true hero. Know that you will always have a friend in Ginro Hearthkindle.' WHERE `word_id` = 138949; -- locales_quest - offerrewardtext : entry=4265
UPDATE `trans_words` SET `content_loc6`='Welcome ta the Skyguard Outpost! I built it with me own hands ya know.$B$BYeah, the whole thing!$B$B<The sky commander looks serious.>$B$BSo, ye must be lookin\' ta spoil for a fight if ye\'re here talkin\' ta me. It was kind of old Chu\'a\'lor ta send ye along. We can always use fresh meat for the grinder around here.$B$BIf ye don\'t mind, I\'d like ya ta start by having a talk with my sergeant across the way: Vanderlip. Don\'t mind her tongue, though it be sharper than the greatsword she be carrying.' WHERE `word_id` = 153504; -- locales_quest - offerrewardtext : entry=11062
UPDATE `trans_words` SET `content_loc6`='Welcome to Aerie Peak, $N. On behalf of High Thane Falstad, I thank ye for comin\' to help us.' WHERE `word_id` = 181428; -- locales_quest - offerrewardtext : entry=26542
UPDATE `trans_words` SET `content_loc6`='Welcome to beautiful Bogpaddle!' WHERE `word_id` = 188983; -- locales_quest - offerrewardtext : entry=28677
UPDATE `trans_words` SET `content_loc6`='Welcome to Bloodhoof Village, hero. I am glad the spirits have guided you safely here.' WHERE `word_id` = 174546; -- locales_quest - offerrewardtext : entry=24215
UPDATE `trans_words` SET `content_loc6`='Welcome to Bogpaddle, $C. I fully expect you will enjoy all our facility has to offer.' WHERE `word_id` = 188975; -- locales_quest - offerrewardtext : entry=28675
UPDATE `trans_words` SET `content_loc6`='Welcome to Cenarion Hold, traveler.' WHERE `word_id` = 189440; -- locales_quest - offerrewardtext : entry=28859
UPDATE `trans_words` SET `content_loc6`='Welcome to Cenarion Hold.' WHERE `word_id` = 188464; -- locales_quest - offerrewardtext : entry=28527
UPDATE `trans_words` SET `content_loc6`='Welcome to Cenarion Hold.$B$B<Windcaller Proudhorn brushes some snow off of your shoulder.>$B$BYou\'ve certainly come from far off.' WHERE `word_id` = 189434; -- locales_quest - offerrewardtext : entry=28856
UPDATE `trans_words` SET `content_loc6`='Welcome to Cenarion Refuge, $N. I hope you\'ve come to aid us in our mission. We\'ve much work ahead of us.' WHERE `word_id` = 148909; -- locales_quest - offerrewardtext : entry=9912
UPDATE `trans_words` SET `content_loc6`='Welcome to Coldarra, $C.$B$BUnder the Transitus Shield we lie hidden just a stone\'s throw from the Nexus, fortress of the great aspect Malygos and his blue dragonflight.$B$BDo make yourself comfortable.' WHERE `word_id` = 157573; -- locales_quest - offerrewardtext : entry=11733
UPDATE `trans_words` SET `content_loc6`='Welcome to Dalaran! This inn is part of the Silver Enclave, the headquarters of the official Alliance presence in our city.$B$BDon\'t forget to bind your hearthstone here for easy travel to the city and access to the portal to Stormwind next door.' WHERE `word_id` = 191221; -- locales_quest - offerrewardtext : entry=29608
UPDATE `trans_words` SET `content_loc6`='Welcome to Dalaran.$B$BI\'ll see to it that the ring is returned.' WHERE `word_id` = 161239; -- locales_quest - offerrewardtext : entry=12791
UPDATE `trans_words` SET `content_loc6`='Welcome to Falcon Watch, $Gbrother:sister;. Many other blood elves haven\'t been fortunate enough to make it this far.$B$BIt\'s a terrible shame; our work in Outland has just begun.' WHERE `word_id` = 147239; -- locales_quest - offerrewardtext : entry=9499
UPDATE `trans_words` SET `content_loc6`='Welcome to Falcon Watch, $R. All our Horde allies are welcome here, especially those recommended by Martik.' WHERE `word_id` = 147236; -- locales_quest - offerrewardtext : entry=9498
UPDATE `trans_words` SET `content_loc6`='Welcome to Feralas, $C. I thank you for coming to assist us.' WHERE `word_id` = 174292; -- locales_quest - offerrewardtext : entry=14410
UPDATE `trans_words` SET `content_loc6`='Welcome to Fuselight!' WHERE `word_id` = 185676; -- locales_quest - offerrewardtext : entry=27763
UPDATE `trans_words` SET `content_loc6`='Welcome to Hardwrench Hideaway!' WHERE `word_id` = 189028; -- locales_quest - offerrewardtext : entry=28704
UPDATE `trans_words` SET `content_loc6`='Welcome to hell, soldier.$B$BMy men are leading the assault on the gate from this position, and we\'re dropping some infiltrators in the city via air - we need more people on both fronts. I have a feeling that this attack will be going on for some time.' WHERE `word_id` = 163601; -- locales_quest - offerrewardtext : entry=13341
UPDATE `trans_words` SET `content_loc6`='Welcome to K3!$B$BWe came here to make our way into those peaks and get our fair share of some of those amazing titan machines. Progress on that end has been somewhat minimal, though. Gnolls, snobolds, magnataur... there\'s no shortage of trouble to go around... not to mention, we can\'t seem to keep any fliers in the air without their motors freezing.$B$BThere\'s plenty of work to be done though - lend a hand, eh?' WHERE `word_id` = 161509; -- locales_quest - offerrewardtext : entry=12853
UPDATE `trans_words` SET `content_loc6`='Welcome to Light\'s Hope Chapel. You wish to join the Crusade?  Very well. We have plenty of tasks for new recruits. But first, a test.' WHERE `word_id` = 184553; -- locales_quest - offerrewardtext : entry=27463
UPDATE `trans_words` SET `content_loc6`='Welcome to Moonglade, $N. I have been looking forward to meeting you.' WHERE `word_id` = 183997; -- locales_quest - offerrewardtext : entry=27283
UPDATE `trans_words` SET `content_loc6`='Welcome to Nijel\'s Point, $R. Now, let\'s see about getting you briefed on our situation here.' WHERE `word_id` = 174178; -- locales_quest - offerrewardtext : entry=14384
UPDATE `trans_words` SET `content_loc6`='Welcome to Northridge, $C.' WHERE `word_id` = 182841; -- locales_quest - offerrewardtext : entry=26935
UPDATE `trans_words` SET `content_loc6`='Welcome to Orgrim\'s Hammer.$B$BFrom here we are laying our groundwork for the assault on Arthas. There\'s a lot of preparation to be done, but before we can get into the real meat of it, we want to make sure that the Argent Crusade is adequately reinforced - we\'re going to need their help later.$B$BIf you have not already, speak to Brother Keltan on the main deck.' WHERE `word_id` = 163825; -- locales_quest - offerrewardtext : entry=13419
UPDATE `trans_words` SET `content_loc6`='Welcome to our camp, $C, such as it is.' WHERE `word_id` = 189010; -- locales_quest - offerrewardtext : entry=28699
UPDATE `trans_words` SET `content_loc6`='Welcome to our camp, $C. You couldn\'t have arrived at a better time.' WHERE `word_id` = 182508; -- locales_quest - offerrewardtext : entry=26838
UPDATE `trans_words` SET `content_loc6`='Welcome to our humble camp. I wouldn\'t call it pleasant, but it\'s at least somewhat private. Beg your pardon? A worgen at the farm?' WHERE `word_id` = 182096; -- locales_quest - offerrewardtext : entry=26719
UPDATE `trans_words` SET `content_loc6`='Welcome to Outland, $N -- welcome to the nightmare! Though the initial Expeditionary Force made it through unscathed, the Legion is attempting to retake the Dark Portal and prevent any of our reinforcements from coming though.$B$BAs much as I\'d like your aid here, our troops and the Horde soldiers are able to fend off the Legion platoons that are attempting to take control of this platform.$B$BWe need you where your talents can best be utilized.' WHERE `word_id` = 149726; -- locales_quest - offerrewardtext : entry=10119
UPDATE `trans_words` SET `content_loc6`='Welcome to Revantusk, $C. I hope fighting the Witherbark hasn\'t exhausted you; the Vilebranch are even worse.' WHERE `word_id` = 181097; -- locales_quest - offerrewardtext : entry=26430
UPDATE `trans_words` SET `content_loc6`='Welcome to Shadebough, $N. Lady Shandris sent word of you.' WHERE `word_id` = 181560; -- locales_quest - offerrewardtext : entry=26574
UPDATE `trans_words` SET `content_loc6`='Welcome to Shadowprey Village, $R. I was not sure the Cenarion Circle would send anyone.' WHERE `word_id` = 174024; -- locales_quest - offerrewardtext : entry=14337
UPDATE `trans_words` SET `content_loc6`='Welcome to Starfall, $C. We have opened our doors to all travelers in our time of need.' WHERE `word_id` = 188971; -- locales_quest - offerrewardtext : entry=28674
UPDATE `trans_words` SET `content_loc6`='Welcome to Sunwell Plateau, cryer - What\'s that? Oh Scryer, yes of course. Isn\'t that what I said?$B$BWell, now, let\'s get a few things straight. There\'s some serious business going on behind those walls and it\'s in the interest of both of our people to address it. And since neither of us have the forces to go it alone, we\'re going to work - TOGETHER. Got that?$B$BNow let\'s get started, shall we?' WHERE `word_id` = 154379; -- locales_quest - offerrewardtext : entry=11482
UPDATE `trans_words` SET `content_loc6`='Welcome to Surwich, $R. Please, have a look around.' WHERE `word_id` = 180148; -- locales_quest - offerrewardtext : entry=26175
UPDATE `trans_words` SET `content_loc6`='Welcome to the Auchenai Crypts, $N, where our two worlds meet.' WHERE `word_id` = 191197; -- locales_quest - offerrewardtext : entry=29595
UPDATE `trans_words` SET `content_loc6`='Welcome to the Cathedral of Light, $C.' WHERE `word_id` = 138416; -- locales_quest - offerrewardtext : entry=3681
UPDATE `trans_words` SET `content_loc6`='Welcome to the Darkmoon Faire!  This voucher is good for FIVE, yes, FIVE prize tickets!$B$BWhen you accumulate prize tickets, you\'ll want to speak to me about redeeming them for valuable prizes. The more tickets you get, the more prizes you can win. Huzzah!' WHERE `word_id` = 142672; -- locales_quest - offerrewardtext : entry=7905
UPDATE `trans_words` SET `content_loc6`='Welcome to the Darnassus tournament delegation, $N. It will be a pleasure to train and compete alongside you.' WHERE `word_id` = 164104; -- locales_quest - offerrewardtext : entry=13689
UPDATE `trans_words` SET `content_loc6`='Welcome to the Greenwarden\'s grove, $C. I am glad Whelgar has told you of us. If you are able to lend us your assistance, I will be most grateful.' WHERE `word_id` = 180215; -- locales_quest - offerrewardtext : entry=26195
UPDATE `trans_words` SET `content_loc6`='Welcome to the home of my pack, $C. Prepare yourself for war.' WHERE `word_id` = 188050; -- locales_quest - offerrewardtext : entry=28381
UPDATE `trans_words` SET `content_loc6`='Welcome to the new frontier, $N. Ashenvale is a land of opportunity, one where a young $C like yourself is able to find boundless chances to prove their mettle. Look around the outpost here, and be sure to travel out to the Zoram Strand, as the Horde has another outpost there as well.$B$BYour presence here tells me that you\'ve come to learn more about the hunt. Listen close, and I will gladly share with you what you need to know.' WHERE `word_id` = 333805; -- locales_quest - offerrewardtext : entry=235
UPDATE `trans_words` SET `content_loc6`='Welcome to the pack, $R. Let\'s hope you don\'t mind getting a bit uncivilized.' WHERE `word_id` = 188062; -- locales_quest - offerrewardtext : entry=28384
UPDATE `trans_words` SET `content_loc6`='Welcome to the Plaguelands, hero. This is one of most dangerous frontiers in Azeroth, especially with the looming threat of the Scourge to our north and northeast. I am glad to hear that the clarion call of duty and service to the greater good did not fall on deaf ears with you!$B$BIf you are ready to get your hands dirty, then there is plenty for willing and able heroes to do here on the edge of the Plaguelands.' WHERE `word_id` = 139781; -- locales_quest - offerrewardtext : entry=5066
UPDATE `trans_words` SET `content_loc6`='Welcome to the Plaguelands, hero. This is one of most dangerous frontiers in Azeroth, with the looming threat of the Scourge to our east, and then even further still eastward from there. I am glad to hear that the call of duty and service to the Horde did not fall on deaf ears with you!$B$BIf you are ready to get your hands dirty, then there is plenty for willing and able heroes to do here on the edge of the Plaguelands.' WHERE `word_id` = 139839; -- locales_quest - offerrewardtext : entry=5094
UPDATE `trans_words` SET `content_loc6`='Welcome to the Plaguelands, hero. This is one of the most dangerous frontiers in Azeroth, especially with the looming threat of the Scourge to our north and northeast. I am glad to hear that the clarion call of duty and service to the greater good did not fall on deaf ears with you!$B$BIf you are ready to get your hands dirty, then there is plenty for willing and able heroes to do here on the edge of the Plaguelands.' WHERE `word_id` = 139830; -- locales_quest - offerrewardtext : entry=10373
UPDATE `trans_words` SET `content_loc6`='Welcome to the Plaguelands, hero. This is one of the most dangerous frontiers in Azeroth, with the looming threat of the Scourge to our east, and then even further still eastward from there. I am glad to hear that the call of duty and service to the Horde did not fall on deaf ears with you!$B$BIf you are ready to get your hands dirty, then there is plenty for willing and able heroes to do here on the edge of the Plaguelands.' WHERE `word_id` = 334616; -- locales_quest - offerrewardtext : entry=10374
UPDATE `trans_words` SET `content_loc6`='Welcome to the Skybreaker.$B$BFrom here we are laying our groundwork for the assault on Arthas. There\'s a lot of work to be done, but before we can get into the real meat of it, we want to make sure that the Argent Crusade is adequately reinforced - we\'re going to need their help later.$B$BIf you have not already, speak to Absalan the Pious on the main deck.' WHERE `word_id` = 163822; -- locales_quest - offerrewardtext : entry=13418
UPDATE `trans_words` SET `content_loc6`='Welcome to the Sunspire, young $C!  Mastery of weapons and battle tactics begins here and now - all I ask of my pupils is that they cover payment for training costs that may come up, and that they are as good of a listener as they are an aspiring $C. The lessons I have to teach are extensive, and I don\'t like to repeat myself unnecessarially. Let\'s get to it then!' WHERE `word_id` = 271975; -- locales_quest - offerrewardtext : entry=8329
UPDATE `trans_words` SET `content_loc6`='Welcome to the Temple of the Moon, $N. I am glad that you chose to answer my summons so quickly.' WHERE `word_id` = 183945; -- locales_quest - offerrewardtext : entry=27269
UPDATE `trans_words` SET `content_loc6`='Welcome to the Undercity delegation. $N. As a valiant, you\'ll train with us to prepare you for the highest level of competition.' WHERE `word_id` = 164127; -- locales_quest - offerrewardtext : entry=13695
UPDATE `trans_words` SET `content_loc6`='Welcome to the Wizard\'s Sanctum, $N. I have been watching your progress with interest.' WHERE `word_id` = 183953; -- locales_quest - offerrewardtext : entry=27271
UPDATE `trans_words` SET `content_loc6`='Welcome to the World of Warcraft!$B$BAs special thanks for purchasing the World of Warcraft Collector\'s Edition, turn in this gift voucher to Jaeleil at the Crash Site in Ammen Vale. You\'ll then be given a gift: a little companion to join you on your quest for adventure and glory.$B$BThanks again, and enjoy your stay in the World of Warcraft!' WHERE `word_id` = 146582; -- locales_quest - details : entry=9278
UPDATE `trans_words` SET `content_loc6`='Welcome to the World of Warcraft!$B$BAs special thanks for purchasing the World of Warcraft Collector\'s Edition, turn in this gift voucher to Marsilla Dawnstar at the Sunspire on Sunstrider Isle. You\'ll then be given a gift: a little companion to join you on your quest for adventure and glory.$B$BThanks again, and enjoy your stay in the World of Warcraft!' WHERE `word_id` = 146088; -- locales_quest - details : entry=8547
UPDATE `trans_words` SET `content_loc6`='Welcome to Thunder Bluff, $N. You\'ve done great things for our people. Tell me of your journey.' WHERE `word_id` = 174938; -- locales_quest - offerrewardtext : entry=24550
UPDATE `trans_words` SET `content_loc6`='Welcome to your first true test as a $R.' WHERE `word_id` = 174387; -- locales_quest - offerrewardtext : entry=14449
UPDATE `trans_words` SET `content_loc6`='Welcome! You seem to have survived the trials relatively unscathed. Good for you, love. You look to have come with a purpose in mind as well. A question. Your thoughts are very loud, it\'s hard for me to not hear them. Go ahead then, hon. Ask away.' WHERE `word_id` = 173860; -- locales_quest - offerrewardtext : entry=14299
UPDATE `trans_words` SET `content_loc6`='Welcome, $C. Are you ready to get to work?' WHERE `word_id` = 174757; -- locales_quest - offerrewardtext : entry=24487
UPDATE `trans_words` SET `content_loc6`='Welcome, $C. As you can see, our camp is a joint expedition between Argent Crusade members and Cenarion Circle druids, but we welcome all who wish to help heal the land.' WHERE `word_id` = 182907; -- locales_quest - offerrewardtext : entry=26952
UPDATE `trans_words` SET `content_loc6`='Welcome, $C. It is good to see that the road to Mor\'shan Ramparts remains clear.' WHERE `word_id` = 172382; -- locales_quest - offerrewardtext : entry=13628
UPDATE `trans_words` SET `content_loc6`='Welcome, $C. My scientists and explorers will be glad to have your help.' WHERE `word_id` = 188460; -- locales_quest - offerrewardtext : entry=28526
UPDATE `trans_words` SET `content_loc6`='Welcome, $C. The Lady has informed me of your coming, and if we are to bring her back to the surface then we have much to discuss...' WHERE `word_id` = 133085; -- locales_quest - offerrewardtext : entry=688
UPDATE `trans_words` SET `content_loc6`='Welcome, $C. Try not to give our position away.$B$BWe wouldn\'t last long if Kael\'s cronies sniff us out.' WHERE `word_id` = 151868; -- locales_quest - offerrewardtext : entry=10652
UPDATE `trans_words` SET `content_loc6`='Welcome, $C. We\'re gonna make the Vilebranch regret ever crossin\' us.' WHERE `word_id` = 181103; -- locales_quest - offerrewardtext : entry=26432
UPDATE `trans_words` SET `content_loc6`='Welcome, $N - I am Matron Arena. I offer my services to those who seek to learn the ways of the $C. Such a path is often misconstrued as a path of pacifism; for we blood elves, this couldn\'t be further from the truth. While we do mend both bone and spirit, we also act as a fist of vengeance for those who would transgress against us.$B$BI will train you, but all I ask is that you cover any training costs that may come up, and that you learn with an open mind. If you agree to this, we may begin.' WHERE `word_id` = 146099; -- locales_quest - offerrewardtext : entry=8564
UPDATE `trans_words` SET `content_loc6`='Welcome, $N. Are you here to join in the festivities?' WHERE `word_id` = 144474; -- locales_quest - offerrewardtext : entry=8870
UPDATE `trans_words` SET `content_loc6`='Welcome, $N. I am glad that you decided to accept my invitation.' WHERE `word_id` = 183949; -- locales_quest - offerrewardtext : entry=27270
UPDATE `trans_words` SET `content_loc6`='Welcome, $N. I\'m glad that you accepted my invitation. We have much to discuss.' WHERE `word_id` = 184136; -- locales_quest - offerrewardtext : entry=27335
UPDATE `trans_words` SET `content_loc6`='Welcome, $N. I\'m glad to hear you\'ve decided to join us in our mission. May the Light always protect you.' WHERE `word_id` = 151446; -- locales_quest - offerrewardtext : entry=10554
UPDATE `trans_words` SET `content_loc6`='Welcome, $N. We have much to discuss.' WHERE `word_id` = 187563; -- locales_quest - offerrewardtext : entry=28262
UPDATE `trans_words` SET `content_loc6`='Welcome, $N. We will need your skills in the continuing battle against Twilight\'s Hammer.' WHERE `word_id` = 187665; -- locales_quest - offerrewardtext : entry=28287
UPDATE `trans_words` SET `content_loc6`='Welcome, $N. Word reached me of your coming, and of your exploits in Durotar.$B$BYou are a $C of growing skill and renown.$B$BStay on the pure path, and your future will be great indeed.' WHERE `word_id` = 133629; -- locales_quest - offerrewardtext : entry=25261
UPDATE `trans_words` SET `content_loc6`='Welcome, brotha. You\'ve chosen a harsh time to be joinin\' us, but that is to our fortune. We could use ya help.' WHERE `word_id` = 184278; -- locales_quest - offerrewardtext : entry=27374
UPDATE `trans_words` SET `content_loc6`='Welcome, friend. We are honored that you have decided to embrace the truth and join us. Come, there will be more time to talk later and you have much to learn.' WHERE `word_id` = 171819; -- locales_quest - offerrewardtext : entry=25288
UPDATE `trans_words` SET `content_loc6`='Welcome, my friend!$B$BThe fruits of our labor have paid off abundantly! The time has come for us to give a dress rehearsal.$B$BOh, before we begin, I have a favor to ask.$B$BThere was a small snafu with a stubborn pygmy, so I have to ask that you take his place for the performance.$B$BI\'m sure you\'ll be able to pick it right up. You\'re a natural!$B$BBreak a leg, my friend!' WHERE `word_id` = 189415; -- locales_quest - details : entry=28851
UPDATE `trans_words` SET `content_loc6`='Welcome, then, $N. I look forward to watching you train with my men.' WHERE `word_id` = 164167; -- locales_quest - offerrewardtext : entry=13707
UPDATE `trans_words` SET `content_loc6`='Welcome, traveler. Thank you for responding to my summons.' WHERE `word_id` = 178376; -- locales_quest - offerrewardtext : entry=25696
UPDATE `trans_words` SET `content_loc6`='Welcome, welcome, welcome!  Please, $N, have a seat and discuss a simple matter with me.$B$BYou see, $N, I\'ve recently lost something dear to me, and to put it bluntly, I want it back at all costs. There\'s something in it for you, don\'t worry about that!  Oh yes, something very nice.$B$BNow, what say you $N... are you up for some adventure?' WHERE `word_id` = 143066; -- locales_quest - offerrewardtext : entry=8233
UPDATE `trans_words` SET `content_loc6`='Welcome, welcome, welcome, $N. Good of you to join us. Perhaps an ale today? Or some mutton?$B$BName your pleasure and I\'ll see to it that you\'re taken care of. My name is Mikhail, and I look forward to serving you.$B$BHmm? What\'s that? Trias? Keep your voice down, my friend. This isn\'t something for the whole tavern to hear about.' WHERE `word_id` = 135230; -- locales_quest - offerrewardtext : entry=1248
UPDATE `trans_words` SET `content_loc6`='Welcome, young $C.$B$BThe power of the Blood Knights is taken from a being of immense power.$B$B<The magister gestures to the large, captive being in the center of the room.>$B$BIsn\'t it magnificent? \'Twas a gift from our beloved Prince Kael\'thas, and it is the foundation of the Blood Knights\' mastery of the Light. This creature begrudges us its power, so we have devised a method for claiming it on our own terms.' WHERE `word_id` = 147923; -- locales_quest - offerrewardtext : entry=9681
UPDATE `trans_words` SET `content_loc6`='Welcome, young $R. Ya come highly recommended, mon. It\'s nice ta finally be meetin\' ya in person.' WHERE `word_id` = 176244; -- locales_quest - offerrewardtext : entry=25073
UPDATE `trans_words` SET `content_loc6`='Welcome, young initiate. That you have answered my summons so promptly bodes well for you.$B$BLet us dispense with pleasantries, then, as they are best left to courtiers and their ilk.$B$BYour rapid progress has caught my attention, $N. We will see if you have taken your lessons to heart.' WHERE `word_id` = 147908; -- locales_quest - offerrewardtext : entry=9677
UPDATE `trans_words` SET `content_loc6`='Welcome. My brothers and I are dedicated to the redemption and rejuvenation of this once-sacred forest.' WHERE `word_id` = 188521; -- locales_quest - offerrewardtext : entry=28542
UPDATE `trans_words` SET `content_loc6`='Well $N, I\'m glad to have you on board as my apprentice. You\'ve made a wise decision as a leatherworker; those who make use of leather armor will be clamoring to you once you learn how to master the art of Wild Leather.$B$BLet\'s get to work - where do you want to start?' WHERE `word_id` = 137278; -- locales_quest - offerrewardtext : entry=2854
UPDATE `trans_words` SET `content_loc6`='Well $N, I\'m glad to have you on board. You\'ve made a smart decision as a leatherworker; those who wear leather armor will be clamoring to you once you learn how to make any of the Wild Leather armor pieces.$B$BLet\'s get to work - where do you want to start?' WHERE `word_id` = 137249; -- locales_quest - offerrewardtext : entry=2847
UPDATE `trans_words` SET `content_loc6`='Well $N, you must have pleased Sage Truthseeker greatly for him to give you this item. You are also to be granted a gift from his cache of potions; you may choose between 5 superior healing potions or 5 greater mana potions as your reward.' WHERE `word_id` = 136853; -- locales_quest - offerrewardtext : entry=2440
UPDATE `trans_words` SET `content_loc6`='Well $N, you were wise to seek my tutelage! Any schlep can become an expert at the basics of engineering - the gnomes and their irradiated home of Gnomeregan are proof of that. It takes a strong mind and a steady hand (emphasis on STEADY) though to master the discipline of Goblin Engineering.$B$BKeep your ears perked $N, and you might learn something.' WHERE `word_id` = 334466; -- locales_quest - offerrewardtext : entry=3526
UPDATE `trans_words` SET `content_loc6`='Well and good, $C.$B$BNow that you\'ve seen firsthand what we\'re up against, it\'s time to get to work...' WHERE `word_id` = 163498; -- locales_quest - offerrewardtext : entry=13315
UPDATE `trans_words` SET `content_loc6`='Well blow me down!  Now there\'s something ya don\'t see too often. Welcome aboard, $N. Plunder and booty await ye.' WHERE `word_id` = 181759; -- locales_quest - offerrewardtext : entry=26629
UPDATE `trans_words` SET `content_loc6`='Well Cleansed' WHERE `word_id` = 174594; -- locales_quest - EndText : entry=24440
UPDATE `trans_words` SET `content_loc6`='Well done - I knew you\'d be perfect for the task. Once it\'s safe to make use of our outdoor facilities on the isle, I\'ll be putting these things to good use.$B$BLike I mentioned, please feel free to keep the satchel. Also, you may find this piece of armor to be useful. Consider it ample compensation for a simple task performed dutifully!' WHERE `word_id` = 145929; -- locales_quest - offerrewardtext : entry=8330
UPDATE `trans_words` SET `content_loc6`='Well done $C, well done! I will continue to send forces to thin out the furbolg, but I can at least count on a different path that will be free of Alliance entanglements. The Horde conquest of Ashenvale is now in full swing!$B$BYou\'ve earned your pay this day, hero. I salute you!' WHERE `word_id` = 131062; -- locales_quest - offerrewardtext : entry=216
UPDATE `trans_words` SET `content_loc6`='Well done $C. The arakkoa have learned to fear the might of the Alliance.' WHERE `word_id` = 152703; -- locales_quest - offerrewardtext : entry=10869
UPDATE `trans_words` SET `content_loc6`='Well done $C. The arakkoa have learned to fear the might of the Horde.' WHERE `word_id` = 152698; -- locales_quest - offerrewardtext : entry=10868
UPDATE `trans_words` SET `content_loc6`='Well done $N - it should take Alchemist Arbington no time at all to analyze this and devise a counter agent to introduce into the cauldron. If we are able to pump out enough of it into the air, we might be able to weaken all the Scourge present here.$B$BThis will no doubt be an ongoing ordeal; we will need to continually hit these cauldrons and introduce our reagents into their mix. Still, if we can keep it up, we will have a huge advantage over Scourge when the time comes for battle!' WHERE `word_id` = 140087; -- locales_quest - offerrewardtext : entry=5217
UPDATE `trans_words` SET `content_loc6`='Well done $N - it should take Apothecary Dithers no time at all to analyze this and devise a counter agent to introduce into the cauldron. If we are able to pump out enough of it into the air, we might be able to weaken all the Scourge present here.$B$BThis will no doubt be an ongoing ordeal; we will need to continually hit these cauldrons and introduce our reagents into their mix. Still, if we can keep it up, we will have a huge advantage over Scourge when the time comes for battle!' WHERE `word_id` = 140130; -- locales_quest - offerrewardtext : entry=5230
UPDATE `trans_words` SET `content_loc6`='Well done $N, I will be sure to give these to the Arch Druid himself when he has need of them. Meanwhile, please accept this as a token of the Cenarion Circle\'s appreciation.$B$BOur need for morrowgrain, for now, is constant; if you wish to continue aiding us, then please return when you have cultivated more of it.' WHERE `word_id` = 138463; -- locales_quest - offerrewardtext : entry=3786
UPDATE `trans_words` SET `content_loc6`='Well done $N, I will be sure to give these to the Arch Druid himself when he has need of them. Meanwhile, please accept this as a token of the Cenarion Circle\'s appreciation.$B$BOur need for morrowgrain, for now, is constant; if you wish to continue aiding us, then please return when you have cultivated more of it.' WHERE `word_id` = 270970; -- locales_quest - offerrewardtext : entry=3785
UPDATE `trans_words` SET `content_loc6`='Well done $N, you\'ve proven your worth once again! The caps will be an interesting study, yon verily!$B$BAnd now, as promised, enjoy a taste of one of my more potent restorative brews from my personal stock...$B$BIf you are a skilled alchemist, then perhaps we might have some additional business to conduct. Regardless, I thank you for your assistance!' WHERE `word_id` = 130212; -- locales_quest - offerrewardtext : entry=17
UPDATE `trans_words` SET `content_loc6`='Well done $N, you\'ve proven your worth once again! The caps will be an interesting study, you betcha.$B$BAnd now, as promised, enjoy a taste of one of my more potent restorative brews from my personal stock...$B$BIf you are a skilled alchemist, then perhaps we might have some additional business to conduct. Regardless, I thank you for your assistance!' WHERE `word_id` = 136663; -- locales_quest - offerrewardtext : entry=2202
UPDATE `trans_words` SET `content_loc6`='Well done $N. But for every element that is subdued another one takes it\'s place. The solution we have here is merely a stopgap. Hopefully my brethren in the Earthen Ring are making more headway.' WHERE `word_id` = 171786; -- locales_quest - offerrewardtext : entry=25055
UPDATE `trans_words` SET `content_loc6`='Well done fleshling. Your presence complimented my brothers well.' WHERE `word_id` = 181604; -- locales_quest - offerrewardtext : entry=26584
UPDATE `trans_words` SET `content_loc6`='Well done indeed... I\'d expect nothing less from my own daughter. She\'ll make a fine witch doctor someday.' WHERE `word_id` = 178128; -- locales_quest - offerrewardtext : entry=25641
UPDATE `trans_words` SET `content_loc6`='Well done my odorific associate. You are both swift and assiduous, if I do say so myself. May your future travels be rampant with inspiration and opulence.' WHERE `word_id` = 177778; -- locales_quest - offerrewardtext : entry=25538
UPDATE `trans_words` SET `content_loc6`='Well done once again, $N. Yet another threat removed from Feralas.' WHERE `word_id` = 177449; -- locales_quest - offerrewardtext : entry=25433
UPDATE `trans_words` SET `content_loc6`='Well done!  I have no doubt that those goats will be back, but they\'ll think twice before they nibble my cogs again!' WHERE `word_id` = 185731; -- locales_quest - offerrewardtext : entry=27776
UPDATE `trans_words` SET `content_loc6`='Well done!  That kinda shouting is sure to bring business our way.$B$BAnd we promised you a reward, here it is. The Barleybrews keep their word.$B$BOh, and come back any other day if you want to help spread the good name of Barleybrew.' WHERE `word_id` = 273825; -- locales_quest - offerrewardtext : entry=11293
UPDATE `trans_words` SET `content_loc6`='Well done!  Yes, this should be enough essence to get started on my research. I have a strong feeling that this tainted essence will be just the thing... the window, as I mentioned.$B$BFollowing the orders of your masters is highly recommended as a young $C. As I promised you, here is the reward for obeying instructions and completing them in a competent manner - the knowledge to summon and bind your own imp.$B$BUse it well, $N' WHERE `word_id` = 145956; -- locales_quest - offerrewardtext : entry=8344
UPDATE `trans_words` SET `content_loc6`='Well done!  You\'ll be a fire juggler faster than the fuse on a ground flower!  Now gird yourself!  The next test is when things really heat up...' WHERE `word_id` = 154707; -- locales_quest - offerrewardtext : entry=11921
UPDATE `trans_words` SET `content_loc6`='Well done! A fine kill that was.$B$BI will send a gathering party to collect the carcass so we can retrieve the hide and meat.$B$BYou have done a great deed for your people, $N.' WHERE `word_id` = 133465; -- locales_quest - offerrewardtext : entry=776
UPDATE `trans_words` SET `content_loc6`='Well done! And here, please... keep the basket!$B$BRemember, you can always return with more Noblegarden Chocolates to trade for additional items.' WHERE `word_id` = 163913; -- locales_quest - offerrewardtext : entry=13502
UPDATE `trans_words` SET `content_loc6`='Well done! Graveyards are vital to our control of Alterac Valley. When you capture one, you help ensure the Alliance\'s sovereignty here.$B$BGo now, $N. Reenter the battle, and if you see a graveyard under the Horde\'s control... you know what to do!' WHERE `word_id` = 141847; -- locales_quest - offerrewardtext : entry=7081
UPDATE `trans_words` SET `content_loc6`='Well done! How does it feel? It is challenging to master flight in this burning wasteland, but it is the only way. I hope you\'re ready for battle, $N...' WHERE `word_id` = 177731; -- locales_quest - offerrewardtext : entry=25523
UPDATE `trans_words` SET `content_loc6`='Well done! I can\'t hope to replicate the feats of Grand Warlock Nethekurse, nor should I aspire to, but we would be fools to ignore the power of the fel embers.$B$BHowever, there are those who would forbid the type of research I\'m about to embark upon, and for that reason, I must keep my efforts secret. Please do not speak of this to anyone, $N.' WHERE `word_id` = 147222; -- locales_quest - offerrewardtext : entry=9494
UPDATE `trans_words` SET `content_loc6`='Well done! I can\'t wait to put this to the test.' WHERE `word_id` = 150478; -- locales_quest - offerrewardtext : entry=10293
UPDATE `trans_words` SET `content_loc6`='Well done! I could almost feel the tremors of the explosions from here!$B$BNow, we have one more bombing mission for you...' WHERE `word_id` = 150090; -- locales_quest - offerrewardtext : entry=10208
UPDATE `trans_words` SET `content_loc6`='Well done! I\'ll put these supplies to good use! Thanks, $N!' WHERE `word_id` = 140769; -- locales_quest - offerrewardtext : entry=5892
UPDATE `trans_words` SET `content_loc6`='Well done! I\'m still in the process of getting the hawks to trust me, but I know enough about them to train this one for your needs.' WHERE `word_id` = 153196; -- locales_quest - offerrewardtext : entry=10987
UPDATE `trans_words` SET `content_loc6`='Well done! My men are working hard on getting our flying machines and steam tanks back into the fight.' WHERE `word_id` = 179815; -- locales_quest - offerrewardtext : entry=26078
UPDATE `trans_words` SET `content_loc6`='Well done! Now, more than ever, we are prepared to face the naga!' WHERE `word_id` = 179382; -- locales_quest - offerrewardtext : entry=25947
UPDATE `trans_words` SET `content_loc6`='Well done! The chaos we sow back here will be felt up and down the chain of command.' WHERE `word_id` = 176838; -- locales_quest - offerrewardtext : entry=25233
UPDATE `trans_words` SET `content_loc6`='Well done! The information you recovered will be invaluable to our cause.' WHERE `word_id` = 141975; -- locales_quest - offerrewardtext : entry=7302
UPDATE `trans_words` SET `content_loc6`='Well done! The Ironforge Guard appreciates this, $N. You\'ve done our city a fine favor. But something new has come up...' WHERE `word_id` = 179029; -- locales_quest - offerrewardtext : entry=25865
UPDATE `trans_words` SET `content_loc6`='Well done! The long term effects of this attack will be far worse than slaying a few orcs could ever be.' WHERE `word_id` = 191192; -- locales_quest - offerrewardtext : entry=29593
UPDATE `trans_words` SET `content_loc6`='Well done! The Tome of the Cabal and three pristine--slightly scratched, I suppose--rods of channeling.$B$BThis will be interesting. It\'s been some time since a young $C took on a felhunter to make his own.' WHERE `word_id` = 136313; -- locales_quest - offerrewardtext : entry=1804
UPDATE `trans_words` SET `content_loc6`='Well done! These all look strong. I think they\'ll do quite nicely. How did those orcs mind you taking their shins, $N? Complain much?$B$BThat was another joke. You\'re not laughing.$B$BI\'m not very funny these days, am I...' WHERE `word_id` = 136383; -- locales_quest - offerrewardtext : entry=1846
UPDATE `trans_words` SET `content_loc6`='Well done! These should hold a curse long enough for our purposes....' WHERE `word_id` = 172524; -- locales_quest - offerrewardtext : entry=13797
UPDATE `trans_words` SET `content_loc6`='Well done! These tumors are the symptom of the timberling\'s disease. They are filled with a poison that we must cleanse from our new land.$B$BI will dispose of these tumors. Thank you, $N.' WHERE `word_id` = 134036; -- locales_quest - offerrewardtext : entry=923
UPDATE `trans_words` SET `content_loc6`='Well done! This is just what we need, $N.' WHERE `word_id` = 182716; -- locales_quest - offerrewardtext : entry=26901
UPDATE `trans_words` SET `content_loc6`='Well done! When it comes to iron, always pick quantity over quality.' WHERE `word_id` = 173492; -- locales_quest - offerrewardtext : entry=14197
UPDATE `trans_words` SET `content_loc6`='Well done! Wilkes was hanged after we discovered that he was ingesting mass quantities of mushrooms and other highly toxic substances. After he died he awoke, reborn as an undead geist. Many of my guards fell to Wilkes before he ran off into hiding. Fearing something like this might happen again, we incinerated the Clerk until only bones were left!$B$BWhy would they willingly ingest toxic substances? I have no idea! This is a farm, $G sir:miss;, nothing more!' WHERE `word_id` = 187298; -- locales_quest - offerrewardtext : entry=28197
UPDATE `trans_words` SET `content_loc6`='Well done! With luck and diligence, we will find whatever link exists between the troggs and these ancient artifacts.$B$BYou have done a great deed for the Prospectors, $N. Your efforts will not be forgotten. And neither will Agmond.' WHERE `word_id` = 133156; -- locales_quest - offerrewardtext : entry=704
UPDATE `trans_words` SET `content_loc6`='Well done! Without their leaders, the Scourge will pose no significant threat.' WHERE `word_id` = 183193; -- locales_quest - offerrewardtext : entry=27024
UPDATE `trans_words` SET `content_loc6`='Well done!$B$B<Mack laughs hysterically for a moment.>$B$BAww, come on now. That was funny...<hic>.' WHERE `word_id` = 158208; -- locales_quest - offerrewardtext : entry=12029
UPDATE `trans_words` SET `content_loc6`='Well done!$B$B<Mack laughs hysterically for a moment.>$B$BAww, come on now. That was funny...hic.' WHERE `word_id` = 158253; -- locales_quest - offerrewardtext : entry=12038
UPDATE `trans_words` SET `content_loc6`='Well done!$B$B<The smith examines the blade closely.>$B$BI\'d say it\'s a fair improvement over the crude Legion weapons you started with. Not only will this blade cut down Illidari demons, but it will work against the Legion\'s minions as well.' WHERE `word_id` = 151981; -- locales_quest - offerrewardtext : entry=10679
UPDATE `trans_words` SET `content_loc6`='Well done!$B$BThey\'ve been attempting to start rituals of this sort all over the Molten Front, but we\'ve been able to thwart them every time so far. Hopefully you will be present in the future to help us keep up that trend.' WHERE `word_id` = 190449; -- locales_quest - offerrewardtext : entry=29210
UPDATE `trans_words` SET `content_loc6`='Well done, $C! Thanks to you, my men will be ready to confront the enemy when they attack.' WHERE `word_id` = 177808; -- locales_quest - offerrewardtext : entry=25545
UPDATE `trans_words` SET `content_loc6`='Well done, $C! You must have a great deal of experience fighting the undead. And, if you didn\'t before, you have some now.$B$BYou\'ll need it if you\'re going to survive in Northrend.' WHERE `word_id` = 156943; -- locales_quest - offerrewardtext : entry=11594
UPDATE `trans_words` SET `content_loc6`='Well done, $C, That\'s one less forge camp we\'ll need to worry about. It\'ll take the Legion a while to get that facility back up and running. You\'ve just bought us the one thing we\'d run out of' WHERE `word_id` = 150839; -- locales_quest - offerrewardtext : entry=10390
UPDATE `trans_words` SET `content_loc6`='Well done, $C. A fitting end to a textbook assault.' WHERE `word_id` = 165539; -- locales_quest - offerrewardtext : entry=25
UPDATE `trans_words` SET `content_loc6`='Well done, $C. And not a moment too soon. I\'ve received word that a counter-attack is imminent.$B$BYou should be on your way. The next post is to the northeast, near the coliseum. Captain Grondel should be waiting for you there.' WHERE `word_id` = 159981; -- locales_quest - offerrewardtext : entry=12509
UPDATE `trans_words` SET `content_loc6`='Well done, $C. I call for more of their blood. Let all the fish men perish.' WHERE `word_id` = 135570; -- locales_quest - offerrewardtext : entry=1428
UPDATE `trans_words` SET `content_loc6`='Well done, $C. I hope you don\'t forget about us here in the Hatchery.' WHERE `word_id` = 190074; -- locales_quest - offerrewardtext : entry=29177
UPDATE `trans_words` SET `content_loc6`='Well done, $C. I see you\'re not only skilled but cunning. To defeat such creatures is a testament to your loyalty to the tenants of the Earthmother, even if you do not recognize her as your own deity. She is in all things on this planet and beyond. Her eyes, one dark and one light, travel across the skies always gazing down upon us.$B$BPlease, take this small amount of coin in appreciation for what you have done.' WHERE `word_id` = 334675; -- locales_quest - offerrewardtext : entry=5536
UPDATE `trans_words` SET `content_loc6`='Well done, $C. I\'ll have to make more use of your skills in the future.' WHERE `word_id` = 189950; -- locales_quest - offerrewardtext : entry=29143
UPDATE `trans_words` SET `content_loc6`='Well done, $C. One last request - it would probably be best if none of my laborers knew this assassin ever existed.' WHERE `word_id` = 172637; -- locales_quest - offerrewardtext : entry=13871
UPDATE `trans_words` SET `content_loc6`='Well done, $C. Shadowmourne is nearly within reach.' WHERE `word_id` = 275272; -- locales_quest - offerrewardtext : entry=24756
UPDATE `trans_words` SET `content_loc6`='Well done, $C. Thank you for your help. The spirits are at peace, and the threats that their desire for the arcane draw have been lessened.$B$BI am sure that both sides of the conflict that ravishes Azeroth will see you are above such things.' WHERE `word_id` = 140458; -- locales_quest - offerrewardtext : entry=5535
UPDATE `trans_words` SET `content_loc6`='Well done, $C. The completion of our work draws near.' WHERE `word_id` = 275271; -- locales_quest - offerrewardtext : entry=24749
UPDATE `trans_words` SET `content_loc6`='Well done, $C. The Scourge military command out of Naxxramas has been thrown into disarray by Razuvious\'s demise.' WHERE `word_id` = 165085; -- locales_quest - offerrewardtext : entry=24582
UPDATE `trans_words` SET `content_loc6`='Well done, $C. They\'re in complete disarray. Just look at them!' WHERE `word_id` = 172607; -- locales_quest - offerrewardtext : entry=13865
UPDATE `trans_words` SET `content_loc6`='Well done, $C. This should help get us by until our shipment arrives.' WHERE `word_id` = 172338; -- locales_quest - offerrewardtext : entry=13615
UPDATE `trans_words` SET `content_loc6`='Well done, $C. This should prove a good staging ground for pushing further into enemy territory.$B$BI sense Druids of the Flame nearby. Perhaps we shall have some sport of them as well.' WHERE `word_id` = 190436; -- locales_quest - offerrewardtext : entry=29205
UPDATE `trans_words` SET `content_loc6`='Well done, $C. Though the extensiveness of the Zukk\'ash presence will not be thwarted with anything less than a full-scale invasion, you have bought us some time so we can devise a proper strategy.$B$BI will be sure to have these carapaces analyzed; we\'ll need to learn everything about them before we deal with what has unfortunately turned into a much larger issue than we had anticipated.' WHERE `word_id` = 142414; -- locales_quest - offerrewardtext : entry=25367
UPDATE `trans_words` SET `content_loc6`='Well done, $C. You have studied the text I asked you to, and now you have passed another of our tests.' WHERE `word_id` = 141569; -- locales_quest - offerrewardtext : entry=6627
UPDATE `trans_words` SET `content_loc6`='Well done, $C. Zul\'jin himself could not have done a better job!' WHERE `word_id` = 142565; -- locales_quest - offerrewardtext : entry=7844
UPDATE `trans_words` SET `content_loc6`='Well done, $C.$B$BPerhaps with some investigation, we can discover how they are bringing such a creature into existence.' WHERE `word_id` = 163345; -- locales_quest - offerrewardtext : entry=13278
UPDATE `trans_words` SET `content_loc6`='Well done, $C.$B$BThat slippery beast got away! I\'ll be making a few upgrades. It won\'t be so lucky next time.' WHERE `word_id` = 180304; -- locales_quest - offerrewardtext : entry=26221
UPDATE `trans_words` SET `content_loc6`='Well done, $N - the minions of Hakkar have surely felt the retribution of the Zandalar today, thanks to you. Your actions have given you a permanent place of reverence within the tribe. With our blessing, please take this confessor\'s mantle as your own!' WHERE `word_id` = 142849; -- locales_quest - offerrewardtext : entry=8071
UPDATE `trans_words` SET `content_loc6`='Well done, $N!  I will make sure that your deeds are known amongst my kind.$B$BRemember to bring any of the feathers you find to me. I will continue to accept them as long as necessary.' WHERE `word_id` = 143594; -- locales_quest - offerrewardtext : entry=28395
UPDATE `trans_words` SET `content_loc6`='Well done, $N!  It never ceases to amaze me how persuasive a volley of arcane missiles can be.' WHERE `word_id` = 143107; -- locales_quest - offerrewardtext : entry=8251
UPDATE `trans_words` SET `content_loc6`='Well done, $N!  It\'ll be a long time before they can grow another clutch of dragons.$B$B...At least, I hope it will!' WHERE `word_id` = 140441; -- locales_quest - offerrewardtext : entry=27145
UPDATE `trans_words` SET `content_loc6`='Well done, $N!  Stormpikes have no hope in this foolish invasion.$B$BHah!  So far, all they\'ve done is get their general killed!' WHERE `word_id` = 141893; -- locales_quest - offerrewardtext : entry=7142
UPDATE `trans_words` SET `content_loc6`='Well done, $N!  With the Horde\'s nose bloodied after the death of their general, we\'ll have the breathing room we need to explore!' WHERE `word_id` = 141887; -- locales_quest - offerrewardtext : entry=7141
UPDATE `trans_words` SET `content_loc6`='Well done, $N!  You demonstrated uncanny courage on the battlefield. Our war against the Troggs will be won with efforts such as yours. You have served King Magni well.' WHERE `word_id` = 180062; -- locales_quest - offerrewardtext : entry=26146
UPDATE `trans_words` SET `content_loc6`='Well done, $N! Aiding that downed gryphon-rider has given us our first contact with the old Wildhammer clans. We\'ll be able to find out who\'s still left, and in what numbers. Our campaign is looking up!' WHERE `word_id` = 184783; -- locales_quest - offerrewardtext : entry=27516
UPDATE `trans_words` SET `content_loc6`='Well done, $N! As soon as Ithania has had time to rest and my preparations are complete, we\'ll depart this sodden wasteland and head back to civilization.' WHERE `word_id` = 146993; -- locales_quest - offerrewardtext : entry=9437
UPDATE `trans_words` SET `content_loc6`='Well done, $N! Children, take a good look at this $C. Grow strong and brave like him and one day you too will fight for the Horde.' WHERE `word_id` = 154171; -- locales_quest - offerrewardtext : entry=11361
UPDATE `trans_words` SET `content_loc6`='Well done, $N! Dis naga has set us back quite some time, but nothin\' will be holdin\' us down foreva. It\'s good ta be havin\' ya wit us, $N. Ya have a fierce heart within ya.' WHERE `word_id` = 175102; -- locales_quest - offerrewardtext : entry=24625
UPDATE `trans_words` SET `content_loc6`='Well done, $N! I wish I could say that the Legion would give up after a setback like this, but you and I know better than that. The Deathforge that you learned about during your reconnaissance mission will be our next target.' WHERE `word_id` = 151491; -- locales_quest - offerrewardtext : entry=10564
UPDATE `trans_words` SET `content_loc6`='Well done, $N! I\'ll admit, when I caught my first glimpse of that beast, I had doubts about our ability to defend the docks, but you held together the defenders brilliantly!' WHERE `word_id` = 154009; -- locales_quest - offerrewardtext : entry=11198
UPDATE `trans_words` SET `content_loc6`='Well done, $N! Know that without your aid, things would be far different here. Not only would I be long dead, but the naga and their allies would have free reign over the sea. The king himself shall hear of your heroism in this realm.' WHERE `word_id` = 189359; -- locales_quest - offerrewardtext : entry=28836
UPDATE `trans_words` SET `content_loc6`='Well done, $N! Let the Vilebranch flee in terror from the sight of you!' WHERE `word_id` = 180619; -- locales_quest - offerrewardtext : entry=26306
UPDATE `trans_words` SET `content_loc6`='Well done, $N! Let us finish this!' WHERE `word_id` = 174220; -- locales_quest - offerrewardtext : entry=14393
UPDATE `trans_words` SET `content_loc6`='Well done, $N! Maybe seein\' yer catch will inspire more dwarves to take up fishin\'!' WHERE `word_id` = 190271; -- locales_quest - offerrewardtext : entry=29343
UPDATE `trans_words` SET `content_loc6`='Well done, $N! On behalf of The People\'s Militia I salute you for your efforts. Soon we will return this land to the people with help from brave souls like you.' WHERE `word_id` = 131053; -- locales_quest - offerrewardtext : entry=214
UPDATE `trans_words` SET `content_loc6`='Well done, $N! These tails will make great prizes for our visitors, and that means happy visitors... who talk to their friends and bring more visitors!$B$BHere are your tickets! Have a good time at the Darkmoon Faire... and don\'t forget to tell your friends about us!' WHERE `word_id` = 142649; -- locales_quest - offerrewardtext : entry=7901
UPDATE `trans_words` SET `content_loc6`='Well done, $N! With each graveyard under our control, the spirits of our enemies will wander more lost and aimless.' WHERE `word_id` = 141853; -- locales_quest - offerrewardtext : entry=7082
UPDATE `trans_words` SET `content_loc6`='Well done, $N! With the befouled elemental numbers thinned and Tideress put back into the lake, we should be able to launch a counter-offensive on Silverwind Refuge. Just as soon as we\'re done with defending the spire....' WHERE `word_id` = 181188; -- locales_quest - offerrewardtext : entry=26470
UPDATE `trans_words` SET `content_loc6`='Well done, $N! Without their leader, the Horde is weak and disorganized. I will order our men forward.' WHERE `word_id` = 159491; -- locales_quest - offerrewardtext : entry=12314
UPDATE `trans_words` SET `content_loc6`='Well done, $N! You demonstrated uncanny courage on the battlefield. Our war against the Troggs will be won with efforts such as yours. You have served King Magni well.$B$BIf you fancy yourself well-suited for this kind of adventuring, I suggest you speak with Mountaineer Gravelgaw. The Captain has him assigned to a rough patrol that I am sure he can use some help with. Check with Gravelgaw just inside the tower.' WHERE `word_id` = 131097; -- locales_quest - offerrewardtext : entry=224
UPDATE `trans_words` SET `content_loc6`='Well done, $N! You have done the Horde proud in my absence. The Dragonmaw have joined us, with Zaela as their new leader. Blood was shed, but casualties were low, and we have their complete loyalty. Our Horde grows stronger!' WHERE `word_id` = 181737; -- locales_quest - offerrewardtext : entry=26622
UPDATE `trans_words` SET `content_loc6`='Well done, $N! You have proven yourself worthy of full membership in the Blood Knights and elevation to adept status. However, there are a few more tasks that remain before your induction can take place.' WHERE `word_id` = 147947; -- locales_quest - offerrewardtext : entry=9686
UPDATE `trans_words` SET `content_loc6`='Well done, $N! You\'re proving very valuable to securing this area for the Alliance.' WHERE `word_id` = 177444; -- locales_quest - offerrewardtext : entry=25431
UPDATE `trans_words` SET `content_loc6`='Well done, $N!$B$BHunt them down and make them pay!' WHERE `word_id` = 163149; -- locales_quest - offerrewardtext : entry=13233
UPDATE `trans_words` SET `content_loc6`='Well done, $N, and just in time too. The Admiral just started sending more men out. Strong sword arms to be sure, but I doubt they possess quite the same awareness and finesse that you do. We were lucky to have you here when things went south. I suspect you will have a large role to play in things to come as well.' WHERE `word_id` = 177916; -- locales_quest - offerrewardtext : entry=25580
UPDATE `trans_words` SET `content_loc6`='Well done, $N, and just in time too. The Captain just started sending more men out. Strong axe arms, but I doubt they possess quite the same awareness and finesse that you do.$B$BWe were lucky to have you here when things went south. I suspect you will have a large role to play in things to come as well.' WHERE `word_id` = 179404; -- locales_quest - offerrewardtext : entry=25953
UPDATE `trans_words` SET `content_loc6`='Well done, $N, this should give us the information we need to have the culprit discovered. How would you feel about taking out the mage who fills our pools with corrupt elementals? The corruption of Ashenvale and beyond makes my stomach turn on its own--to have a wizard actively attempting to destroy it further sickens me to no end. We shall make him pay.' WHERE `word_id` = 134394; -- locales_quest - offerrewardtext : entry=1016
UPDATE `trans_words` SET `content_loc6`='Well done, $N, well done!$B$BHere, I cooked up some tenderloins while you were gone. Maybe you\'d like the recipe, too?' WHERE `word_id` = 147074; -- locales_quest - offerrewardtext : entry=9454
UPDATE `trans_words` SET `content_loc6`='Well done, $N, well done. We\'ll make an orc of you yet! Time to move on. We\'d better hurry.' WHERE `word_id` = 173624; -- locales_quest - offerrewardtext : entry=14238
UPDATE `trans_words` SET `content_loc6`='Well done, $N. 10 stalks of akiris reed wasn\'t too much a challenge after all, huh? No offense to the naga, but hey, everybody\'s gotta make a living.' WHERE `word_id` = 132793; -- locales_quest - offerrewardtext : entry=617
UPDATE `trans_words` SET `content_loc6`='Well done, $N. All who would oppose us would do well to heed the warning you delivered. Our foes should know that they are in peril, from the lowest foot soldier to the mightiest $Ghero:heroine;.$B$BYou have earned your place in the ranks of the masters. Your dedication and prowess serve as an example for all Blood Knights. As a symbol of your new status, I grant you the ability to summon and command the thalassian charger.' WHERE `word_id` = 148169; -- locales_quest - offerrewardtext : entry=9737
UPDATE `trans_words` SET `content_loc6`='Well done, $N. As fate foretold, you will be the one to carry us through these hard times. Now the long journey begins.' WHERE `word_id` = 159351; -- locales_quest - offerrewardtext : entry=12282
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Children, take a good look at this $C. Let $Ghim:her; be a fine example for you to follow.' WHERE `word_id` = 154362; -- locales_quest - offerrewardtext : entry=11450
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Children, take a good look at this $C. Let her be a strong example to you.' WHERE `word_id` = 154358; -- locales_quest - offerrewardtext : entry=11449
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Come. Rest. Let us eat and talk of days long past!' WHERE `word_id` = 140477; -- locales_quest - offerrewardtext : entry=5542
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Disabling those portals will stem the flow of Legion reinforcements for a long time!$B$BBut we can\'t rest yet...' WHERE `word_id` = 149829; -- locales_quest - offerrewardtext : entry=10144
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Disrupting the Shadow Council\'s summoning puts us one step closer to shutting down the Deathforge.' WHERE `word_id` = 151597; -- locales_quest - offerrewardtext : entry=10585
UPDATE `trans_words` SET `content_loc6`='Well done, $N. He still remembered the day I drove this spear into him?  That was so long ago...' WHERE `word_id` = 151932; -- locales_quest - offerrewardtext : entry=10669
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Here\'s to hoping the trap performs as well when we try it on Abyssion.' WHERE `word_id` = 182289; -- locales_quest - offerrewardtext : entry=26771
UPDATE `trans_words` SET `content_loc6`='Well done, $N. I am pleased. You have done a great thing here today.$B$BU\'cha was one of the greatest apes I had ever seen. The fact that you have bested his strength speaks well of your own strength... and courage.$B$BIt is only fitting that I give you this as a sign of respect. You have earned it.' WHERE `word_id` = 139054; -- locales_quest - offerrewardtext : entry=4301
UPDATE `trans_words` SET `content_loc6`='Well done, $N. I had my doubts if you studied the text I asked you to get, but you\'ve answered correctly.' WHERE `word_id` = 141574; -- locales_quest - offerrewardtext : entry=6628
UPDATE `trans_words` SET `content_loc6`='Well done, $N. I have discovered a way in which we can free the enslaved drakes that are being held at Dragonmaw Fortress.' WHERE `word_id` = 152541; -- locales_quest - offerrewardtext : entry=10836
UPDATE `trans_words` SET `content_loc6`='Well done, $N. I hope you are a good actor. The next step in my grand plan is going to require some theatrics!' WHERE `word_id` = 151552; -- locales_quest - offerrewardtext : entry=10576
UPDATE `trans_words` SET `content_loc6`='Well done, $N. I hope you didn\'t have too much trouble with those murlocs.$B$BIt\'s strange seeing them so far from the sea. I wonder if they\'re here because they\'re running from something...' WHERE `word_id` = 130764; -- locales_quest - offerrewardtext : entry=150
UPDATE `trans_words` SET `content_loc6`='Well done, $N. I see you will have no trouble mastering our more advanced arts as you gain experience. Return to me when you need further training.' WHERE `word_id` = 183205; -- locales_quest - offerrewardtext : entry=27027
UPDATE `trans_words` SET `content_loc6`='Well done, $N. I think we managed to catch them off guard. Their agents probably thought themselves safe within the city walls.$B$BGiven time, the agitators will return and spread their lies again. We\'ll use the time you\'ve bought to shut out the deserters further.' WHERE `word_id` = 153732; -- locales_quest - offerrewardtext : entry=11126
UPDATE `trans_words` SET `content_loc6`='Well done, $N. I think you and I have managed to outshine a whole team of prospectors. When I get back to Ironforge, I\'m sure I\'ll get my choice of assignments.' WHERE `word_id` = 148861; -- locales_quest - offerrewardtext : entry=9901
UPDATE `trans_words` SET `content_loc6`='Well done, $N. I was worried that the marking device might malfunction, but it seems like everything went according to plan.' WHERE `word_id` = 147758; -- locales_quest - offerrewardtext : entry=9629
UPDATE `trans_words` SET `content_loc6`='Well done, $N. I will be able to reform the Scepter of the Shifting Sands soon.' WHERE `word_id` = 144182; -- locales_quest - offerrewardtext : entry=8729
UPDATE `trans_words` SET `content_loc6`='Well done, $N. I will deliver these to Tyrosus when we return.' WHERE `word_id` = 185110; -- locales_quest - offerrewardtext : entry=27614
UPDATE `trans_words` SET `content_loc6`='Well done, $N. I will notify Illusia that a hero has heard her call for help and risen to meet the challenge! Here is your reward.' WHERE `word_id` = 159686; -- locales_quest - offerrewardtext : entry=12438
UPDATE `trans_words` SET `content_loc6`='Well done, $N. I will use the power of these charms to augment the magic that has already been gathered. When enough power has been amassed, I shall focus the collected energies and call upon the Forest Lord to aid our cause.' WHERE `word_id` = 142032; -- locales_quest - offerrewardtext : entry=7386
UPDATE `trans_words` SET `content_loc6`='Well done, $N. I will use the power of this charm to augment the magic that has already been gathered. When enough power has been amassed, I shall focus the collected energies and call upon the Forest Lord to aid our cause.' WHERE `word_id` = 141685; -- locales_quest - offerrewardtext : entry=6881
UPDATE `trans_words` SET `content_loc6`='Well done, $N. I\'ll prepare the incense and then we\'ll see what exactly this royal dwarf was doing so far from home.' WHERE `word_id` = 158328; -- locales_quest - offerrewardtext : entry=12054
UPDATE `trans_words` SET `content_loc6`='Well done, $N. I\'ll take these to the Argent Crusade\'s keepers right away. They\'ll have no choice but to acknowledge that we\'re doing far more than the Sunreavers to keep the coliseum running smoothly.' WHERE `word_id` = 164657; -- locales_quest - offerrewardtext : entry=14076
UPDATE `trans_words` SET `content_loc6`='Well done, $N. I\'m amazed you dealt with them so quickly. Thank you.$B$BI have yet to meet anyone that approves of the Scarlet Crusade and their methods. I believe in the Light as much as anyone else in Stormwind, but they\'ve demonstrated that they only want to kill anyone not loyal to their movement... even innocents.' WHERE `word_id` = 140850; -- locales_quest - offerrewardtext : entry=6004
UPDATE `trans_words` SET `content_loc6`='Well done, $N. It is good to know Vorlus is defeated and our moonwell cleansed, and it is good to see young warriors eager to keep our beloved Teldrassil free of corruption.$B$BNow let us begin your training...' WHERE `word_id` = 136099; -- locales_quest - offerrewardtext : entry=1683
UPDATE `trans_words` SET `content_loc6`='Well done, $N. It might be awkward at first, but you will get used to advocating our beliefs in public. It is one way of remembering our obligation to bring the truth to others. We must save as many as we can.' WHERE `word_id` = 171847; -- locales_quest - offerrewardtext : entry=25380
UPDATE `trans_words` SET `content_loc6`='Well done, $N. It might be awkward at first, but you will grow accustomed to advocating our beliefs in public. It is one way of remembering our obligation to bring the truth to others. We must offer hope to everyone, for we all face the end of this world together.' WHERE `word_id` = 171860; -- locales_quest - offerrewardtext : entry=25415
UPDATE `trans_words` SET `content_loc6`='Well done, $N. It should be a long time before the harbor receives any reinforcements.' WHERE `word_id` = 154546; -- locales_quest - offerrewardtext : entry=11542
UPDATE `trans_words` SET `content_loc6`='Well done, $N. It\'s going to be much easier to get reinforcements from Blood Watch, or launch strikes against the Sunhawks without having to worry about what the tanglers are up to.' WHERE `word_id` = 149523; -- locales_quest - offerrewardtext : entry=10066
UPDATE `trans_words` SET `content_loc6`='Well done, $N. It\'s good to know I can count on you.' WHERE `word_id` = 190672; -- locales_quest - offerrewardtext : entry=29296
UPDATE `trans_words` SET `content_loc6`='Well done, $N. It\'s important not to overlook any opportunity to deny the Sunhawks sustenance.' WHERE `word_id` = 149528; -- locales_quest - offerrewardtext : entry=10067
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Let me see what I can learn from this glyph, so we can plan our next move.' WHERE `word_id` = 147508; -- locales_quest - offerrewardtext : entry=9567
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Let them see the desecrated corpses of their fallen comrades and think twice about striking at us!$B$BIf you come back tomorrow, I will make certain to have more banners prepared for you.' WHERE `word_id` = 162150; -- locales_quest - offerrewardtext : entry=12995
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Mounted combat is often hard to pick up for those accustomed to fighting on foot. I have no doubt that you\'ll continue to improve with practice, which is more than I can say for some warriors.' WHERE `word_id` = 163976; -- locales_quest - offerrewardtext : entry=13625
UPDATE `trans_words` SET `content_loc6`='Well done, $N. My men can take it from here. Let\'s focus on getting the other survivors out now.' WHERE `word_id` = 185343; -- locales_quest - offerrewardtext : entry=27670
UPDATE `trans_words` SET `content_loc6`='Well done, $N. My scout witnessed your valiant acts. You are proving yourself quite well so far.' WHERE `word_id` = 130191; -- locales_quest - offerrewardtext : entry=12
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Now we must focus our efforts on discovering more about this cult and freeing the people they\'ve conned into joining.' WHERE `word_id` = 171812; -- locales_quest - offerrewardtext : entry=25254
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Now, let\'s see what they\'ve got in this elixir of theirs...' WHERE `word_id` = 182150; -- locales_quest - offerrewardtext : entry=26732
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Now, we must follow through and press the advantage.' WHERE `word_id` = 159067; -- locales_quest - offerrewardtext : entry=12223
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Our control over the logging camp might not be complete yet, but thanks to you we\'re one step closer to smashing these Alliance worms.' WHERE `word_id` = 158829; -- locales_quest - offerrewardtext : entry=12170
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Respect your prey, and the danger it carries, but never fear it. In fear, you become prey yourself.' WHERE `word_id` = 174407; -- locales_quest - offerrewardtext : entry=14456
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Return tomorrow for another mission into the depths of the mine.' WHERE `word_id` = 153571; -- locales_quest - offerrewardtext : entry=11076
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Several of the looters have already crawled back, requesting clemency. I\'ll let them stew for a bit in the Northwatch brig. Thank you for helping maintain discipline in the ranks. It is important for our army to be perceived as soldiers, not common bandits.' WHERE `word_id` = 176175; -- locales_quest - offerrewardtext : entry=25057
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Such fine specimens too! And here is your delicious delicacy known as Redridge Goulash!' WHERE `word_id` = 130513; -- locales_quest - offerrewardtext : entry=92
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Thanks to you, Gnomeregan is one step closer to its day of liberation!' WHERE `word_id` = 136588; -- locales_quest - offerrewardtext : entry=2040
UPDATE `trans_words` SET `content_loc6`='Well done, $N. That should teach those beasts who\'s boss!' WHERE `word_id` = 146807; -- locales_quest - offerrewardtext : entry=9385
UPDATE `trans_words` SET `content_loc6`='Well done, $N. That\'ll teach them not to mess with us in our home!' WHERE `word_id` = 189473; -- locales_quest - offerrewardtext : entry=28868
UPDATE `trans_words` SET `content_loc6`='Well done, $N. The Argus Wake does not sound like a group we want running the Syndicate.$B$BAnd although you may have destroyed their leader here, Nagaz was merely the servant of another. The Argus Wake might run deep through the continent of Azeroth.$B$BOr even the world.' WHERE `word_id` = 132439; -- locales_quest - offerrewardtext : entry=537
UPDATE `trans_words` SET `content_loc6`='Well done, $N. The centaur are low, brutish creatures, but a few have the wit and vision to lead. And those are the most dangerous.$B$BBarak was such a centaur. It is good that he is dead.' WHERE `word_id` = 133726; -- locales_quest - offerrewardtext : entry=850
UPDATE `trans_words` SET `content_loc6`='Well done, $N. The contents of this chest are sure to advance Gnomeregan\'s knowledge base significantly. I believe you\'ve earned a reward.' WHERE `word_id` = 176211; -- locales_quest - offerrewardtext : entry=25065
UPDATE `trans_words` SET `content_loc6`='Well done, $N. The expedition has already doomed itself. There is no need for it to endanger the rest of the world as well.' WHERE `word_id` = 185664; -- locales_quest - offerrewardtext : entry=27761
UPDATE `trans_words` SET `content_loc6`='Well done, $N. The loss of the iron dwarf commander should be enough to break the remaining defenders. The Earthen should be able to deal with the survivors.' WHERE `word_id` = 158054; -- locales_quest - offerrewardtext : entry=11985
UPDATE `trans_words` SET `content_loc6`='Well done, $N. The mountaineers will be glad to hear that half-rations are at an end. They\'ll need all their strength to continue fighting the Frostmanes.' WHERE `word_id` = 178246; -- locales_quest - offerrewardtext : entry=25668
UPDATE `trans_words` SET `content_loc6`='Well done, $N. The naga seek the destruction and subjugation of all surface-dwellers -- do not hesitate to drive them out of our lands and back into the shadowy ocean depths where they belong.' WHERE `word_id` = 172748; -- locales_quest - offerrewardtext : entry=13898
UPDATE `trans_words` SET `content_loc6`='Well done, $N. The Paragons of Power from Zul\'Gurub should act as a reminder of wasted power. The Gurubashi were strong at their height, but they allowed themselves to be consumed by it. A demoniac knows that the game they play is much similar, but the power unleashed is often the very thing needed for survival.$B$BUse this mantle as an honored ally of the Zandalar... it will serve you well.' WHERE `word_id` = 142873; -- locales_quest - offerrewardtext : entry=8076
UPDATE `trans_words` SET `content_loc6`='Well done, $N. The powers granted by the Light will never come \'naturally\' to the Blood Knights, but we are not so naive as to be thwarted by the will of a single being, however powerful.$B$BOur mastery of the arcane has unlocked this path and these powers for you, initiate. Few have the stomach or the aptitude to wield them, but I know you will not disappoint the order.' WHERE `word_id` = 147937; -- locales_quest - offerrewardtext : entry=9684
UPDATE `trans_words` SET `content_loc6`='Well done, $N. The praise the Banshee Queen lavishes upon you is apparently not unfounded.' WHERE `word_id` = 184604; -- locales_quest - offerrewardtext : entry=27475
UPDATE `trans_words` SET `content_loc6`='Well done, $N. The presence of these ogres so close to our outpost does have me worried, though. It seems like they\'ve been filtering down from the northern mountains for some time.' WHERE `word_id` = 148520; -- locales_quest - offerrewardtext : entry=9820
UPDATE `trans_words` SET `content_loc6`='Well done, $N. The ravager population will bear watching for some time to come, but we\'ve made a good start.' WHERE `word_id` = 147782; -- locales_quest - offerrewardtext : entry=9634
UPDATE `trans_words` SET `content_loc6`='Well done, $N. The scout never had a chance.' WHERE `word_id` = 175062; -- locales_quest - offerrewardtext : entry=24616
UPDATE `trans_words` SET `content_loc6`='Well done, $N. The twilight marauders were inflicting an unacceptable number of casualties on our patrols. Here\'s your next assignment.' WHERE `word_id` = 144222; -- locales_quest - offerrewardtext : entry=8740
UPDATE `trans_words` SET `content_loc6`='Well done, $N. The Twilight\'s Hammer is an evil that we must fight anywhere we find them.' WHERE `word_id` = 180419; -- locales_quest - offerrewardtext : entry=26251
UPDATE `trans_words` SET `content_loc6`='Well done, $N. The Wickerman Festival will continue to be trouble so long as the abominable Forsaken are allowed to haunt the remnants of Lordaeron. It is NOT a fitting tribute to that once proud kingdom, let me tell you...$B$BHere\'s some coin for any repairs you might need, and in the spirit of the season I\'ve also included some treats you might like. Happy Hallow\'s End - let\'s keep it as safe as possible here in Southshore.' WHERE `word_id` = 136065; -- locales_quest - offerrewardtext : entry=1658
UPDATE `trans_words` SET `content_loc6`='Well done, $N. The Wildhammers will be heartened to know that they don\'t stand against the Witherbark trolls alone. Showing them that the Alliance can prove a reliable partner in protecting the gryphons should lend more weight to our proposition.' WHERE `word_id` = 147134; -- locales_quest - offerrewardtext : entry=9470
UPDATE `trans_words` SET `content_loc6`='Well done, $N. These are the crystals I spoke of. Intriguings...$B$BI can feel the power within them. The spirits of water cling to them. Whatever the naga were going to use these for... well, let us be glad they are no longer in their hands.' WHERE `word_id` = 141433; -- locales_quest - offerrewardtext : entry=6563
UPDATE `trans_words` SET `content_loc6`='Well done, $N. These blood samples will prove helpful in my research and in the Expedition\'s wider studies of Outland\'s creatures.$B$BIf I could isolate what causes these creatures to change continuously, perhaps I could concoct an antidote.' WHERE `word_id` = 146766; -- locales_quest - offerrewardtext : entry=9372
UPDATE `trans_words` SET `content_loc6`='Well done, $N. These remedies will keep more of us alive and in fighting shape. And the extracts will help us create our own remedies.' WHERE `word_id` = 131007; -- locales_quest - offerrewardtext : entry=204
UPDATE `trans_words` SET `content_loc6`='Well done, $N. This was a difficult test of your abilities, but you have passed with flying colors. To become a soldier of the Hand of Argus, you must pass many such tests.' WHERE `word_id` = 147856; -- locales_quest - offerrewardtext : entry=9663
UPDATE `trans_words` SET `content_loc6`='Well done, $N. This will do nicely.$B$BThe only thing left for you to do is to decide on which demon you want me to imprison within the cleansed orb. Do that, and you will have a magnificent new item to wield.' WHERE `word_id` = 139632; -- locales_quest - offerrewardtext : entry=4976
UPDATE `trans_words` SET `content_loc6`='Well done, $N. This will help us turn the tide against the enemy.' WHERE `word_id` = 177110; -- locales_quest - offerrewardtext : entry=25323
UPDATE `trans_words` SET `content_loc6`='Well done, $N. To render the judgment of the Light on those that dwell in darkness is a task we must approach with vim and zeal; you have done just that with your sacrifice in Ironforge and your judgments of the spirits in Terrordale.$B$BYour next steps will call on both sacrifice and judgment again as you now strive to reclaim the spirit of a fallen charger who now serves a Death Knight known as Darkreaver.' WHERE `word_id` = 142279; -- locales_quest - offerrewardtext : entry=7640
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Ulag was no minor agent of the Scourge. You have shown the Deathguards, and hence the Dark Lady, that you are a $C of skill and resolve, and are worthy of further instruction.' WHERE `word_id` = 136326; -- locales_quest - offerrewardtext : entry=1819
UPDATE `trans_words` SET `content_loc6`='Well done, $N. We are almost ready.' WHERE `word_id` = 138084; -- locales_quest - offerrewardtext : entry=3442
UPDATE `trans_words` SET `content_loc6`='Well done, $N. We cannot afford to lose any further ground in Hyjal. By stopping Baron Geddon we will have dealt a huge blow to the enemy.' WHERE `word_id` = 177535; -- locales_quest - offerrewardtext : entry=25464
UPDATE `trans_words` SET `content_loc6`='Well done, $N. We have enough to worry about without wondering when the arakkoa will accost our merchants and messengers again.' WHERE `word_id` = 149190; -- locales_quest - offerrewardtext : entry=9986
UPDATE `trans_words` SET `content_loc6`='Well done, $N. We shouldn\'t be too quick to congratulate ourselves, though. I\'m certain we haven\'t seen the last of those worms. If they show their hideous heads around these parts again, the Sons\'ll be ready for them!' WHERE `word_id` = 146716; -- locales_quest - offerrewardtext : entry=9355
UPDATE `trans_words` SET `content_loc6`='Well done, $N. We\'ll be able to focus on greater threats here now.' WHERE `word_id` = 181967; -- locales_quest - offerrewardtext : entry=26682
UPDATE `trans_words` SET `content_loc6`='Well done, $N. We\'ll be knockin\' on this bug\'s door in no time.' WHERE `word_id` = 181371; -- locales_quest - offerrewardtext : entry=26528
UPDATE `trans_words` SET `content_loc6`='Well done, $N. We\'ll make sure Krennan makes it out of the city alive.' WHERE `word_id` = 173836; -- locales_quest - offerrewardtext : entry=14293
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Well done indeed! You are turning out to be a special $R - a cut above the others of your age. Take this as a reward!' WHERE `word_id` = 147695; -- locales_quest - offerrewardtext : entry=9612
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Well done indeed. The nether dragonkin will not accept a blue dragon among them at first, but with time and the firm demonstration of my authority, they will come around. Scattered, isolated, and without a definitive leader, we must be vigilant in preventing them from falling under the sway of a malevolent power, keeping them in check all the while.' WHERE `word_id` = 150389; -- locales_quest - offerrewardtext : entry=10274
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Well done!$B$BI suspect we will hear little more from the ogres and their master.' WHERE `word_id` = 152400; -- locales_quest - offerrewardtext : entry=10802
UPDATE `trans_words` SET `content_loc6`='Well done, $N. With valiant adventurers such as yourself fighting alongside The People\'s Militia, Westfall just might return to the prosperous breadbasket it once was. Please accept this in recognition of your tireless efforts.' WHERE `word_id` = 130555; -- locales_quest - offerrewardtext : entry=102
UPDATE `trans_words` SET `content_loc6`='Well done, $N. With your victory in Hellfire Citadel, we have struck a major blow against the orcs.$B$BI must speak with Field Commander Romus about following up and ensuring that the orcs stay on the defensive. Now that we\'ve been reinforced, we can do far more than simply defend Honor Hold.' WHERE `word_id` = 147212; -- locales_quest - offerrewardtext : entry=9492
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Without Kreenig to orchestrate the raids, my grunts should be able to keep Far Watch secure. Your people should be proud to count you among them.' WHERE `word_id` = 166596; -- locales_quest - offerrewardtext : entry=872
UPDATE `trans_words` SET `content_loc6`='Well done, $N. You are now ready to proceed.' WHERE `word_id` = 136866; -- locales_quest - offerrewardtext : entry=2460
UPDATE `trans_words` SET `content_loc6`='Well done, $N. You have one task left for you before you depart Shadowglen for the open wilds of Teldrassil.' WHERE `word_id` = 189116; -- locales_quest - offerrewardtext : entry=28729
UPDATE `trans_words` SET `content_loc6`='Well done, $N. You have passed The Affray!$B$BYou are a worthy warrior, and I am honored to teach you...' WHERE `word_id` = 136219; -- locales_quest - offerrewardtext : entry=1719
UPDATE `trans_words` SET `content_loc6`='Well done, $N. You have passed the trial of mercy.' WHERE `word_id` = 152768; -- locales_quest - offerrewardtext : entry=10884
UPDATE `trans_words` SET `content_loc6`='Well done, $N. You have passed the trial of strength.' WHERE `word_id` = 152772; -- locales_quest - offerrewardtext : entry=10885
UPDATE `trans_words` SET `content_loc6`='Well done, $N. You have passed the trial of tenacity.' WHERE `word_id` = 152776; -- locales_quest - offerrewardtext : entry=10886
UPDATE `trans_words` SET `content_loc6`='Well done, $N. You have provided us a temporary reprieve from the onslaught of gnolls, but there is more work to be done.' WHERE `word_id` = 180546; -- locales_quest - offerrewardtext : entry=26287
UPDATE `trans_words` SET `content_loc6`='Well done, $N. You have quite a knack for combat. Thanks to you the Coast of Westfall is a safer place.' WHERE `word_id` = 130774; -- locales_quest - offerrewardtext : entry=152
UPDATE `trans_words` SET `content_loc6`='Well done, $N. You have successfully created your first runebladed weapon. With it you will sow the seeds of chaos and destruction! In your wake will be a bloodied field of battle, littered with the corpses of all that would dare oppose the Scourge.' WHERE `word_id` = 160474; -- locales_quest - offerrewardtext : entry=12619
UPDATE `trans_words` SET `content_loc6`='Well done, $N. You might be a bloody beast, but you\'re our beast.' WHERE `word_id` = 174169; -- locales_quest - offerrewardtext : entry=14382
UPDATE `trans_words` SET `content_loc6`='Well done, $N. You might prove to be a useful ally after all.' WHERE `word_id` = 150937; -- locales_quest - offerrewardtext : entry=10412
UPDATE `trans_words` SET `content_loc6`='Well done, $N. You\'ll make a fine $C in due time. Return to me after you\'ve proven yourself a bit more, and I\'ll reward you by teaching you another spell or two.' WHERE `word_id` = 174905; -- locales_quest - offerrewardtext : entry=24533
UPDATE `trans_words` SET `content_loc6`='Well done, $N. You\'ve passed the first part of this test. Hopefully Andron will not have caught wind of your activities, and we will be able to convince him that you are, in fact, the messenger he is expecting.' WHERE `word_id` = 136437; -- locales_quest - offerrewardtext : entry=14420
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Your actions in the field are instrumental in our battle for Hellfire Peninsula.' WHERE `word_id` = 149667; -- locales_quest - offerrewardtext : entry=10106
UPDATE `trans_words` SET `content_loc6`='Well done, $N. Your soul is burdened with many deaths... and the Demonaic Scryer will now perform its duties for you.$B$BLet us hope you are ready for the coming trial...' WHERE `word_id` = 152678; -- locales_quest - offerrewardtext : entry=10864
UPDATE `trans_words` SET `content_loc6`='Well done, $N.$B$BNow that you\'re in the spirit of things, why don\'t you take this invitation to the festival in Moonglade.$B$BIt\'s quite an enjoyable celebration, if you have the time to spare. We share food, drink, stories and of course... more fireworks!$B$BOpen the invitation while standing inside the circle of greater moonlight and you\'ll be transported to Moonglade.' WHERE `word_id` = 144460; -- locales_quest - offerrewardtext : entry=8867
UPDATE `trans_words` SET `content_loc6`='Well done, $N.$B$BWe are under constant threat from the furbolg. Hopefully this will keep them at bay for a bit longer.' WHERE `word_id` = 134546; -- locales_quest - offerrewardtext : entry=1054
UPDATE `trans_words` SET `content_loc6`='Well done, $N.$B$BYou\'ve bought us some time here, though likely no more than a day. Nevertheless, the Alliance is grateful for your efforts.' WHERE `word_id` = 159381; -- locales_quest - offerrewardtext : entry=12289
UPDATE `trans_words` SET `content_loc6`='Well done, $N... your accomplishments in Zul\'Gurub have earned you the right to call the Zandalar tribe as a friend. I will see to the disposition of the Paragons; for your effort please accept this reward!' WHERE `word_id` = 142809; -- locales_quest - offerrewardtext : entry=8063
UPDATE `trans_words` SET `content_loc6`='Well done, $R!  Did you feel it?  The rush of the melee?  The true risk of free-for-all combat? As today is your first day here, I\'ll let you perform in one more competition for another reward. If you return in the future, you may compete again, once a day. Your rewards will, of course, improve as you become stronger.' WHERE `word_id` = 181165; -- locales_quest - offerrewardtext : entry=26451
UPDATE `trans_words` SET `content_loc6`='Well done, $R!  I look forward to seeing you fight again soon.' WHERE `word_id` = 181169; -- locales_quest - offerrewardtext : entry=26452
UPDATE `trans_words` SET `content_loc6`='Well done, $R! The danger persists, but you have bought us some time.' WHERE `word_id` = 174044; -- locales_quest - offerrewardtext : entry=14342
UPDATE `trans_words` SET `content_loc6`='Well done, $R. But don\'t break your arm patting yourself on the back. They\'ll have those overseers replaced in no time...' WHERE `word_id` = 163518; -- locales_quest - offerrewardtext : entry=13319
UPDATE `trans_words` SET `content_loc6`='Well done, $R. But there is much that remains to be done here.' WHERE `word_id` = 172866; -- locales_quest - offerrewardtext : entry=13943
UPDATE `trans_words` SET `content_loc6`='Well done, $R. Gorsik shall hear of your deed. Therazane shall hear of your deed. You are quite vicious for one so small... and so soft.' WHERE `word_id` = 181599; -- locales_quest - offerrewardtext : entry=26583
UPDATE `trans_words` SET `content_loc6`='Well done, $R. Lord Magmathar\'s fire is now trapped, restrained within the earth once more.' WHERE `word_id` = 172678; -- locales_quest - offerrewardtext : entry=13880
UPDATE `trans_words` SET `content_loc6`='Well done, $R. The elf captain is dead and the soul of one of our beloved brethren can sleep in peace. I shall pass on word of your deeds to my superiors in Orgrimmar.' WHERE `word_id` = 172361; -- locales_quest - offerrewardtext : entry=13621
UPDATE `trans_words` SET `content_loc6`='Well done, $R. These will do well.' WHERE `word_id` = 183282; -- locales_quest - offerrewardtext : entry=27047
UPDATE `trans_words` SET `content_loc6`='Well done, $R. You have proven that while you are tolerant of others, you will stop at nothing to destroy the minions of Kel\'Thuzad.' WHERE `word_id` = 140162; -- locales_quest - offerrewardtext : entry=5243
UPDATE `trans_words` SET `content_loc6`='Well done, $R. You\'ve proven that you can take down an ogre... no small feat. Now let\'s see if you can take down an Alliance iron mine...' WHERE `word_id` = 178276; -- locales_quest - offerrewardtext : entry=25674
UPDATE `trans_words` SET `content_loc6`='Well done, $R.$B$BEvery weapon we can take away from Loken is a great boon to our own war effort.' WHERE `word_id` = 191455; -- locales_quest - offerrewardtext : entry=29850
UPDATE `trans_words` SET `content_loc6`='Well done, Battlemaiden.$B$BI will attend to the eastern bridge. I need you to attend to the temple itself. The ritual must not be interrupted.' WHERE `word_id` = 178090; -- locales_quest - offerrewardtext : entry=25629
UPDATE `trans_words` SET `content_loc6`='Well done, brother! Now they will know that they cannot sneak up on the Horde!' WHERE `word_id` = 179556; -- locales_quest - offerrewardtext : entry=26003
UPDATE `trans_words` SET `content_loc6`='Well done, champion.' WHERE `word_id` = 189975; -- locales_quest - offerrewardtext : entry=29148
UPDATE `trans_words` SET `content_loc6`='Well done, citizen. Those kobolds are thieves and cowards, but in large numbers they pose a threat to us. And the humans of Stormwind do not need another threat.$B$BFor defeating them, you have my gratitude.' WHERE `word_id` = 130167; -- locales_quest - offerrewardtext : entry=7
UPDATE `trans_words` SET `content_loc6`='Well done, death knight! You feed the Scourge war machine with your deeds!$B$BAh yes, your reward - as promised.' WHERE `word_id` = 160772; -- locales_quest - offerrewardtext : entry=12679
UPDATE `trans_words` SET `content_loc6`='Well done, death knight. The remaining Scourge will be dealt with in short order and the rebuilding process will begin anew.$B$BI have but one last task for you.' WHERE `word_id` = 336180; -- locales_quest - offerrewardtext : entry=13166
UPDATE `trans_words` SET `content_loc6`='Well done, fleshling!$B$BThe gyreworms are among our most dangerous enemies.$B$BThe other Stone Lords shall have trouble not turning their head to this accomplishment.' WHERE `word_id` = 182623; -- locales_quest - offerrewardtext : entry=26871
UPDATE `trans_words` SET `content_loc6`='Well done, friend.$B$BThe messenger is up and reporting in as we speak.' WHERE `word_id` = 163614; -- locales_quest - offerrewardtext : entry=13344
UPDATE `trans_words` SET `content_loc6`='Well done, hero. You will now be granted the ability to deputize another Agent of Nozdormu.' WHERE `word_id` = 143228; -- locales_quest - offerrewardtext : entry=8302
UPDATE `trans_words` SET `content_loc6`='Well done, indeed. This is an entirely unshameful amount of clams to have gathered. The survivors back at the cave will surely be grateful.' WHERE `word_id` = 177783; -- locales_quest - offerrewardtext : entry=25539
UPDATE `trans_words` SET `content_loc6`='Well done, lad! Now go to the discs and learn all you can!' WHERE `word_id` = 185370; -- locales_quest - offerrewardtext : entry=27677
UPDATE `trans_words` SET `content_loc6`='Well done, little $R.$B$BEverfrost was once plentiful in this region. Over the ages, though, the Jormungar devoured nearly all of it. It is now very rare and very precious to my people.$B$BAs you roam these lands, keep a lookout for more pieces of Everfrost. Return to me with any that you find. and we will me most grateful.' WHERE `word_id` = 163831; -- locales_quest - offerrewardtext : entry=13421
UPDATE `trans_words` SET `content_loc6`='Well done, mon. A fine hero of the Horde you are.' WHERE `word_id` = 180465; -- locales_quest - offerrewardtext : entry=26263
UPDATE `trans_words` SET `content_loc6`='Well done, my Battlemaiden.$B$BThis crucible will serve as a font of power for our summoning ritual. With it, Sira\'kess will beckon our allies from the depths to bolster our already swelling forces.' WHERE `word_id` = 179020; -- locales_quest - offerrewardtext : entry=25863
UPDATE `trans_words` SET `content_loc6`='Well done, my boy.$B$B<Lorekeeper Lydros tears out a page from the book.>$B$BI am sure you can make use of this recipe.' WHERE `word_id` = 142318; -- locales_quest - offerrewardtext : entry=7649
UPDATE `trans_words` SET `content_loc6`='Well done, once again. Horde superiority is assured, at least until those Alliance pukes decide to challenge us again.' WHERE `word_id` = 163577; -- locales_quest - offerrewardtext : entry=13331
UPDATE `trans_words` SET `content_loc6`='Well done, sir. You all look very good riding around in the hot rod. I\'m sure you made quite an impression on the citizenry.' WHERE `word_id` = 173243; -- locales_quest - offerrewardtext : entry=14071
UPDATE `trans_words` SET `content_loc6`='Well done, soldier! Now the rest of the men can focus their efforts on the Horde.' WHERE `word_id` = 159527; -- locales_quest - offerrewardtext : entry=12323
UPDATE `trans_words` SET `content_loc6`='Well done, soldier. I will notify you when I have stockpiled enough of the hides.$B$BCarry on!' WHERE `word_id` = 141753; -- locales_quest - offerrewardtext : entry=7002
UPDATE `trans_words` SET `content_loc6`='Well done, soldier. One step at a time.' WHERE `word_id` = 191342; -- locales_quest - offerrewardtext : entry=29687
UPDATE `trans_words` SET `content_loc6`='Well done, well done, $N. I see we\'ve done right in deciding to train you. Your performance certainly does not disappoint.$B$BI\'ve no qualms in showing you the way to control your newly bound succubus. Observe carefully.' WHERE `word_id` = 136227; -- locales_quest - offerrewardtext : entry=1739
UPDATE `trans_words` SET `content_loc6`='Well done, you found it!$B$BDid you read it? Is Joseph Redpath mentioned? If he is, then perhaps we can save him!$B$BAh. The Annals of Darrowshire tell a very unsettling tale. It says that during the battle, Joseph Redpath was corrupted and joined the ranks of the Scourge! He then betrayed the defenders of Darrowshire.$B$BThat is an ill fate. If we can, we should try to change that fate, don\'t you think?' WHERE `word_id` = 139947; -- locales_quest - offerrewardtext : entry=5154
UPDATE `trans_words` SET `content_loc6`='Well done, young one. It looks like our deal has been satisfied on both our ends.' WHERE `word_id` = 162011; -- locales_quest - offerrewardtext : entry=12969
UPDATE `trans_words` SET `content_loc6`='Well done, young one... well done indeed.' WHERE `word_id` = 133591; -- locales_quest - offerrewardtext : entry=818
UPDATE `trans_words` SET `content_loc6`='Well done. Dig Three and any secrets beyond that door will soon be ours and ours alone.' WHERE `word_id` = 185377; -- locales_quest - offerrewardtext : entry=27681
UPDATE `trans_words` SET `content_loc6`='Well done. From what I\'ve heard, it couldn\'t have been an easy fight. You\'ve more than earned your reward, $N.' WHERE `word_id` = 147836; -- locales_quest - offerrewardtext : entry=9646
UPDATE `trans_words` SET `content_loc6`='Well done. How about I give you a nice reward for your work, and you don\'t tell a soul that you saw me here. Deal?' WHERE `word_id` = 183066; -- locales_quest - offerrewardtext : entry=26993
UPDATE `trans_words` SET `content_loc6`='Well done. I enjoyed watching you smash those $R into rotting pulp!' WHERE `word_id` = 176302; -- locales_quest - offerrewardtext : entry=25090
UPDATE `trans_words` SET `content_loc6`='Well done. I regret not being there to see you smash those undead into rotting pulp!' WHERE `word_id` = 131857; -- locales_quest - offerrewardtext : entry=404
UPDATE `trans_words` SET `content_loc6`='Well done. It looks like everything is in order. We\'re ready to begin.' WHERE `word_id` = 151047; -- locales_quest - offerrewardtext : entry=10436
UPDATE `trans_words` SET `content_loc6`='Well done. Let me have a look at this.' WHERE `word_id` = 177164; -- locales_quest - offerrewardtext : entry=25341
UPDATE `trans_words` SET `content_loc6`='Well done. Now, this is curious...$B$BThe inscription on this totem is in a language no troll could ever hope to learn, but one that is known to us. It names the bearer as an ally of a water elemental named Naias and marks him as one worthy to channel his power.$B$BThis is most unexpected, but not entirely bad, $N. With your help, I\'d like to pursue this further.' WHERE `word_id` = 146988; -- locales_quest - offerrewardtext : entry=9436
UPDATE `trans_words` SET `content_loc6`='Well done. Of my main strengths, speed is not one.' WHERE `word_id` = 181865; -- locales_quest - offerrewardtext : entry=26656
UPDATE `trans_words` SET `content_loc6`='Well done. Such a treasure deserves better than to be shut away in a dark chest. We shall see it used for a worthy purpose.' WHERE `word_id` = 152295; -- locales_quest - offerrewardtext : entry=10778
UPDATE `trans_words` SET `content_loc6`='Well done. Take a moment to let the saltwater dry from your skin before we continue.' WHERE `word_id` = 161310; -- locales_quest - offerrewardtext : entry=12810
UPDATE `trans_words` SET `content_loc6`='Well done. Thank you, $R. I will not be so foolish as to let such an oversight happen again.' WHERE `word_id` = 184749; -- locales_quest - offerrewardtext : entry=27509
UPDATE `trans_words` SET `content_loc6`='Well done. That should ease the demand on the food supply around here for the immediate future, although we\'re going to need to find new sources of food. Fish and mushrooms suffice, but variety can never be underestimated.' WHERE `word_id` = 148341; -- locales_quest - offerrewardtext : entry=9781
UPDATE `trans_words` SET `content_loc6`='Well done. The loss of those Jungle Fighters will relieve some of the pressure on our camp.$B$BBut now that you\'ve had a taste of the forces we\'re up against, do you still want to throw your lot in with us??' WHERE `word_id` = 131002; -- locales_quest - offerrewardtext : entry=203
UPDATE `trans_words` SET `content_loc6`='Well done. These will provide the raw materials for another batch of enhancement crystals.$B$BYou\'re welcome to choose from among the three kinds of crystals I can make. I find each of them useful for stalking different kinds of prey.' WHERE `word_id` = 147813; -- locales_quest - offerrewardtext : entry=9641
UPDATE `trans_words` SET `content_loc6`='Well done. This swift act against the Kolkar is an effective show of strength, and strength is what the centaur understand best.$B$BThe death of the Kolkar leader will not only disrupt that clan, but give pause to the others.' WHERE `word_id` = 135405; -- locales_quest - offerrewardtext : entry=1365
UPDATE `trans_words` SET `content_loc6`='Well done. This will show those blood crazed monsters what they\'re up against.' WHERE `word_id` = 191285; -- locales_quest - offerrewardtext : entry=29655
UPDATE `trans_words` SET `content_loc6`='Well done. Training against the other valiants is one of the best ways to prepare yourself for tournament competition. I know you\'ll make Darnassus proud, $N.' WHERE `word_id` = 164257; -- locales_quest - offerrewardtext : entry=13761
UPDATE `trans_words` SET `content_loc6`='Well done. Training against the other valiants is one of the best ways to prepare yourself for tournament competition. I know you\'ll make Ironforge proud, $N.' WHERE `word_id` = 164232; -- locales_quest - offerrewardtext : entry=13745
UPDATE `trans_words` SET `content_loc6`='Well done. Training against the other valiants is one of the best ways to prepare yourself for tournament competition. I know you\'ll make Stormwind proud, $N.' WHERE `word_id` = 164032; -- locales_quest - offerrewardtext : entry=13665
UPDATE `trans_words` SET `content_loc6`='Well done. Training against the other valiants is one of the best ways to prepare yourself for tournament competition. I know you\'ll make the Alliance proud, $N.' WHERE `word_id` = 164305; -- locales_quest - offerrewardtext : entry=13790
UPDATE `trans_words` SET `content_loc6`='Well done. Training against the other valiants is one of the best ways to prepare yourself for tournament competition. I know you\'ll make the Horde proud, $N.' WHERE `word_id` = 164325; -- locales_quest - offerrewardtext : entry=13811
UPDATE `trans_words` SET `content_loc6`='Well done. Training against the other valiants is one of the best ways to prepare yourself for tournament competition. I know you\'ll make us proud, $N.' WHERE `word_id` = 164242; -- locales_quest - offerrewardtext : entry=13750
UPDATE `trans_words` SET `content_loc6`='Well done. We need the time you\'ve bought us to investigate this link between the fel orcs and the Burning Legion. You very well may have saved hundreds of lives through your actions.' WHERE `word_id` = 191089; -- locales_quest - offerrewardtext : entry=29530
UPDATE `trans_words` SET `content_loc6`='Well done. With them out of the way, I have no more excuses. Soon, I must battle Koltira, for honor and for Andorhal.' WHERE `word_id` = 183809; -- locales_quest - offerrewardtext : entry=27199
UPDATE `trans_words` SET `content_loc6`='Well done. With them out of the way, I have no more excuses. Soon, I must battle Thassarian, for honor and for Andorhal.' WHERE `word_id` = 183433; -- locales_quest - offerrewardtext : entry=27086
UPDATE `trans_words` SET `content_loc6`='Well done. You have sent those foul Bristlebacks a clear message. They will think twice before attempting to meddle in this holy spot again.' WHERE `word_id` = 133651; -- locales_quest - offerrewardtext : entry=833
UPDATE `trans_words` SET `content_loc6`='Well done. You have served the Warchief with honor.' WHERE `word_id` = 132582; -- locales_quest - offerrewardtext : entry=569
UPDATE `trans_words` SET `content_loc6`='Well done.$B$BDon\'t get too full of yourself, $N. The hard part\'s about to come.' WHERE `word_id` = 179784; -- locales_quest - offerrewardtext : entry=26071
UPDATE `trans_words` SET `content_loc6`='Well done.$B$BI will dispose of these filthy garments by cleansing them in the moonwell and destroying them through the power of the goddess!' WHERE `word_id` = 158815; -- locales_quest - offerrewardtext : entry=12167
UPDATE `trans_words` SET `content_loc6`='Well done... no doubt they were made to pay in excruciating ways before they gave up the Paragons of Power. Take this - the demoniac\'s wraps. Made from powerful mojo used in summoning the demoniac\'s prey, it is a powerful tool in your acquisition of power.' WHERE `word_id` = 142790; -- locales_quest - offerrewardtext : entry=8059
UPDATE `trans_words` SET `content_loc6`='Well fought, $N. The Dragonmaws are not the same threat they were when they had dragons, but they\'re still fierce opponents.$B$BThe Dragonmaws are led by Nek\'rosh, son of Nekros, who was the former head of the Dragonmaws years ago. Nekros used powerful magic to tame the Red Dragons, but when that magic failed him...the dragons were not happy.$B$BNekros was killed, and his son has vowed revenge against the dragons who slew his father.' WHERE `word_id` = 132123; -- locales_quest - offerrewardtext : entry=464
UPDATE `trans_words` SET `content_loc6`='Well fought, young $R.' WHERE `word_id` = 175481; -- locales_quest - offerrewardtext : entry=24812
UPDATE `trans_words` SET `content_loc6`='Well great monkey toads! What have you got here? It\'s sort of book shaped, I guess... I\'ll try crack this shell on it.$B$BLayo sent you to me from Silithus? I\'ve only heard very hushed rumblings - which I guess aren\'t all that hushed when you think about it - about the horrors that are going on out there. Couple that with my lack of pleasure for anything bug-like, and you\'ve got the recipe for me needing to stay here with the books!' WHERE `word_id` = 141671; -- locales_quest - offerrewardtext : entry=6844
UPDATE `trans_words` SET `content_loc6`='Well hello there, $C! I heard that a resourceful young $R formerly in the service of Magistrix Erona might be making their way down here to help us Outrunners out. We certainly welcome the help, especially from someone who\'s proven to be so competent.$B$BI hope you\'re interested in running an errand or two for us.' WHERE `word_id` = 145973; -- locales_quest - offerrewardtext : entry=8347
UPDATE `trans_words` SET `content_loc6`='Well hello there, $C. Welcome to Tanaris!' WHERE `word_id` = 184481; -- locales_quest - offerrewardtext : entry=27447
UPDATE `trans_words` SET `content_loc6`='Well hello there, young $R. Either my memory is failing me, or I forgot to give you this the last time we spoke....' WHERE `word_id` = 275147; -- locales_quest - offerrewardtext : entry=13826
UPDATE `trans_words` SET `content_loc6`='Well I\'ll be - I sure am glad to see you and this power source in one piece. It will take me a moment to magically fuse the power source with the necklace now that the gems have been reunited with it. Once it is done, however, the necklace will be yours!$B$BYou\'re excited, aren\'t you?  All tingly with anticipation?  Now where did I put that spell focus at...' WHERE `word_id` = 136672; -- locales_quest - offerrewardtext : entry=2204
UPDATE `trans_words` SET `content_loc6`='Well I\'ll be a monkey\'s uncle. You actually got the beast, eh? Well, a deal\'s a deal! Here\'s your reward!' WHERE `word_id` = 178526; -- locales_quest - offerrewardtext : entry=25730
UPDATE `trans_words` SET `content_loc6`='Well I\'ll be! Where were they??' WHERE `word_id` = 139665; -- locales_quest - offerrewardtext : entry=5001
UPDATE `trans_words` SET `content_loc6`='Well I\'ll be. There\'s someone around here that isn\'t a disgrace to Magni, bless his poor gravelly heart.$B$BJust for that, you get first crack at the ale, $Glad:lass;. But don\'t let it get to your bloody head! We\'ve got more gorram work to do, and I won\'t tolerate you lettin\' up early!' WHERE `word_id` = 178486; -- locales_quest - offerrewardtext : entry=25721
UPDATE `trans_words` SET `content_loc6`='Well I\'ll be... that\'s Shizzle\'s old portable dinomometer!  Where did you find this? I\'m surprised it\'s still functional, even after all these years. I\'ll have it delivered to Shizzle promptly.' WHERE `word_id` = 175560; -- locales_quest - offerrewardtext : entry=24866
UPDATE `trans_words` SET `content_loc6`='Well if it isn\'t Mesnanub! I hope you\'re ready for battle, because that\'s all you\'ll find in this hellhole. We\'re at the heart of contested territory and these worgen mongrels won\'t give an inch!' WHERE `word_id` = 184002; -- locales_quest - offerrewardtext : entry=27290
UPDATE `trans_words` SET `content_loc6`='Well it looks like it\'s just you and the captain now, grunt. Good luck! You\'re gonna need it!' WHERE `word_id` = 153541; -- locales_quest - offerrewardtext : entry=11070
UPDATE `trans_words` SET `content_loc6`='Well look at that. You found one of our stolen documents!$B$BWe\'re much obliged for its return. Hopefully a bit of gold will suit your fancy for the finder\'s fee?' WHERE `word_id` = 172465; -- locales_quest - offerrewardtext : entry=13655
UPDATE `trans_words` SET `content_loc6`='Well look at that... you caught a big one!  This old man is eating in style tonight.' WHERE `word_id` = 154611; -- locales_quest - offerrewardtext : entry=11666
UPDATE `trans_words` SET `content_loc6`='Well look at this - my journal!  I thought it was lost, much like almost everything else we brought into Un\'Goro. I thank you kindly for returning this to me!' WHERE `word_id` = 138565; -- locales_quest - offerrewardtext : entry=3884
UPDATE `trans_words` SET `content_loc6`='Well look at ya, all geared and fancy. Keely send you to keep an eye on old Cayden, eh? Won\'t be needin\' it. I got these louts handled. Hahah! They chose the wrong dwarf to be ambushin\' today! Ahahahahah!' WHERE `word_id` = 185232; -- locales_quest - offerrewardtext : entry=27645
UPDATE `trans_words` SET `content_loc6`='Well look at you, aren\'t you just a marvel of modern Magery! Quite a fancy hat, I must say. Apprentices. Apprentici? I do think that I might have quite a few already, but I suppose one can never have too many appren..tici.' WHERE `word_id` = 174036; -- locales_quest - offerrewardtext : entry=14340
UPDATE `trans_words` SET `content_loc6`='Well look at you, helping out the Wildhammers!  You\'re a $C of action, and action is what we respect around here.$B$BYour little clean-up mission is a lot more important than you might think it is. With folks thinning out the oozes and sludges, we\'ll be able to extend our sphere of influence in no time. Here - have a little something in addition to just making new friends here in Aerie Peak!' WHERE `word_id` = 137373; -- locales_quest - offerrewardtext : entry=2877
UPDATE `trans_words` SET `content_loc6`='Well met! If you\'ve got a weapon on you, I\'ll take any help I can get clearing out these crazed fools!' WHERE `word_id` = 180716; -- locales_quest - offerrewardtext : entry=26327
UPDATE `trans_words` SET `content_loc6`='Well met, $C. I am Gavan Grayfeather, emissary of the Wildhammer shaman. There is much work to be done. The leaders fear for their citizens, as many have turned to cults. We shaman are concerned with the elemental unrest. Our advice to you, $C, is to be vigilant. Help where you can, be it calming the people or putting the elementals at rest. There is plenty to do.' WHERE `word_id` = 171934; -- locales_quest - offerrewardtext : entry=27540
UPDATE `trans_words` SET `content_loc6`='Well met, $C. I\'m glad that crusty old dwarf finally saw fit to send us some help!' WHERE `word_id` = 150810; -- locales_quest - offerrewardtext : entry=10382
UPDATE `trans_words` SET `content_loc6`='Well met, $C. I\'ve dedicated a fair portion of my recent studies to the Scholomance, and I believe I know a means by which you can acquire a key... one that will let you past the front door.$B$BI can only imagine what horrid sights are to be seen inside, $N. I don\'t know how you heroic types do it sometimes...' WHERE `word_id` = 140449; -- locales_quest - offerrewardtext : entry=5533
UPDATE `trans_words` SET `content_loc6`='Well met, $C. We\'ve got raptor problems for sure... sort of. More like the entire dig site deciding to attack us. I\'ve never seen rocks and fossils get so upset!' WHERE `word_id` = 183024; -- locales_quest - offerrewardtext : entry=26981
UPDATE `trans_words` SET `content_loc6`='Well met, $C. Your scouting of the furbolg situation comes at a fortuitous time for us in Auberdine. For some time we have dealt with not only forest dwellers who are now openly hostile to our presence, but also the outright corruption of the forest itself. Hopefully you might be able to lend Auberdine aid during these troubling times.' WHERE `word_id` = 139365; -- locales_quest - offerrewardtext : entry=4761
UPDATE `trans_words` SET `content_loc6`='Well met, $C.$B$BGaining but one trinket is not enough to make you a Master Pit Fighter.$B$BNow is the time to truly prove your steel.' WHERE `word_id` = 191365; -- locales_quest - offerrewardtext : entry=29760
UPDATE `trans_words` SET `content_loc6`='Well met, $N! We\'ve got a lot of trolls to kill and only so many hands to do it with!' WHERE `word_id` = 181260; -- locales_quest - offerrewardtext : entry=26496
UPDATE `trans_words` SET `content_loc6`='Well met, $N. Can I trust you to assist me in taking this abbey away from these abominations?' WHERE `word_id` = 184545; -- locales_quest - offerrewardtext : entry=27461
UPDATE `trans_words` SET `content_loc6`='Well met, $N. I hope the Light shows you the true path and keeps you safe from evil.' WHERE `word_id` = 137697; -- locales_quest - offerrewardtext : entry=2997
UPDATE `trans_words` SET `content_loc6`='Well met, my $Gbrother:sister;. I\'m glad you\'ve come. I am Danath - leader of the Sons of Lothar, and force commander of this hold.' WHERE `word_id` = 150298; -- locales_quest - offerrewardtext : entry=10254
UPDATE `trans_words` SET `content_loc6`='Well met. If Ahmo has already explained the minor quibbles of what kind of goblin we\'ll be driving from our lands, let\'s get to work.' WHERE `word_id` = 174646; -- locales_quest - offerrewardtext : entry=24459
UPDATE `trans_words` SET `content_loc6`='Well met. You\'ve come to learn some new ways to handle fire?' WHERE `word_id` = 274179; -- locales_quest - offerrewardtext : entry=11882
UPDATE `trans_words` SET `content_loc6`='Well now! Good ol\' Prigmon sent ya did he?$B$BThis should make fer some mighty fine eatin\'. Needs just a couple things...' WHERE `word_id` = 158922; -- locales_quest - offerrewardtext : entry=12190
UPDATE `trans_words` SET `content_loc6`='Well now, $C... since you were informed that I was leading this research myself, you obviously came as quickly as you could.$B$BNow that you are here, be sure to pay attention the first time I tell you the details; I have no desire to repeat myself.' WHERE `word_id` = 138441; -- locales_quest - offerrewardtext : entry=10520
UPDATE `trans_words` SET `content_loc6`='Well now, that looks like it could be valuable. Difficult to know until it\'s cleaned up and appraised, but I\'m still willing to make the trade if you are.' WHERE `word_id` = 164364; -- locales_quest - offerrewardtext : entry=13832
UPDATE `trans_words` SET `content_loc6`='Well now, that\'s quite a load of feathers, $C. They\'ll make for a good pillow \'til I get \'em to market!' WHERE `word_id` = 173689; -- locales_quest - offerrewardtext : entry=14253
UPDATE `trans_words` SET `content_loc6`='Well now, these are excellent specimens you collected, $N. Good money for high quality materials, that\'s for sure.' WHERE `word_id` = 131783; -- locales_quest - offerrewardtext : entry=385
UPDATE `trans_words` SET `content_loc6`='Well now, you have one--amazing!$B$BI hope you always remember the good you have done today.' WHERE `word_id` = 137072; -- locales_quest - offerrewardtext : entry=2747
UPDATE `trans_words` SET `content_loc6`='Well now. it looks like yet another druid has successfully completed the Trial of the Lake! Well done. my young friend - well done.$B$BAs I\'ve said, my name is Tajarri. It is my duty and my privilege to serve as a warden for Moonglade, and specifically this shrine. Keeper Remulos guides the Cenarion Circle in preservation of nature and balance, and his shrine serves as a lasting tribute to such. By using the bauble here, it serves as a nod to the importance of this place within the Circle.' WHERE `word_id` = 130260; -- locales_quest - offerrewardtext : entry=29
UPDATE `trans_words` SET `content_loc6`='Well now. it looks like yet another druid has successfully completed the Trial of the Lake! Well done. my young friend - well done.$B$BAs I\'ve said. my name is Tajarri. It is my duty and my privilege to serve as a warden for Moonglade. and specifically this shrine. Keeper Remulos guides the Cenarion Circle in preservation of nature and balance. and his shrine serves as a lasting tribute to such. By using the bauble here. it serves as a nod to the importance of this place within the Circle.' WHERE `word_id` = 130258; -- locales_quest - offerrewardtext : entry=28
UPDATE `trans_words` SET `content_loc6`='Well now... Where did you find this?' WHERE `word_id` = 138212; -- locales_quest - offerrewardtext : entry=3513
UPDATE `trans_words` SET `content_loc6`='Well played! I\'ve got this pretty sweet deal going. A bunch of shmucks go up there to Therazane\'s Throne and give all kinds of gold to D\'lom for who-knows-what kinda crap. He forks all that cash over to me for these crystals. I go home freakin\' rich for a couple chunks of rock. Don\'t tell no one though. I don\'t want no competition springin\' up. Think you can come back later to help me get another load of crystals out?' WHERE `word_id` = 183287; -- locales_quest - offerrewardtext : entry=27048
UPDATE `trans_words` SET `content_loc6`='Well played, $N! Lord Shalzaru\'s demise will throw the naga into disarray for some time, if not permanently. The threat they pose is now under more of our control than it has ever been, and we have you to thank for it.$B$BHere is more bounty money from the stronghold treasury - you\'ve earned every copper. You and yours are welcome in Feathermoon Stronghold anytime, my friend.$B$BOne more thing before you depart, $N... if you could...' WHERE `word_id` = 137340; -- locales_quest - offerrewardtext : entry=2870
UPDATE `trans_words` SET `content_loc6`='Well that was unexpected. I am not one to doubt our Warchief, but... Nevermind! We have work to do. I\'m sure the Dark Lady and the Warchief will get this all worked out.' WHERE `word_id` = 182962; -- locales_quest - offerrewardtext : entry=26965
UPDATE `trans_words` SET `content_loc6`='Well that\'s a load off my mind. Thank you again, $C.' WHERE `word_id` = 177517; -- locales_quest - offerrewardtext : entry=25459
UPDATE `trans_words` SET `content_loc6`='Well then, let\'s see them get any kind of magical hocus pocus air support now when we take over!' WHERE `word_id` = 178815; -- locales_quest - offerrewardtext : entry=25813
UPDATE `trans_words` SET `content_loc6`='Well then. Maybe you\'re okay after all. Alright, alright... don\'t get all bent out of shape. Just give me a few moments with these parts.' WHERE `word_id` = 181517; -- locales_quest - offerrewardtext : entry=26564
UPDATE `trans_words` SET `content_loc6`='Well this explains a lot. If you ever find a way into Ahn\'Qiraj, keep an eye out for ol\' Brann. I\'m afraid that he\'ll be powerless without his monkey.' WHERE `word_id` = 142665; -- locales_quest - offerrewardtext : entry=7907
UPDATE `trans_words` SET `content_loc6`='Well timed, $C!$B$BThe Pit of Saron lies ahead, and if our scouts are correct, past that will be the Halls of Reflection. It is there that Arthas lets his guard down, and it is there that we hope to find a clue to his weakness... and a route to vengeance long overdue.' WHERE `word_id` = 165012; -- locales_quest - offerrewardtext : entry=24511
UPDATE `trans_words` SET `content_loc6`='Well well well!  Someone who goes and gets things done!  Here\'s your cut, chief. I\'ll be seein\' you around.' WHERE `word_id` = 184531; -- locales_quest - offerrewardtext : entry=27458
UPDATE `trans_words` SET `content_loc6`='Well well well! What do we have here?$B$BThis is impressive, $N. I wouldn\'t have thought it possible, but here it is in my very hands, the complete Tome of the Cabal.$B$BAllow me to leaf through its pages for just a moment before you are off again.' WHERE `word_id` = 136309; -- locales_quest - offerrewardtext : entry=1803
UPDATE `trans_words` SET `content_loc6`='Well well well. Look who come searching for Lunk this time! This sounds like case for Lunk\'s unique brand of no-kill problem-solve.' WHERE `word_id` = 186651; -- locales_quest - offerrewardtext : entry=28033
UPDATE `trans_words` SET `content_loc6`='Well well! This is interesting... very interesting. Heh, perhaps I should bow to the goblin pilot the next time I see him.$B$BThis is worth plenty, $N. I\'m sure Nazz will pay a small fortune to keep his secret... a secret.' WHERE `word_id` = 134826; -- locales_quest - offerrewardtext : entry=1117
UPDATE `trans_words` SET `content_loc6`='Well well, let\'s take a look at what we got here... Bah! Crumbly! Useless. Wouldn\'t even make a good countertop. My miners may be mediocre workers, but they make lousy bricks. . Well, I suppose we should re-fleshify these guys so I can get some return on my investment.' WHERE `word_id` = 173441; -- locales_quest - offerrewardtext : entry=14165
UPDATE `trans_words` SET `content_loc6`='Well whadda we have here?  You here to help?' WHERE `word_id` = 188414; -- locales_quest - offerrewardtext : entry=28512
UPDATE `trans_words` SET `content_loc6`='Well would you look at that!  My favorite soldier comes through in the clutch! I\'ll make sure to get this distributed among the ogres. I\'ll see you in Redridge, soldier. For the glory of the Blackrock!' WHERE `word_id` = 187780; -- locales_quest - offerrewardtext : entry=28317
UPDATE `trans_words` SET `content_loc6`='Well would you look at this!  An axe blade, a couple of arrowheads, and what appears to be some sort of rudimentary spoon. This is indeed fascinating. Thanks again for your help, $N. I\'ve much studying to keep myself occupied while Joren\'s got us under lockdown.' WHERE `word_id` = 174752; -- locales_quest - offerrewardtext : entry=24486
UPDATE `trans_words` SET `content_loc6`='Well ya made it this far! Let\'s put ya to work before you get too bloodied up.' WHERE `word_id` = 147740; -- locales_quest - offerrewardtext : entry=9625
UPDATE `trans_words` SET `content_loc6`='Well! Curgle\'s invention lived up to its expectations.$B$BDon\'t get too excited, but do know that I am pleased with your work, $N.$B$BNow, where did I leave that book...' WHERE `word_id` = 137510; -- locales_quest - offerrewardtext : entry=2944
UPDATE `trans_words` SET `content_loc6`='Well, $G sir : ma\'am;, I have to say that you went about that particular business with a bit of zeal.$B$B<The sergeant looks at you warily.>$B$BAnyway, if you don\'t mind, I\'ll be taking my machete back now. It looks dull and we wouldn\'t want you to accidentally slip and carve me up, too.' WHERE `word_id` = 155270; -- locales_quest - offerrewardtext : entry=11246
UPDATE `trans_words` SET `content_loc6`='Well, $N, at least you\'ve made it back in one piece. I suppose that\'s worth something....' WHERE `word_id` = 159933; -- locales_quest - offerrewardtext : entry=12501
UPDATE `trans_words` SET `content_loc6`='Well, $N? Everything come out okay?$B$BHmm, looks like we caught \'em just in time....' WHERE `word_id` = 159085; -- locales_quest - offerrewardtext : entry=12227
UPDATE `trans_words` SET `content_loc6`='Well, aren\'t these just the cutest little packets of destruction you\'ve ever seen?!' WHERE `word_id` = 161382; -- locales_quest - offerrewardtext : entry=12826
UPDATE `trans_words` SET `content_loc6`='Well, gosh, this day just keeps getting better and better! I may actually smile. When you\'re not looking. You\'ve seen enough of that already.' WHERE `word_id` = 176203; -- locales_quest - offerrewardtext : entry=25063
UPDATE `trans_words` SET `content_loc6`='Well, here we are. I hope you\'re ready, $N.' WHERE `word_id` = 186727; -- locales_quest - offerrewardtext : entry=28052
UPDATE `trans_words` SET `content_loc6`='Well, hi there! So Meggi told you of the trouble I have found here?' WHERE `word_id` = 141506; -- locales_quest - offerrewardtext : entry=6603
UPDATE `trans_words` SET `content_loc6`='Well, I couldn\'t see if you helped out with saving Kristen, or not. But if she handed you one of her bundles of skins then you\'re alright in my book.Here\'s your reward.' WHERE `word_id` = 148843; -- locales_quest - offerrewardtext : entry=9897
UPDATE `trans_words` SET `content_loc6`='Well, I did warn you - did I not? Hopefully this payment more than makes up for the mild discomfort that you may have suffered. Return to me when you have gained strength and perhaps I will have another job for you - mushrooms of course!' WHERE `word_id` = 147846; -- locales_quest - offerrewardtext : entry=9648
UPDATE `trans_words` SET `content_loc6`='Well, I expected to hear as much, lad. I figured that the crazed beasts destroyed all of my belongings...$B$BBut you did find my tools, and most importantly, the amulet! I wonder why the wildkin so fiercely defended this item... In any case, I\'ll think twice before heading their way again!' WHERE `word_id` = 139509; -- locales_quest - offerrewardtext : entry=4864
UPDATE `trans_words` SET `content_loc6`='Well, I for one am glad that this petty little business is behind us. I cannot remember a time when the Consortium has had dealings with such boorish and untrustworthy beings.$B$BYou, however, seem to be very trustworthy, $N. Perhaps you wouldn\'t mind doing me one more favor?' WHERE `word_id` = 150353; -- locales_quest - offerrewardtext : entry=10267
UPDATE `trans_words` SET `content_loc6`='Well, I guess if he said that the rest of it will be coming quickly overland, then I\'ll take him at his word. He\'s certainly never failed to provide the goods in the past. Thank you, $N. Here take this coin... it\'s done nothing but jingle around in my pocket all day.' WHERE `word_id` = 168596; -- locales_quest - offerrewardtext : entry=9135
UPDATE `trans_words` SET `content_loc6`='Well, I guess its another night of sharpening bones for me. Why didn\'t I stay in Silvemoon City like my brother?' WHERE `word_id` = 163002; -- locales_quest - offerrewardtext : entry=13193
UPDATE `trans_words` SET `content_loc6`='Well, I guess you saved plenty of them. Thank you, $R.$B$BNow the wolvar will have a chance of surviving along with us. If we survive at all, that is.$B$BDo you think that you could return tomorrow so that we can save more of the pups?' WHERE `word_id` = 157973; -- locales_quest - offerrewardtext : entry=11960
UPDATE `trans_words` SET `content_loc6`='Well, I had a whole speech prepared, but the old man himself has some words he wants to say to you.' WHERE `word_id` = 160452; -- locales_quest - offerrewardtext : entry=12614
UPDATE `trans_words` SET `content_loc6`='Well, I have my doubts, but this is the only solution available right now. Once we have it up and running, I\'m sure there will be time to look at other ways to fuel the lamp.' WHERE `word_id` = 153989; -- locales_quest - offerrewardtext : entry=11192
UPDATE `trans_words` SET `content_loc6`='Well, I must say, I am impressed, $N. I didn\'t think you had the mettle for this mission. Well done. Well done, indeed.' WHERE `word_id` = 150860; -- locales_quest - offerrewardtext : entry=10394
UPDATE `trans_words` SET `content_loc6`='Well, I suppose it\'s good that we\'ve "recovered" the crusader, though I doubt his comrades had this in mind. Let\'s see what we can do to get him back on his feet so we can get this charade over with and complete our mission here.' WHERE `word_id` = 163055; -- locales_quest - offerrewardtext : entry=13212
UPDATE `trans_words` SET `content_loc6`='Well, I suppose that will have to do, now won\'t it? Thanks for the help, $C. I can only hope to get this mess completely cleaned up before the Magister returns to Duskwither Spire.$B$BTake this pocket change; it\'s not much but it\'s all that I have at the moment. Feel free to dispatch a few more of the beasties on your way out, if you\'d be so kind.' WHERE `word_id` = 146185; -- locales_quest - offerrewardtext : entry=8894
UPDATE `trans_words` SET `content_loc6`='Well, I\'ll be! How in tarnation!? You found the key! I knew them blasted Dark Irons were hiding it after they locked me in here! Thank ya so much, $N. You\'re the greatest! Now, just turn the key and let ol\' Locheed outta here!' WHERE `word_id` = 139129; -- locales_quest - offerrewardtext : entry=4451
UPDATE `trans_words` SET `content_loc6`='Well, I\'ll be...$B$BThis was one of Chen Stormstout\'s kegs. He traveled with Rexxar a long time ago. I haven\'t seen or heard about him in a kodo\'s age. I appreciate you bringing this to me, $N.' WHERE `word_id` = 133595; -- locales_quest - offerrewardtext : entry=819
UPDATE `trans_words` SET `content_loc6`='Well, I\'ve got bad news and I\'ve got...$B$BNo, scratch that. There only be bad news.' WHERE `word_id` = 189175; -- locales_quest - offerrewardtext : entry=28746
UPDATE `trans_words` SET `content_loc6`='Well, it does look a little cleaner out there...$B$BI suppose it\'s time to get down to the business of salvaging this wreckage.' WHERE `word_id` = 153940; -- locales_quest - offerrewardtext : entry=11174
UPDATE `trans_words` SET `content_loc6`='Well, it looks like it\'s time for us to part ways, at least for a while. To the northwest, the tauren of Freewind Post are in desparate need of your assistance in pushing back the Grimtotem clan!$B$BYou know, you\'ve been such a great help around here, I bet you\'re going to be their $G hero : heroine; in no-time!' WHERE `word_id` = 178890; -- locales_quest - details : entry=25836
UPDATE `trans_words` SET `content_loc6`='Well, it says here that you\'ve been awarded Acting Deputy Status with the Stormwind Marshals. Congratulations.$B$BAnd good luck - keeping Elwynn safe is no picnic... what with most the army busy doing who knows what for who knows which noble!$B$BIt\'s hard to keep track of politics in these dark days...' WHERE `word_id` = 130349; -- locales_quest - offerrewardtext : entry=54
UPDATE `trans_words` SET `content_loc6`='Well, it sounds like they\'re certainly afraid of the mustang, but they\'re not leaving for good. I can\'t have you running around on a horse forever. Nor do I think you\'d want to. Fine. Bloodshed it is.' WHERE `word_id` = 183101; -- locales_quest - offerrewardtext : entry=27000
UPDATE `trans_words` SET `content_loc6`='Well, it would seem that even though we know more about this crystal, more unanswered questions are the result!$B$BI\'ll take the information you\'ve uncovered to the council here in Auberdine. Perhaps they\'ll know a course of action to follow on this crystal. Well, that is if there is one to be taken; for now, the threat remains far enough to our east to be out of harm\'s way.$B$BAs for you, please have this. Consider it a reward for a job well done, $N.' WHERE `word_id` = 139479; -- locales_quest - offerrewardtext : entry=4813
UPDATE `trans_words` SET `content_loc6`='Well, it\'s a start. It\'ll take a few weeks or months to fully clean out the infestation. After that, we\'ll have to get down there with some torches to burn away the webbing. You\'ve done your duty well, $N.' WHERE `word_id` = 175862; -- locales_quest - offerrewardtext : entry=24973
UPDATE `trans_words` SET `content_loc6`='Well, it\'s about time! Dogran has been dying out there, waiting for this.$B$BWe need to get this out to him right away, but most of the grunts stationed here are out on a foraging mission.' WHERE `word_id` = 135859; -- locales_quest - offerrewardtext : entry=1511
UPDATE `trans_words` SET `content_loc6`='Well, it\'s catchy, that\'s for sure. But is it a classic?' WHERE `word_id` = 188181; -- locales_quest - offerrewardtext : entry=28413
UPDATE `trans_words` SET `content_loc6`='Well, it\'s no egg... Hey! I love chicken too! I\'ll just chomp right through this ginourmous talon! Thanks!' WHERE `word_id` = 149691; -- locales_quest - offerrewardtext : entry=10111
UPDATE `trans_words` SET `content_loc6`='Well, it\'s not encouraging, but it\'s something...' WHERE `word_id` = 178852; -- locales_quest - offerrewardtext : entry=25824
UPDATE `trans_words` SET `content_loc6`='Well, it\'s not much, but it should keep us alive for another day.$B$BI believe you\'ll find this to be a fair price for your efforts.' WHERE `word_id` = 157837; -- locales_quest - offerrewardtext : entry=11912
UPDATE `trans_words` SET `content_loc6`='Well, kid, it looks like you\'ve done it again. This starlight is exactly what we were missing. Now we can get to work!' WHERE `word_id` = 184912; -- locales_quest - offerrewardtext : entry=27549
UPDATE `trans_words` SET `content_loc6`='Well, lad! Don\'t hesitate to knock a few more down for good measure!' WHERE `word_id` = 174140; -- locales_quest - offerrewardtext : entry=14373
UPDATE `trans_words` SET `content_loc6`='Well, let\'s see what\'s in this barrel!$B$BYes, yes, it looks as though it is all here. Here\'s your fee for the package, good job on that. I stand to make five times that fee when I sell it to the tavern! Har har!$B$BThese extra bottles of Hinterlands Honey Ripple are going to be set for a special occasion. I think I\'ll drink some with champagne. I\'ll call it... champipple.' WHERE `word_id` = 130461; -- locales_quest - offerrewardtext : entry=81
UPDATE `trans_words` SET `content_loc6`='Well, look at that. You found the dream dust!$B$BAnd how did you like the swamp, $N? It\'s not easy to get a hot meal there, is it?' WHERE `word_id` = 134821; -- locales_quest - offerrewardtext : entry=1116
UPDATE `trans_words` SET `content_loc6`='Well, look at this! Little Tweedle has really come through this time. This stuff blew up Astranaar, you say? As you can see, we don\'t need any help taking over Silverwind Refuge, but Tweedle\'s ideas are as small as his hands. If this stuff\'s really as good as you say, there\'s no limit to what we might do....' WHERE `word_id` = 172962; -- locales_quest - offerrewardtext : entry=13974
UPDATE `trans_words` SET `content_loc6`='Well, lookee there! Ye\'ve got yerself a right bushel o\' wolpertingers, ye do!$B$B<He takes a deep drink from his tankard.>$B$BI\'ll tell ye what I\'m gonna do. Since ye\'re such a spot-on $C, I\'ll let ye keep one\'a these fer yer very own!' WHERE `word_id` = 153702; -- locales_quest - offerrewardtext : entry=11117
UPDATE `trans_words` SET `content_loc6`='Well, looks like you\'re here to more than just inquire! The citizens will be delighted to know that tonight they will sleep a little bit more soundly and safer.$B$BYou\'ve acted as the executioner of justice for the people of Auberdine today, $N. For that, I would like to offer you this as a suitable reward... for a true hero of the Auberdine people.' WHERE `word_id` = 139349; -- locales_quest - offerrewardtext : entry=4740
UPDATE `trans_words` SET `content_loc6`='Well, my boy that certainly was exciting! And for my money, I\'d say that the Scrap Reaver X6000 performed admirably, despite its rookie pilot.$B$BHehe, no, don\'t take offense, I was just kidding. After all, you just saved Area 52 and the X' WHERE `word_id` = 150270; -- locales_quest - offerrewardtext : entry=10248
UPDATE `trans_words` SET `content_loc6`='Well, my entire operation\'s nearly been ruined. But you came through in the clinch, $N. It could\'ve been worse! I gotta hand it to you.' WHERE `word_id` = 187997; -- locales_quest - offerrewardtext : entry=28368
UPDATE `trans_words` SET `content_loc6`='Well, my update will explain all of this to Kadrak. You must return this to him as quickly as possible.' WHERE `word_id` = 141411; -- locales_quest - offerrewardtext : entry=6545
UPDATE `trans_words` SET `content_loc6`='Well, now that you\'re here, we\'ll gladly take that armor off your hands. You can keep the weapons, though. These men wouldn\'t know which end to hold.' WHERE `word_id` = 183721; -- locales_quest - offerrewardtext : entry=27172
UPDATE `trans_words` SET `content_loc6`='Well, now Tormak can\'t complain anymore. This baby\'s as ready as I can make it.$B$BAfter you tear your way through Dun Argol with it, that overgrown cow\'ll have to release me.' WHERE `word_id` = 158942; -- locales_quest - offerrewardtext : entry=12198
UPDATE `trans_words` SET `content_loc6`='Well, now!$B$BYa saved me a fair bit o\' work there $Glad:lass;. I\'m grateful. I\'ll be sure ta make mention of ya in me report.$B$BOff with yeh now. Finklestein\'ll likely be waitin fer ya a bit west o\' here.' WHERE `word_id` = 160020; -- locales_quest - offerrewardtext : entry=12519
UPDATE `trans_words` SET `content_loc6`='Well, now, that was thoughtful o\' Babs to send that over. I don\'t know if you\'ve leafed through it or not, but there\'s a lot of truth to the tales in that book. Sailors didn\'t just make it all up, you know.' WHERE `word_id` = 153993; -- locales_quest - offerrewardtext : entry=11193
UPDATE `trans_words` SET `content_loc6`='Well, now, that\'s more like it. We may get around to blowin\' up somethin\' after all.' WHERE `word_id` = 180537; -- locales_quest - offerrewardtext : entry=26285
UPDATE `trans_words` SET `content_loc6`='Well, now. Would ya look at that! As amazing as this is, I am confident that there\'s more to our discovery than meets the eye... Much more.' WHERE `word_id` = 185338; -- locales_quest - offerrewardtext : entry=27669
UPDATE `trans_words` SET `content_loc6`='Well, now... I must admit I\'m more than a little surprised to see you, $N.$B$BTell me what you\'ve learned.' WHERE `word_id` = 160673; -- locales_quest - offerrewardtext : entry=12661
UPDATE `trans_words` SET `content_loc6`='Well, of course I can temper this sword. While I\'m working, you can explain what took Linken so long to get this to me. He\'s not usually one to forget things...' WHERE `word_id` = 138600; -- locales_quest - offerrewardtext : entry=3908
UPDATE `trans_words` SET `content_loc6`='Well, seein\' how this is your prime directive, or whatever yer callin\' it these days, I gave my word I\'d do whatever I can to make sure you and yours were ready to take back your home... and that\'s just what I\'m gonna do.$B$BAny time you feel yer ready, come back to good ol\' Thran and I\'ll be happy to share my knowledge wit ya. There\'s some cost to me trainin\' ya, but it won\'t be too expensive. Good luck.' WHERE `word_id` = 137850; -- locales_quest - offerrewardtext : entry=3112
UPDATE `trans_words` SET `content_loc6`='Well, sure, I can tell you more about Kel\'Theril. Mind you now, I\'m no historian, but I can tell ya about what I saw there...' WHERE `word_id` = 140167; -- locales_quest - offerrewardtext : entry=5244
UPDATE `trans_words` SET `content_loc6`='Well, that makes me feel a little better...$B$BBut I won\'t be happy until they\'re all dead! All of them!' WHERE `word_id` = 131977; -- locales_quest - offerrewardtext : entry=432
UPDATE `trans_words` SET `content_loc6`='Well, that\'s a better assortment than I would\'ve expected from this place. Now, for the important question: can we distill it into anything drinkable? We\'d better get started.' WHERE `word_id` = 160542; -- locales_quest - offerrewardtext : entry=12634
UPDATE `trans_words` SET `content_loc6`='Well, that\'s good and bad news, now isn\'t it? We\'ve dealt them a blow, but with the Burning Legion running free all over the Netherstorm' WHERE `word_id` = 150190; -- locales_quest - offerrewardtext : entry=10232
UPDATE `trans_words` SET `content_loc6`='Well, these certainly are ugly enough, but I suppose I don\'t care if it actually works. I\'ll be relieved once the expedition has passed through and I can go back to simply fishing for my dinner.' WHERE `word_id` = 156379; -- locales_quest - offerrewardtext : entry=11329
UPDATE `trans_words` SET `content_loc6`='Well, this is certainly more information than I suspected to get out of Andron. Give me a moment to peruse it, before I set you to your next task.' WHERE `word_id` = 136445; -- locales_quest - offerrewardtext : entry=14421
UPDATE `trans_words` SET `content_loc6`='Well, this news is disturbing indeed. I will start dispatching messengers to Ashenvale immediately and see if we can\'t get some reinforcements prepared for them. I cannot thank you enough for all the help you\'ve lent us in these trying times. Such heroic deeds deserve reward. I think I might have something that will serve you perfectly.' WHERE `word_id` = 172277; -- locales_quest - offerrewardtext : entry=13591
UPDATE `trans_words` SET `content_loc6`='Well, we\'ve stymied their efforts for now, but they\'re still here. We\'ll have to press on, $N.' WHERE `word_id` = 176572; -- locales_quest - offerrewardtext : entry=25167
UPDATE `trans_words` SET `content_loc6`='Well, well, if it isn\'t $N. And you pulled through. I guess now I\'ll have to keep my eye on you. It hurts me to say this, but thanks for your efforts. The Alliance won\'t forget, and neither will I. Now get out there and bust your hump on whatever else needs collecting.' WHERE `word_id` = 143634; -- locales_quest - offerrewardtext : entry=8494
UPDATE `trans_words` SET `content_loc6`='Well, well, well, I guess you had it in you after all. Good work. Maybe Old Man Keller was wrong about you after all.$B$B<Sky Sergeant Vanderlip smirks.>' WHERE `word_id` = 153298; -- locales_quest - offerrewardtext : entry=11010
UPDATE `trans_words` SET `content_loc6`='Well, well, well, if it isn\'t my wayward protege, Mr. $N. I\'m hurt and disappointed. You didn\'t send me an invitation to your little party out there. Sorry about the crashers... you know how pirates can be. So, shall we get down to business?' WHERE `word_id` = 173309; -- locales_quest - offerrewardtext : entry=14116
UPDATE `trans_words` SET `content_loc6`='Well, well, well, it looks like you\'ve survived the rhinos, after all, and in pretty good shape if I do say so myself. But there\'s more to rhino hunting than just staring down the beast as it charges you and hoping your aim is true. No, you\'ll need more than that to take on the rhino patriarch that\'s rumored to dwell in the jungle.' WHERE `word_id` = 160025; -- locales_quest - offerrewardtext : entry=12520
UPDATE `trans_words` SET `content_loc6`='Well, well, well. If it isn\'t $N. You show up at Scalawag point, no one\'s heard of you except some goblin friend of that half-wit Terry.$B$BLet\'s just say I\'m a bit... skeptical... of your intentions here.' WHERE `word_id` = 156730; -- locales_quest - offerrewardtext : entry=11480
UPDATE `trans_words` SET `content_loc6`='Well, well, well... I haven\'t seen one of these in a dragon\'s age. Faerie dragon, if I\'m not mistaken... looks to be of the sprite darter variety. Excellent, excellent indeed.$B$BWell, if this little fella\'s gonna hatch and live to be strong and healthy, then we should get some food for it. Good ya got it to me in time.' WHERE `word_id` = 138532; -- locales_quest - offerrewardtext : entry=3843
UPDATE `trans_words` SET `content_loc6`='Well, well, well... If it isn\'t the grunt I sent out into the field. You\'re back and in one piece.$B$B<Or\'barokh nods.>$B$BI knew you wouldn\'t let me down, soldier. You\'ve stopped a major Illidari advance and, in doing so, earned yourself a little reward.' WHERE `word_id` = 152258; -- locales_quest - offerrewardtext : entry=10769
UPDATE `trans_words` SET `content_loc6`='Well, well, well... If it isn\'t the soldier I sent out into the field. You\'re back and in one piece.$B$B<Yoregar nods.>$B$BI knew you wouldn\'t let me down, soldier. You\'ve stopped a major Illidari advance and, in doing so, earned yourself a little reward.' WHERE `word_id` = 152286; -- locales_quest - offerrewardtext : entry=10776
UPDATE `trans_words` SET `content_loc6`='Well, well, well... look what the Ruag dragged in!' WHERE `word_id` = 188558; -- locales_quest - offerrewardtext : entry=28553
UPDATE `trans_words` SET `content_loc6`='Well, what are you waiting for, $R? Hand them over!' WHERE `word_id` = 159116; -- locales_quest - offerrewardtext : entry=12234
UPDATE `trans_words` SET `content_loc6`='Well, you did not flee in the face of defeat. You stood your ground, $N, and when it comes right down to it, that is what makes for a true hero. No doubt you will win next time... but nonetheless we thank you for your effort.' WHERE `word_id` = 143146; -- locales_quest - offerrewardtext : entry=8266
UPDATE `trans_words` SET `content_loc6`='Well, you\'ve proven yourself a capable hunter, but how are you as a tracker?' WHERE `word_id` = 160170; -- locales_quest - offerrewardtext : entry=12549
UPDATE `trans_words` SET `content_loc6`='Well. well, it would appear that we are in luck, young man.$B$BI was just recently able to finish the construction of my latest invention, the Scrap Reaver X6000!$B$BAnd you\'re going to have the honor of helping me test it so that it can be used in defense of Area 52!' WHERE `word_id` = 150266; -- locales_quest - offerrewardtext : entry=10247
UPDATE `trans_words` SET `content_loc6`='Well... that wasn\'t all that great, was it?' WHERE `word_id` = 187346; -- locales_quest - offerrewardtext : entry=28210
UPDATE `trans_words` SET `content_loc6`='Well-fought, $N. Take a break from the flames for a while.$B$BHyjal will live to see another day, and when that day comes, I will need you alive.' WHERE `word_id` = 189877; -- locales_quest - offerrewardtext : entry=29123
UPDATE `trans_words` SET `content_loc6`='Wellson Supply Boats Destroyed' WHERE `word_id` = 187619; -- locales_quest - ObjectiveText1 : entry=28275
UPDATE `trans_words` SET `content_loc6`='Wenikee said what? Copperplug? BOSS Copperplug? Oh no, this might be tricky.' WHERE `word_id` = 173078; -- locales_quest - offerrewardtext : entry=14004
