UPDATE `trans_words` SET `content_loc7`='"Archmage Pratt, a blacksmith sorcerer recently arrived from Dalaran, usually works with the local sorceress Lavinia Crowe. Both can be found in the Pharmacists area."' WHERE `word_id` = 280233; -- locales_npc_text - Text0_1 : entry=17316
