UPDATE `trans_words` SET `content_loc7`='Do we have Mining trainers here?  Where do ye think ye are!?  $B$BWhy you\'ll find one o\' the best miners in all Dun Morogh at the Deepmountain Mining Guild on the northern side of The Great Forge. There, Geofram Bouldertoe will show ye the true art of mining.' WHERE `word_id` = 192677; -- locales_npc_text - Text0_1 : entry=2804
UPDATE `trans_words` SET `content_loc7`='Do you hear the call of the hunt, $C?  You must listen closely, but the call comes from deep within you. It comes from deep within all of us.' WHERE `word_id` = 193954; -- locales_npc_text - Text0_1 : entry=5529
UPDATE `trans_words` SET `content_loc7`='Do you really want me to help you in the battle with Yogg-Saron?' WHERE `word_id` = 279332; -- locales_npc_text - Text0_1 : entry=14330
