UPDATE `trans_words` SET `content_loc7`='Find the representatives of the Reliquary on the Royal Exchange. I have no doubt that among them there will be many archaeologists who will gladly help you.' WHERE `word_id` = 280224; -- locales_npc_text - Text0_1 : entry=17209
UPDATE `trans_words` SET `content_loc7`='Fishmaster Bassbait has had the waters here seeded with high quality Tastyfish so he can have his tournament.$B$BOnce he gets his winner, I will give you silver for each five Tastyfish you bring me!' WHERE `word_id` = 194742; -- locales_npc_text - Text0_1 : entry=7696
UPDATE `trans_words` SET `content_loc7`='Forgetting your skill in Elixir Mastery is not something to do lightly.$B$BAre you absolutely sure?' WHERE `word_id` = 198940; -- locales_npc_text - Text0_1 : entry=11075
