UPDATE `trans_words` SET `content_loc7`='You will find a pair of draenei conversing with Tyrande Whisperwind in the upper level of The Temple of the Moon.$B$BThey will know how to help you.' WHERE `word_id` = 199818; -- locales_npc_text - Text0_1 : entry=15906
UPDATE `trans_words` SET `content_loc7`='You$G owe: must; go to the Thousand Needles and provide assistance. You are welcome!' WHERE `word_id` = 280063; -- locales_npc_text - Text2_1 : entry=15837
